        {stat.icon}
        <Box>
          <Typography variant="h5">{stat.label} Details</Typography>
          <Typography variant="body2" sx={{ opacity: 0.9 }}>
            {stat.description}
          </Typography>
        </Box>
      </DialogTitle>
      <DialogContent sx={{ p: 3 }}>
        {getDialogContent()}
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Close</Button>
        <Button variant="contained" onClick={onClose}>
          Export Report
        </Button>
      </DialogActions>
    </Dialog>
  );
};

