// CourseFacultyMappingDialog Component
const CourseFacultyMappingDialog = ({ open, onClose, courses, faculty, courseMapping, setCourseMapping, onMap }) => (
  <Dialog open={open} onClose={onClose} maxWidth="sm" fullWidth>
    <DialogTitle>Map Course to Faculty</DialogTitle>
    <DialogContent>
      <FormControl fullWidth margin="normal">
        <InputLabel>Select Course</InputLabel>
        <Select value={courseMapping.courseId} onChange={(e) => setCourseMapping({...courseMapping, courseId: e.target.value})} label="Select Course">
          {courses.map(course => (
            <MenuItem key={course.id} value={course.id}>{course.code} - {course.name}</MenuItem>
          ))}
        </Select>
      </FormControl>
      <FormControl fullWidth margin="normal">
        <InputLabel>Select Faculty</InputLabel>
        <Select value={courseMapping.facultyId} onChange={(e) => setCourseMapping({...courseMapping, facultyId: e.target.value})} label="Select Faculty">
          {faculty.map(faculty => (
            <MenuItem key={faculty.id} value={faculty.id}>{faculty.name} ({faculty.department})</MenuItem>
          ))}
        </Select>
      </FormControl>
      <FormControl fullWidth margin="normal">
        <InputLabel>Semester</InputLabel>
        <Select value={courseMapping.semester} onChange={(e) => setCourseMapping({...courseMapping, semester: e.target.value})} label="Semester">
          {[1, 2, 3, 4, 5, 6, 7, 8].map(sem => (
            <MenuItem key={sem} value={sem}>Semester {sem}</MenuItem>
          ))}
        </Select>
      </FormControl>
    </DialogContent>
    <DialogActions>
      <Button onClick={onClose}>Cancel</Button>
      <Button variant="contained" onClick={onMap}>Map Course</Button>
    </DialogActions>
  </Dialog>
);

