import React, { useState, useEffect, useRef } from 'react';
import {
  Container,
  Typography,
  Paper,
  Grid,
  Card,
  CardContent,
  Button,
  Tabs,
  Tab,
  Box,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  FormControl,
  Select,
  MenuItem,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  LinearProgress,
  Alert,
  Snackbar,
  AppBar,
  Toolbar,
  IconButton,
  TextField,
  Avatar,
  Divider,
  InputLabel,
  OutlinedInput,
  InputAdornment,
  CircularProgress
} from '@mui/material';
import { 
  QrCode2, 
  Face, 
  Groups, 
  Schedule, 
  Analytics, 
  Refresh,
  Download,
  Print,
  CameraAlt,
  Stop,
  PlayArrow,
  CheckCircle,
  Logout,
  AccountCircle,
  Edit,
  Save,
  Cancel,
  Email,
  Phone,
  LocationOn,
  Person
} from '@mui/icons-material';
import { QRCodeSVG } from 'qrcode.react';

// Enhanced API Service with better error handling
const createApiService = () => {
  const baseUrl = 'http://localhost:5001/api';

  const request = async (endpoint, options = {}) => {
    const token = localStorage.getItem('token') || localStorage.getItem('accessToken');
    console.log(`ðŸ”„ API Request: ${baseUrl}${endpoint}`);
    console.log(`ðŸ”‘ Token: ${token ? 'Present' : 'Missing'}`);

    const config = {
      headers: {
        'Content-Type': 'application/json',
        ...(token && { 'Authorization': `Bearer ${token}` }),
      },
      ...options,
    };

    if (config.body && typeof config.body === 'object') {
      config.body = JSON.stringify(config.body);
    }

    try {
      const response = await fetch(`${baseUrl}${endpoint}`, config);
      console.log(`ðŸ“¡ Response Status: ${response.status} for ${endpoint}`);

      if (response.status === 401) {
        console.error('âŒ Authentication failed');
        localStorage.removeItem('token');
        localStorage.removeItem('accessToken');
        localStorage.removeItem('user');
        window.location.href = '/login';
        throw new Error('Authentication failed');
      }

      if (!response.ok) {
        const errorText = await response.text();
        console.error(`âŒ HTTP Error: ${response.status}`, errorText);
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      console.log(`âœ… API Success: ${endpoint}`, data);
      return data;

    } catch (error) {
      console.error(`âŒ API Error for ${endpoint}:`, error);
      throw error;
    }
  };

  return {
    // Test endpoints
    async testConnection() {
      try {
        const response = await fetch(`${baseUrl}/health`);
        return response.ok;
      } catch (error) {
        return false;
      }
    },

    // Faculty endpoints
    async getFacultyProfile() {
      return request('/faculty/profile');
    },

    async updateFacultyProfile(profileData) {
      return request('/faculty/profile', {
        method: 'PUT',
        body: profileData,
      });
    },

    // Course endpoints
    async getFacultyCourses() {
      try {
        console.log('ðŸ“š Fetching faculty courses...');
        const response = await request('/faculty/courses');

        // Handle various response formats
        let courses = [];

        if (Array.isArray(response)) {
          courses = response;
        } else if (response && Array.isArray(response.courses)) {
          courses = response.courses;
        } else if (response && Array.isArray(response.data)) {
          courses = response.data;
        } else if (response && response.courses) {
          courses = [response.courses].flat();
        } else if (response && response.data) {
          courses = [response.data].flat();
        } else {
          console.warn('âš ï¸ Unexpected courses response format:', response);
          courses = [];
        }

        console.log(`âœ… Found ${courses.length} courses`);
        return courses;

      } catch (error) {
        console.error('âŒ Error fetching faculty courses:', error);
        throw new Error(`Failed to fetch courses: ${error.message}`);
      }
    },

    async getCourseStudents(courseId) {
      try {
        console.log(`ðŸ‘¥ Fetching students for course ${courseId}`);
        return await request(`/courses/${courseId}/students`);
      } catch (error) {
        console.error(`âŒ Error fetching students for course ${courseId}:`, error);
        return [];
      }
    },

    // Attendance endpoints
    async startAttendanceSession(sessionData) {
      return request('/attendance/sessions', {
        method: 'POST',
        body: sessionData,
      });
    },

    async markAttendance(attendanceData) {
      return request('/attendance/mark', {
        method: 'POST',
        body: attendanceData,
      });
    },

    async getAttendanceReports(courseId, date) {
      const params = new URLSearchParams();
      if (courseId) params.append('courseId', courseId);
      if (date) params.append('date', date);

      return request(`/attendance/reports?${params}`);
    },

    // Schedule endpoints
    async getFacultySchedule() {
      try {
        return await request('/faculty/schedule');
      } catch (error) {
        console.error('âŒ Error fetching schedule:', error);
        return { schedule: [] };
      }
    },
  };
};

const apiService = createApiService();

// Real Face Recognition Component
const RealFaceRecognition = ({ onAttendanceMarked, isActive, setSnackbar, course }) => {
  const [isScanning, setIsScanning] = useState(false);
  const [detectedFaces, setDetectedFaces] = useState([]);
  const videoRef = useRef(null);
  const streamRef = useRef(null);

  const startFaceRecognition = async () => {
    try {
      setIsScanning(true);
      setDetectedFaces([]);

      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { facingMode: 'user' } 
      });

      streamRef.current = stream;

      if (videoRef.current) {
        videoRef.current.srcObject = stream;
      }

      // Simulate face detection for demo
      setTimeout(() => {
        const sampleStudents = [
          { student_id: 'S001', name: 'Alice Johnson' },
          { student_id: 'S002', name: 'Bob Smith' },
        ];

        sampleStudents.forEach((student, index) => {
          setTimeout(() => {
            setDetectedFaces(prev => [...prev, student]);
            onAttendanceMarked(student.student_id, 'Present', 'Face Recognition', student.name);
          }, index * 2000);
        });
      }, 1000);

    } catch (error) {
      console.error('Error accessing camera:', error);
      setSnackbar({
        open: true,
        message: 'Cannot access camera. Please check permissions.',
        severity: 'error'
      });
      setIsScanning(false);
    }
  };

  const stopFaceRecognition = () => {
    setIsScanning(false);

    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
    }

    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
  };

  useEffect(() => {
    return () => {
      stopFaceRecognition();
    };
  }, []);

  return (
    <Paper sx={{ p: 3, border: '1px solid #1976d2' }}>
      <Typography variant="h6" gutterBottom color="primary">
        <CameraAlt sx={{ mr: 1 }} />
        Face Recognition Attendance
      </Typography>

      <Box sx={{ 
        width: '100%', 
        height: 300,
        border: '2px solid #1976d2',
        borderRadius: 2,
        overflow: 'hidden',
        mx: 'auto',
        my: 2,
        backgroundColor: '#000',
        position: 'relative'
      }}>
        {isScanning ? (
          <video
            ref={videoRef}
            style={{
              width: '100%',
              height: '100%',
              objectFit: 'cover'
            }}
            playsInline
            muted
            autoPlay
          />
        ) : (
          <Box sx={{ 
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'center', 
            height: '100%',
            color: 'white'
          }}>
            <Typography variant="body1" align="center">
              Camera feed will appear here
              <br />
              when recognition starts
            </Typography>
          </Box>
        )}
      </Box>

      <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center', mb: 3 }}>
        <Button
          variant="contained"
          startIcon={<PlayArrow />}
          onClick={startFaceRecognition}
          disabled={isScanning}
          color="primary"
        >
          Start Recognition
        </Button>
        <Button
          variant="outlined"
          startIcon={<Stop />}
          onClick={stopFaceRecognition}
          disabled={!isScanning}
          color="primary"
        >
          Stop Recognition
        </Button>
      </Box>

      {detectedFaces.length > 0 && (
        <Box>
          <Typography variant="subtitle1" gutterBottom color="primary">
            âœ… Detected Students ({detectedFaces.length})
          </Typography>
          <List dense>
            {detectedFaces.map((student, index) => (
              <ListItem key={`${student.student_id}-${index}`}>
                <ListItemIcon>
                  <Avatar sx={{ width: 32, height: 32, bgcolor: 'primary.main' }}>
                    <Person />
                  </Avatar>
                </ListItemIcon>
                <ListItemText 
                  primary={student.name}
                  secondary={`ID: ${student.student_id} - Marked Present`}
                />
                <CheckCircle color="primary" />
              </ListItem>
            ))}
          </List>
        </Box>
      )}
    </Paper>
  );
};

// QR Attendance Component
const QRAttendanceComponent = ({ 
  qrCodeHash, 
  onRefresh, 
  onSimulateScan, 
  scannedCount, 
  isActive,
  course,
  sessionTimeLeft 
}) => {
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const qrValue = qrCodeHash ? JSON.stringify({
    type: 'attendance',
    courseCode: course?.courseCode || course?.course_code,
    courseName: course?.courseName || course?.course_name,
    sessionId: qrCodeHash,
    timestamp: new Date().toISOString(),
  }) : '';

  return (
    <Paper sx={{ p: 3, textAlign: 'center', border: '1px solid #1976d2' }}>
      <Typography variant="h6" gutterBottom color="primary">
        <QrCode2 sx={{ mr: 1 }} />
        QR Code Attendance
      </Typography>

      <Box sx={{ 
        width: 256, 
        height: 256, 
        mx: 'auto', 
        my: 3,
        border: '2px solid #1976d2',
        borderRadius: 2,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#ffffff',
        p: 2
      }}>
        {qrCodeHash ? (
          <QRCodeSVG 
            value={qrValue}
            size={200}
            level="H"
            includeMargin={true}
          />
        ) : (
          <Typography variant="body2" color="textSecondary">
            Generate QR Code to start
          </Typography>
        )}
      </Box>

      <Box sx={{ mb: 3 }}>
        <Typography variant="body2" color="textSecondary" gutterBottom>
          Students should scan this QR code with their mobile devices
        </Typography>
        <Typography variant="caption" color="textSecondary">
          Session: {isActive ? 'Active' : 'Inactive'} â€¢ Scanned: {scannedCount}
        </Typography>
        {isActive && sessionTimeLeft && (
          <Typography variant="caption" color="primary" display="block" sx={{ mt: 1, fontWeight: 'bold' }}>
            â° Expires in: {formatTime(sessionTimeLeft)}
          </Typography>
        )}
      </Box>

      <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center', flexWrap: 'wrap' }}>
        <Button
          variant="contained"
          startIcon={<Refresh />}
          onClick={onRefresh}
          disabled={!isActive}
          color="primary"
        >
          Refresh QR
        </Button>
        <Button
          variant="outlined"
          onClick={onSimulateScan}
          disabled={!isActive}
          color="primary"
        >
          Simulate Scan
        </Button>
      </Box>
    </Paper>
  );
};

// Faculty Profile Component
const FacultyProfile = ({ faculty, onUpdateProfile, setSnackbar }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editedProfile, setEditedProfile] = useState(faculty);
  const [loading, setLoading] = useState(false);

  const handleEdit = () => {
    setIsEditing(true);
    setEditedProfile({ ...faculty });
  };

  const handleCancel = () => {
    setIsEditing(false);
    setEditedProfile(faculty);
  };

  const handleSave = async () => {
    try {
      setLoading(true);
      await onUpdateProfile(editedProfile);
      setIsEditing(false);
      setSnackbar({
        open: true,
        message: 'Profile updated successfully!',
        severity: 'success'
      });
    } catch (error) {
      setSnackbar({
        open: true,
        message: 'Failed to update profile.',
        severity: 'error'
      });
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (field, value) => {
    setEditedProfile(prev => ({
      ...prev,
      [field]: value
    }));
  };

  return (
    <Paper sx={{ p: 3, border: '1px solid #1976d2' }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h5" color="primary.main">
          Faculty Profile
        </Typography>
        {!isEditing ? (
          <Button startIcon={<Edit />} onClick={handleEdit} variant="outlined" color="primary">
            Edit Profile
          </Button>
        ) : (
          <Box sx={{ display: 'flex', gap: 1 }}>
            <Button startIcon={<Cancel />} onClick={handleCancel} variant="outlined" color="secondary">
              Cancel
            </Button>
            <Button startIcon={<Save />} onClick={handleSave} variant="contained" color="primary" disabled={loading}>
              {loading ? 'Saving...' : 'Save'}
            </Button>
          </Box>
        )}
      </Box>

      <Grid container spacing={3}>
        <Grid item xs={12} md={4}>
          <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <Avatar sx={{ width: 120, height: 120, mb: 2, bgcolor: 'primary.main' }}>
              <Person sx={{ fontSize: 60 }} />
            </Avatar>
            <Typography variant="h6" gutterBottom>
              {faculty?.name}
            </Typography>
            <Typography variant="body2" color="textSecondary">
              {faculty?.department}
            </Typography>
          </Box>
        </Grid>

        <Grid item xs={12} md={8}>
          <Grid container spacing={2}>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Full Name"
                value={isEditing ? editedProfile.name : faculty?.name}
                onChange={(e) => handleChange('name', e.target.value)}
                disabled={!isEditing}
                InputProps={{ startAdornment: <InputAdornment position="start"><Person /></InputAdornment> }}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField fullWidth label="Employee ID" value={faculty?.employeeId || 'N/A'} disabled />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Email"
                value={isEditing ? editedProfile.email : faculty?.email}
                onChange={(e) => handleChange('email', e.target.value)}
                disabled={!isEditing}
                InputProps={{ startAdornment: <InputAdornment position="start"><Email /></InputAdornment> }}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Phone"
                value={isEditing ? editedProfile.phone : faculty?.phone}
                onChange={(e) => handleChange('phone', e.target.value)}
                disabled={!isEditing}
                InputProps={{ startAdornment: <InputAdornment position="start"><Phone /></InputAdornment> }}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField fullWidth label="Department" value={faculty?.department} disabled />
            </Grid>
          </Grid>
        </Grid>
      </Grid>
    </Paper>
  );
};

const FacultyPortal = ({ user, setUser }) => {
  const [activeTab, setActiveTab] = useState(0);
  const [courses, setCourses] = useState([]);
  const [attendanceDialog, setAttendanceDialog] = useState(false);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [qrCodeHash, setQrCodeHash] = useState('');
  const [attendanceMethod, setAttendanceMethod] = useState('qr');
  const [attendanceRecords, setAttendanceRecords] = useState({});
  const [manualAttendance, setManualAttendance] = useState({});
  const [scheduleData, setScheduleData] = useState([]);
  const [reportsData, setReportsData] = useState({});
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });
  const [qrSessionActive, setQrSessionActive] = useState(false);
  const [scannedCount, setScannedCount] = useState(0);
  const [qrSessionTimer, setQrSessionTimer] = useState(300);
  const [facultyProfile, setFacultyProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [connectionError, setConnectionError] = useState('');

  // Logout function
  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('accessToken');
    localStorage.removeItem('user');
    setUser(null);
    window.location.href = '/login';
  };

  // Enhanced data fetching with better error handling
  const fetchFacultyData = async () => {
    try {
      setLoading(true);
      setConnectionError('');
      console.log('ðŸš€ Starting to fetch faculty data...');

      // First test backend connection
      const isBackendConnected = await apiService.testConnection();
      if (!isBackendConnected) {
        throw new Error('Backend server is not running. Please start the server on port 5001.');
      }

      // Fetch profile
      try {
        const profileData = await apiService.getFacultyProfile();
        setFacultyProfile(profileData);
        console.log('âœ… Profile data loaded:', profileData);
      } catch (profileError) {
        console.error('âŒ Error fetching profile:', profileError);
        throw new Error('Failed to load profile. Please check your authentication.');
      }

      // Fetch courses
      await fetchFacultyCourses();

      // Fetch schedule
      try {
        const scheduleData = await apiService.getFacultySchedule();
        setScheduleData(scheduleData.schedule || []);
        console.log('âœ… Schedule data loaded');
      } catch (scheduleError) {
        console.warn('âš ï¸ Could not load schedule:', scheduleError);
        setScheduleData([]);
      }

      console.log('âœ… All faculty data loaded successfully');

    } catch (error) {
      console.error('âŒ Failed to fetch faculty data:', error);
      setConnectionError(error.message);
      setSnackbar({
        open: true,
        message: error.message || 'Failed to load data. Please check your connection.',
        severity: 'error'
      });
    } finally {
      setLoading(false);
    }
  };

  // Fixed course fetching function
  const fetchFacultyCourses = async () => {
    try {
      console.log('ðŸ“š Fetching faculty courses...');
      const coursesData = await apiService.getFacultyCourses();
      console.log('âœ… Courses data received:', coursesData);

      if (coursesData && coursesData.length > 0) {
        setCourses(coursesData);

        // Initialize attendance records
        const initialRecords = {};
        for (const course of coursesData) {
          const courseId = course._id || course.id;
          try {
            const students = await apiService.getCourseStudents(courseId);
            initialRecords[courseId] = Array.isArray(students) ? students.map(student => ({
              id: student._id || student.id,
              student_name: student.name || student.student_name,
              student_id: student.studentId || student.student_id,
              status: 'Absent',
              time: '-',
              method: '-'
            })) : [];
          } catch (error) {
            console.warn(`âš ï¸ Could not fetch students for course ${courseId}:`, error);
            initialRecords[courseId] = [];
          }
        }
        setAttendanceRecords(initialRecords);
        console.log('âœ… Attendance records initialized');
      } else {
        setCourses([]);
        console.log('â„¹ï¸ No courses found for faculty');
      }
    } catch (error) {
      console.error('âŒ Error fetching faculty courses:', error);
      setCourses([]);
      throw error;
    }
  };

  useEffect(() => {
    fetchFacultyData();
  }, []);

  useEffect(() => {
    let timer;
    if (qrSessionActive && qrSessionTimer > 0) {
      timer = setInterval(() => {
        setQrSessionTimer(prev => prev - 1);
      }, 1000);
    } else if (qrSessionTimer === 0 && qrSessionActive) {
      setQrSessionActive(false);
      setSnackbar({
        open: true,
        message: 'QR session expired.',
        severity: 'warning'
      });
    }
    return () => clearInterval(timer);
  }, [qrSessionActive, qrSessionTimer]);

  const generateQRCode = async (course) => {
    try {
      const sessionData = {
        courseId: course._id || course.id,
        courseCode: course.courseCode || course.course_code,
        duration: 300,
        method: 'qr'
      };

      const result = await apiService.startAttendanceSession(sessionData);
      const hash = result.sessionId || `QR_${Date.now()}`;

      setQrCodeHash(hash);
      setQrSessionActive(true);
      setQrSessionTimer(300);
      setScannedCount(0);

      setSnackbar({
        open: true,
        message: 'QR Code generated!',
        severity: 'success'
      });

      return hash;
    } catch (error) {
      setSnackbar({
        open: true,
        message: 'Failed to generate QR code.',
        severity: 'error'
      });
    }
  };

  const startAttendanceSession = async (course, method = 'qr') => {
    setSelectedCourse(course);
    setAttendanceMethod(method);

    if (method === 'qr') {
      await generateQRCode(course);
    }

    setAttendanceDialog(true);
  };

  const markAttendance = async (studentId, status = 'Present', method = 'Manual', studentName = '') => {
    if (!selectedCourse) return;

    try {
      const attendanceData = {
        studentId,
        courseId: selectedCourse._id || selectedCourse.id,
        status,
        method,
        timestamp: new Date().toISOString()
      };

      await apiService.markAttendance(attendanceData);

      const time = new Date().toLocaleTimeString();
      const courseId = selectedCourse._id || selectedCourse.id;

      setAttendanceRecords(prev => ({
        ...prev,
        [courseId]: prev[courseId]?.map(record =>
          record.student_id === studentId
            ? { ...record, status, time, method }
            : record
        ) || []
      }));

      if (method === 'QR Code') {
        setScannedCount(prev => prev + 1);
      }

    } catch (error) {
      setSnackbar({
        open: true,
        message: 'Failed to mark attendance.',
        severity: 'error'
      });
    }
  };

  const simulateQRScan = () => {
    if (!selectedCourse || !qrSessionActive) return;

    const courseId = selectedCourse._id || selectedCourse.id;
    const students = attendanceRecords[courseId] || [];
    const absentStudents = students.filter(s => s.status === 'Absent');

    if (absentStudents.length > 0) {
      const randomStudent = absentStudents[Math.floor(Math.random() * absentStudents.length)];
      markAttendance(randomStudent.student_id, 'Present', 'QR Code', randomStudent.student_name);
    }
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', flexDirection: 'column' }}>
        <CircularProgress size={60} />
        <Typography variant="h6" sx={{ mt: 2 }}>
          Loading Faculty Portal...
        </Typography>
        <Typography variant="body2" color="textSecondary" sx={{ mt: 1 }}>
          Please wait while we load your data
        </Typography>
      </Box>
    );
  }

  if (connectionError) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', flexDirection: 'column' }}>
        <Alert severity="error" sx={{ mb: 2, maxWidth: 500 }}>
          <Typography variant="h6">Connection Error</Typography>
          <Typography>{connectionError}</Typography>
        </Alert>
        <Button variant="contained" onClick={fetchFacultyData} startIcon={<Refresh />}>
          Retry
        </Button>
        <Button variant="outlined" onClick={handleLogout} sx={{ mt: 1 }}>
          Back to Login
        </Button>
      </Box>
    );
  }

  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="static" sx={{ bgcolor: '#1976d2', mb: 3 }}>
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Faculty Portal - Smart Classroom
          </Typography>
          <Typography variant="body2" sx={{ mr: 2 }}>
            Welcome, {facultyProfile?.name}
          </Typography>
          <Button color="inherit" startIcon={<Logout />} onClick={handleLogout}>
            Logout
          </Button>
        </Toolbar>
      </AppBar>

      <Container maxWidth="lg" sx={{ mb: 4 }}>
        <Paper sx={{ width: '100%', mb: 3 }}>
          <Tabs value={activeTab} onChange={(e, newValue) => setActiveTab(newValue)} variant="scrollable">
            <Tab icon={<Schedule />} label="My Schedule" />
            <Tab icon={<Groups />} label="My Courses" />
            <Tab icon={<QrCode2 />} label="Take Attendance" />
            <Tab icon={<Analytics />} label="Reports" />
            <Tab icon={<AccountCircle />} label="Profile" />
          </Tabs>
        </Paper>

        {/* My Courses Tab - FIXED */}
        {activeTab === 1 && (
          <Box>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
              <Typography variant="h5">My Courses</Typography>
              <Button startIcon={<Refresh />} onClick={fetchFacultyCourses} variant="outlined">
                Refresh Courses
              </Button>
            </Box>

            {courses.length > 0 ? (
              <Grid container spacing={3}>
                {courses.map((course, index) => (
                  <Grid item xs={12} md={6} key={course._id || course.id || index}>
                    <Card sx={{ height: '100%', border: '1px solid #1976d2' }}>
                      <CardContent>
                        <Typography variant="h6" color="primary">
                          {course.courseCode || course.course_code}
                        </Typography>
                        <Typography variant="body1" sx={{ fontWeight: 'bold', mb: 1 }}>
                          {course.courseName || course.course_name}
                        </Typography>
                        <Typography variant="body2" color="textSecondary">
                          Department: {course.department}
                        </Typography>
                        <Typography variant="body2" color="textSecondary">
                          Schedule: {course.schedule || course.schedule_time}
                        </Typography>
                        <Typography variant="body2" color="textSecondary">
                          Room: {course.room}
                        </Typography>

                        <Box sx={{ mt: 2, display: 'flex', gap: 1, flexWrap: 'wrap' }}>
                          <Button 
                            variant="contained" 
                            size="small"
                            onClick={() => startAttendanceSession(course, 'qr')}
                            startIcon={<QrCode2 />}
                          >
                            QR Code
                          </Button>
                          <Button 
                            variant="outlined" 
                            size="small"
                            onClick={() => startAttendanceSession(course, 'face')}
                            startIcon={<Face />}
                          >
                            Face Recognition
                          </Button>
                        </Box>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            ) : (
              <Paper sx={{ p: 4, textAlign: 'center' }}>
                <Typography variant="h6" color="textSecondary">
                  No Courses Assigned
                </Typography>
                <Typography variant="body2" color="textSecondary" sx={{ mt: 1 }}>
                  You haven't been assigned any courses yet. Please contact the administrator.
                </Typography>
                <Button 
                  variant="outlined" 
                  onClick={fetchFacultyCourses} 
                  startIcon={<Refresh />}
                  sx={{ mt: 2 }}
                >
                  Check Again
                </Button>
              </Paper>
            )}
          </Box>
        )}

        {/* Take Attendance Tab */}
        {activeTab === 2 && (
          <Box>
            <Typography variant="h5" gutterBottom sx={{ mb: 3 }}>
              Quick Attendance
            </Typography>
            {courses.length > 0 ? (
              <Grid container spacing={3}>
                {courses.map((course, index) => (
                  <Grid item xs={12} md={4} key={course._id || course.id || index}>
                    <Card sx={{ textAlign: 'center', p: 3 }}>
                      <CardContent>
                        <Typography variant="h6" color="primary">
                          {course.courseCode || course.course_code}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" sx={{ mb: 2 }}>
                          {course.courseName || course.course_name}
                        </Typography>
                        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
                          <Button 
                            variant="contained" 
                            onClick={() => startAttendanceSession(course, 'qr')}
                            startIcon={<QrCode2 />}
                          >
                            QR Attendance
                          </Button>
                          <Button 
                            variant="outlined" 
                            onClick={() => startAttendanceSession(course, 'face')}
                            startIcon={<Face />}
                          >
                            Face Recognition
                          </Button>
                        </Box>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            ) : (
              <Paper sx={{ p: 4, textAlign: 'center' }}>
                <Typography variant="h6" color="textSecondary">
                  No courses available for attendance
                </Typography>
              </Paper>
            )}
          </Box>
        )}

        {/* Attendance Dialog */}
        <Dialog open={attendanceDialog} onClose={() => setAttendanceDialog(false)} maxWidth="md" fullWidth>
          <DialogTitle>
            Take Attendance - {selectedCourse?.courseCode || selectedCourse?.course_code}
          </DialogTitle>
          <DialogContent>
            <Box sx={{ mb: 3, display: 'flex', gap: 1 }}>
              <Button 
                variant={attendanceMethod === 'qr' ? 'contained' : 'outlined'}
                onClick={() => setAttendanceMethod('qr')}
                startIcon={<QrCode2 />}
              >
                QR Code
              </Button>
              <Button 
                variant={attendanceMethod === 'face' ? 'contained' : 'outlined'}
                onClick={() => setAttendanceMethod('face')}
                startIcon={<Face />}
              >
                Face Recognition
              </Button>
            </Box>

            {attendanceMethod === 'qr' && (
              <QRAttendanceComponent
                qrCodeHash={qrCodeHash}
                onRefresh={() => generateQRCode(selectedCourse)}
                onSimulateScan={simulateQRScan}
                scannedCount={scannedCount}
                isActive={qrSessionActive}
                course={selectedCourse}
                sessionTimeLeft={qrSessionTimer}
              />
            )}

            {attendanceMethod === 'face' && (
              <RealFaceRecognition
                onAttendanceMarked={markAttendance}
                isActive={attendanceMethod === 'face'}
                setSnackbar={setSnackbar}
                course={selectedCourse}
              />
            )}
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setAttendanceDialog(false)}>Close</Button>
          </DialogActions>
        </Dialog>

        <Snackbar
          open={snackbar.open}
          autoHideDuration={4000}
          onClose={() => setSnackbar({ ...snackbar, open: false })}
        >
          <Alert severity={snackbar.severity}>
            {snackbar.message}
          </Alert>
        </Snackbar>
      </Container>
    </Box>
  );
};

export default FacultyPortal;
