import OverviewTab from '../pages/admin/tabs/OverviewTab';
import UserManagementTab from '../pages/admin/tabs/UserManagementTab';
import CourseManagementTab from '../pages/admin/tabs/CourseManagementTab';
import ClassroomManagementTab from '../pages/admin/tabs/ClassroomManagementTab';
import TimetableManagementTab from '../pages/admin/tabs/TimetableManagementTab';
import CourseFacultyMappingTab from './tabs/CourseFacultyMappingTab';
import ReportsAnalyticsTab from './tabs/ReportsAnalyticsTab';
import SystemSettingsTab from './tabs/SystemSettingsTab';

import StatDetailsDialog from './dialogs/StatDetailsDialog';
import AddUserDialog from './dialogs/AddUserDialog';
import AddCourseDialog from './dialogs/AddCourseDialog';
import AddClassroomDialog from './dialogs/AddClassroomDialog';
import CourseFacultyMappingDialog from './dialogs/CourseFacultyMappingDialog';
import CourseDetailsDialog from './dialogs/CourseDetailsDialog';
import EditCourseDialog from './dialogs/EditCourseDialog';

import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Paper,
  Button,
  Tabs,
  Tab,
  Box,
  AppBar,
  Toolbar,
  Avatar,
  Badge,
  Alert,
  Snackbar
} from '@mui/material';
import {
  Refresh,
  Logout,
  Dashboard,
  People,
  School,
  Class,
  Schedule,
  Analytics,
  Settings,
  AdminPanelSettings,
  Person,
  Group,
  Notifications
} from '@mui/icons-material';

// API service functions - replace with your actual API calls
const apiService = {
  // Users
  getUsers: async () => {
    const response = await fetch('/api/users');
    return await response.json();
  },
  createUser: async (userData) => {
    const response = await fetch('/api/users', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(userData)
    });
    return await response.json();
  },
  updateUser: async (userId, userData) => {
    const response = await fetch(`/api/users/${userId}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(userData)
    });
    return await response.json();
  },
  deleteUser: async (userId) => {
    const response = await fetch(`/api/users/${userId}`, { method: 'DELETE' });
    return await response.json();
  },

  // Courses
  getCourses: async () => {
    const response = await fetch('/api/courses');
    return await response.json();
  },
  createCourse: async (courseData) => {
    const response = await fetch('/api/courses', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(courseData)
    });
    return await response.json();
  },
  updateCourse: async (courseId, courseData) => {
    const response = await fetch(`/api/courses/${courseId}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(courseData)
    });
    return await response.json();
  },
  deleteCourse: async (courseId) => {
    const response = await fetch(`/api/courses/${courseId}`, { method: 'DELETE' });
    return await response.json();
  },

  // Classrooms
  getClassrooms: async () => {
    const response = await fetch('/api/classrooms');
    return await response.json();
  },
  createClassroom: async (classroomData) => {
    const response = await fetch('/api/classrooms', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(classroomData)
    });
    return await response.json();
  },
  deleteClassroom: async (classroomId) => {
    const response = await fetch(`/api/classrooms/${classroomId}`, { method: 'DELETE' });
    return await response.json();
  },

  // System Stats
  getSystemStats: async () => {
    const response = await fetch('/api/system/stats');
    return await response.json();
  },

  // Timetable
  generateTimetable: async (timetableData) => {
    const response = await fetch('/api/timetable/generate', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(timetableData)
    });
    return await response.json();
  },

  // Course-Faculty Mapping
  mapCourseToFaculty: async (mappingData) => {
    const response = await fetch('/api/courses/map-faculty', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(mappingData)
    });
    return await response.json();
  }
};

const AdminPortal = ({ user, setUser }) => {
  const [activeTab, setActiveTab] = useState(0);
  const [users, setUsers] = useState([]);
  const [courses, setCourses] = useState([]);
  const [classrooms, setClassrooms] = useState([]);
  const [timetable, setTimetable] = useState([]);
  const [systemStats, setSystemStats] = useState({});
  const [loading, setLoading] = useState(false);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });

  // Dialog states
  const [selectedStat, setSelectedStat] = useState(null);
  const [statDetailsDialog, setStatDetailsDialog] = useState(false);
  const [newUserDialog, setNewUserDialog] = useState(false);
  const [newCourseDialog, setNewCourseDialog] = useState(false);
  const [newClassroomDialog, setNewClassroomDialog] = useState(false);
  const [courseFacultyDialog, setCourseFacultyDialog] = useState(false);
  const [courseDetailsDialog, setCourseDetailsDialog] = useState(false);
  const [editCourseDialog, setEditCourseDialog] = useState(false);
  const [selectedCourse, setSelectedCourse] = useState(null);

  // Form states
  const [newUser, setNewUser] = useState({
    name: '', email: '', role: 'student', password: '', 
    department: 'Computer Science', phone: '', semester: 1
  });

  const [newCourse, setNewCourse] = useState({
    code: '', name: '', credits: 3, faculty: '', 
    maxStudents: 50, department: 'Computer Science', 
    description: '', courseType: 'Theory'
  });

  const [newClassroom, setNewClassroom] = useState({
    name: '', capacity: 30, facilities: '', 
    building: 'Main', floor: '1', type: 'Lecture Hall'
  });

  const [courseMapping, setCourseMapping] = useState({
    courseId: '', facultyId: '', semester: 1, schedule: []
  });

  // Real-time data
  const [realtimeData, setRealtimeData] = useState({
    activeSessions: 0,
    serverLoad: 0,
    onlineUsers: 0
  });

  const [notifications, setNotifications] = useState([]);

  // Color scheme
  const colorScheme = {
    primary: '#1976d2',
    primaryLight: '#42a5f5',
    primaryDark: '#1565c0',
    secondary: '#dc004e',
    success: '#2e7d32',
    warning: '#ed6c02',
    error: '#d32f2f',
    info: '#0288d1',
    background: '#f5f5f5'
  };

  // Show snackbar message
  const showSnackbar = (message, severity = 'success') => {
    setSnackbar({ open: true, message, severity });
  };

  // Fetch all data
  const fetchAdminData = async () => {
    setLoading(true);
    try {
      const [usersData, coursesData, classroomsData, statsData] = await Promise.all([
        apiService.getUsers(),
        apiService.getCourses(),
        apiService.getClassrooms(),
        apiService.getSystemStats()
      ]);

      setUsers(usersData);
      setCourses(coursesData);
      setClassrooms(classroomsData);
      setSystemStats(statsData);

      showSnackbar('Data loaded successfully');
    } catch (error) {
      console.error('Error fetching data:', error);
      showSnackbar('Error loading data', 'error');
    } finally {
      setLoading(false);
    }
  };

  // User operations
  const addNewUser = async () => {
    try {
      const result = await apiService.createUser(newUser);
      setUsers(prev => [...prev, result]);
      setNewUserDialog(false);
      setNewUser({ name: '', email: '', role: 'student', password: '', department: 'Computer Science', phone: '', semester: 1 });
      showSnackbar('User created successfully');
    } catch (error) {
      showSnackbar('Error creating user', 'error');
    }
  };

  const updateUserStatus = async (userId, status) => {
    try {
      await apiService.updateUser(userId, { status });
      setUsers(prev => prev.map(user => 
        user.id === userId ? { ...user, status } : user
      ));
      showSnackbar('User status updated');
    } catch (error) {
      showSnackbar('Error updating user', 'error');
    }
  };

  const deleteUser = async (userId) => {
    try {
      await apiService.deleteUser(userId);
      setUsers(prev => prev.filter(user => user.id !== userId));
      showSnackbar('User deleted successfully');
    } catch (error) {
      showSnackbar('Error deleting user', 'error');
    }
  };

  // Course operations
  const addNewCourse = async () => {
    try {
      const result = await apiService.createCourse(newCourse);
      setCourses(prev => [...prev, result]);
      setNewCourseDialog(false);
      setNewCourse({ code: '', name: '', credits: 3, faculty: '', maxStudents: 50, department: 'Computer Science', description: '', courseType: 'Theory' });
      showSnackbar('Course created successfully');
    } catch (error) {
      showSnackbar('Error creating course', 'error');
    }
  };

  const deleteCourse = async (courseId) => {
    try {
      await apiService.deleteCourse(courseId);
      setCourses(prev => prev.filter(course => course.id !== courseId));
      showSnackbar('Course deleted successfully');
    } catch (error) {
      showSnackbar('Error deleting course', 'error');
    }
  };

  const handleEditCourse = (course) => {
    setSelectedCourse(course);
    setEditCourseDialog(true);
  };

  const handleViewCourseDetails = (course) => {
    setSelectedCourse(course);
    setCourseDetailsDialog(true);
  };

  // Classroom operations
  const addNewClassroom = async () => {
    try {
      const result = await apiService.createClassroom(newClassroom);
      setClassrooms(prev => [...prev, result]);
      setNewClassroomDialog(false);
      setNewClassroom({ name: '', capacity: 30, facilities: '', building: 'Main', floor: '1', type: 'Lecture Hall' });
      showSnackbar('Classroom created successfully');
    } catch (error) {
      showSnackbar('Error creating classroom', 'error');
    }
  };

  const deleteClassroom = async (classroomId) => {
    try {
      await apiService.deleteClassroom(classroomId);
      setClassrooms(prev => prev.filter(classroom => classroom.id !== classroomId));
      showSnackbar('Classroom deleted successfully');
    } catch (error) {
      showSnackbar('Error deleting classroom', 'error');
    }
  };

  // Course-Faculty mapping
  const mapCourseToFaculty = async () => {
    try {
      await apiService.mapCourseToFaculty(courseMapping);
      // Refresh courses to get updated faculty mapping
      const updatedCourses = await apiService.getCourses();
      setCourses(updatedCourses);
      setCourseFacultyDialog(false);
      setCourseMapping({ courseId: '', facultyId: '', semester: 1, schedule: [] });
      showSnackbar('Course mapped to faculty successfully');
    } catch (error) {
      showSnackbar('Error mapping course to faculty', 'error');
    }
  };

  // Timetable generation
  const generateTimetable = async (optimizeFor = 'no_conflicts') => {
    try {
      const timetableData = {
        semester: 1,
        academicYear: new Date().getFullYear(),
        optimizeFor
      };

      const result = await apiService.generateTimetable(timetableData);
      setTimetable(result.timetable);
      showSnackbar('Timetable generated successfully');
    } catch (error) {
      showSnackbar('Error generating timetable', 'error');
    }
  };

  // Utility functions
  const getFacultyMembers = () => users.filter(user => user.role === 'faculty');
  const getUnmappedCourses = () => courses.filter(course => !course.facultyId);

  const getRoleColor = (role) => {
    switch (role) {
      case 'admin': return 'error';
      case 'faculty': return 'warning';
      case 'student': return 'success';
      default: return 'default';
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'Active': return 'success';
      case 'Inactive': return 'error';
      case 'Available': return 'success';
      default: return 'default';
    }
  };

  // Load data on component mount
  useEffect(() => {
    fetchAdminData();

    // Real-time updates for system stats
    const interval = setInterval(async () => {
      try {
        const stats = await apiService.getSystemStats();
        setRealtimeData({
          activeSessions: stats.activeSessions || 0,
          serverLoad: stats.serverLoad || 0,
          onlineUsers: stats.onlineUsers || 0
        });
      } catch (error) {
        console.error('Error fetching real-time data:', error);
      }
    }, 10000);

    return () => clearInterval(interval);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
  };

  return (
    <Box sx={{ flexGrow: 1, bgcolor: colorScheme.background, minHeight: '100vh' }}>
      {/* Header */}
      <AppBar position="static" elevation={2} sx={{ bgcolor: colorScheme.primary }}>
        <Toolbar>
          <AdminPanelSettings sx={{ mr: 2 }} />
          <Typography variant="h6" component="div" sx={{ flexGrow: 1, fontWeight: 'bold' }}>
            ðŸŽ“ Smart Classroom Admin Portal
          </Typography>

          {/* Real-time Info */}
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 3, mr: 3 }}>
            <Box sx={{ textAlign: 'center' }}>
              <Typography variant="caption" sx={{ opacity: 0.9, display: 'block' }}>
                Active Sessions
              </Typography>
              <Typography variant="body2" sx={{ fontWeight: 'bold' }}>
                {realtimeData.activeSessions}
              </Typography>
            </Box>
            <Box sx={{ textAlign: 'center' }}>
              <Typography variant="caption" sx={{ opacity: 0.9, display: 'block' }}>
                Online Users
              </Typography>
              <Typography variant="body2" sx={{ fontWeight: 'bold' }}>
                {realtimeData.onlineUsers}
              </Typography>
            </Box>
            <Box sx={{ textAlign: 'center' }}>
              <Typography variant="caption" sx={{ opacity: 0.9, display: 'block' }}>
                Server Load
              </Typography>
              <Typography variant="body2" sx={{ fontWeight: 'bold' }}>
                {realtimeData.serverLoad}%
              </Typography>
            </Box>
          </Box>

          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
            <Badge badgeContent={notifications.filter(n => !n.read).length} color="error">
              <Notifications sx={{ color: 'white' }} />
            </Badge>
            <Avatar sx={{ width: 32, height: 32, bgcolor: colorScheme.primaryLight }}>
              {user?.name?.charAt(0) || 'A'}
            </Avatar>
            <Typography variant="body2">{user?.name}</Typography>
            <Button color="inherit" startIcon={<Logout />} onClick={handleLogout}>
              Logout
            </Button>
          </Box>
        </Toolbar>
      </AppBar>

      <Container maxWidth="xl" sx={{ mt: 3, mb: 4 }}>
        {/* Status Bar */}
        <Paper sx={{ 
          p: 2, 
          mb: 3, 
          bgcolor: colorScheme.primary, 
          color: 'white',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center'
        }}>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 3 }}>
            <Box>
              <Typography variant="caption" sx={{ opacity: 0.9 }}>System Status</Typography>
              <Typography variant="body1" sx={{ fontWeight: 'bold', color: colorScheme.success }}>
                â— All Systems Operational
              </Typography>
            </Box>
          </Box>
          <Button 
            variant="contained" 
            size="small" 
            sx={{ 
              bgcolor: 'white', 
              color: colorScheme.primary,
              '&:hover': { bgcolor: '#f5f5f5' }
            }}
            startIcon={<Refresh />}
            onClick={fetchAdminData}
            disabled={loading}
          >
            {loading ? 'Loading...' : 'Refresh Data'}
          </Button>
        </Paper>

        {/* Main Navigation Tabs */}
        <Paper sx={{ width: '100%', mb: 3, borderRadius: 2 }}>
          <Tabs 
            value={activeTab} 
            onChange={(e, newValue) => setActiveTab(newValue)} 
            variant="scrollable" 
            scrollButtons="auto"
          >
            <Tab icon={<Dashboard />} label="Overview" />
            <Tab icon={<People />} label="User Management" />
            <Tab icon={<School />} label="Course Management" />
            <Tab icon={<Class />} label="Classroom Management" />
            <Tab icon={<Schedule />} label="Timetable Management" />
            <Tab icon={<Group />} label="Course-Faculty Mapping" />
            <Tab icon={<Analytics />} label="Reports & Analytics" />
            <Tab icon={<Settings />} label="System Settings" />
          </Tabs>
        </Paper>

        {/* Tab Content */}
        {activeTab === 0 && (
          <OverviewTab 
            systemStats={systemStats}
            realtimeData={realtimeData}
            onStatClick={handleStatClick}
            onAddUser={() => setNewUserDialog(true)}
            colorScheme={colorScheme}
            loading={loading}
          />
        )}

        {activeTab === 1 && (
          <UserManagementTab 
            users={users}
            onAddUser={() => setNewUserDialog(true)}
            onDeleteUser={deleteUser}
            onToggleStatus={updateUserStatus}
            getRoleColor={getRoleColor}
            getStatusColor={getStatusColor}
            colorScheme={colorScheme}
            loading={loading}
          />
        )}

        {activeTab === 2 && (
          <CourseManagementTab 
            courses={courses}
            onAddCourse={() => setNewCourseDialog(true)}
            onDeleteCourse={deleteCourse}
            onViewDetails={handleViewCourseDetails}
            onEditCourse={handleEditCourse}
            onMapCourse={() => setCourseFacultyDialog(true)}
            colorScheme={colorScheme}
            loading={loading}
          />
        )}

        {activeTab === 3 && (
          <ClassroomManagementTab 
            classrooms={classrooms}
            onAddClassroom={() => setNewClassroomDialog(true)}
            onDeleteClassroom={deleteClassroom}
            getStatusColor={getStatusColor}
            colorScheme={colorScheme}
            loading={loading}
          />
        )}

        {/* Add other tabs as needed */}

      </Container>

      {/* Dialogs */}
      <StatDetailsDialog 
        open={statDetailsDialog} 
        onClose={() => setStatDetailsDialog(false)} 
        stat={selectedStat}
        colorScheme={colorScheme}
      />

      <AddUserDialog 
        open={newUserDialog} 
        onClose={() => setNewUserDialog(false)} 
        newUser={newUser} 
        setNewUser={setNewUser} 
        onAdd={addNewUser} 
        colorScheme={colorScheme} 
      />

      <AddCourseDialog 
        open={newCourseDialog} 
        onClose={() => setNewCourseDialog(false)} 
        newCourse={newCourse} 
        setNewCourse={setNewCourse} 
        onAdd={addNewCourse} 
        colorScheme={colorScheme} 
      />

      <AddClassroomDialog 
        open={newClassroomDialog} 
        onClose={() => setNewClassroomDialog(false)} 
        newClassroom={newClassroom} 
        setNewClassroom={setNewClassroom} 
        onAdd={addNewClassroom} 
        colorScheme={colorScheme} 
      />

      <CourseFacultyMappingDialog 
        open={courseFacultyDialog} 
        onClose={() => setCourseFacultyDialog(false)} 
        courses={getUnmappedCourses()}
        faculty={getFacultyMembers()}
        courseMapping={courseMapping}
        setCourseMapping={setCourseMapping}
        onMap={mapCourseToFaculty}
        colorScheme={colorScheme}
      />

      <CourseDetailsDialog 
        open={courseDetailsDialog}
        onClose={() => setCourseDetailsDialog(false)}
        course={selectedCourse}
        colorScheme={colorScheme}
      />

      <EditCourseDialog 
        open={editCourseDialog}
        onClose={() => setEditCourseDialog(false)}
        course={selectedCourse}
        colorScheme={colorScheme}
      />

      {/* Snackbar for notifications */}
      <Snackbar
        open={snackbar.open}
        autoHideDuration={6000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
      >
        <Alert 
          onClose={() => setSnackbar({ ...snackbar, open: false })} 
          severity={snackbar.severity}
          sx={{ width: '100%' }}
        >
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default AdminPortal;
