// Fetch users from API
const fetchUsers = async () => {
  setLoading(true);
  try {
    console.log('üîÑ Starting fetchUsers...');
    const result = await userService.fetchUsers();
    console.log('üìä fetchUsers result:', result);

    if (result.success) {
      // Ensure users is always an array
      const usersArray = Array.isArray(result.data) ? result.data : [];
      console.log('‚úÖ Setting users array with', usersArray.length, 'users:', usersArray);
      setUsers(usersArray);
    } else {
      console.error('‚ùå fetchUsers failed:', result.error);
      showSnackbar(result.error, 'error');
      setUsers([]); // Set empty array on error
    }
  } catch (error) {
    console.error('‚ùå Fetch users error:', error);
    showSnackbar('Failed to fetch users', 'error');
    setUsers([]); // Set empty array on error
  } finally {
    setLoading(false);
  }
};
