// UserManagementTab.jsx
import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Button,
  Card,
  CardContent,
  Avatar,
  Chip,
  Switch,
  IconButton,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Collapse,
  Tabs,
  Tab,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  TextField,
  MenuItem,
  Snackbar,
  Alert,
  CircularProgress,
  Tooltip,
  Grid
} from '@mui/material';
import {
  Add,
  Refresh,
  Edit,
  Delete,
  Dashboard,
  ViewList,
  ExpandLess,
  ExpandMore
} from '@mui/icons-material';
import { userService } from '../../../services/userService';

const checkAuth = () => {
  const token = localStorage.getItem('accessToken');
  if (!token) {
    alert('Please login first!');
    window.location.href = '/login';
    return false;
  }
  return true;
};

const UserManagementTab = () => {
  const [users, setUsers] = useState([]);
  const [expandedGroups, setExpandedGroups] = useState({});
  const [viewMode, setViewMode] = useState('groups');
  const [loading, setLoading] = useState(false);
  const [actionLoading, setActionLoading] = useState(false);
  const [openDialog, setOpenDialog] = useState(false);
  const [openEditDialog, setOpenEditDialog] = useState(false);
  const [selectedUser, setSelectedUser] = useState(null);
  const [snackbar, setSnackbar] = useState({ 
    open: false, 
    message: '', 
    severity: 'success' 
  });

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    role: '',
    department: '',
    studentId: '',
    facultyId: '',
    adminId: '',
    staffId: '',
    status: 'Active'
  });

  const [formErrors, setFormErrors] = useState({});

  const departments = [
    'Computer Science',
    'Mathematics',
    'Physics',
    'Chemistry',
    'Biology',
    'Electrical Engineering',
    'Mechanical Engineering',
    'Civil Engineering',
    'Business Administration',
    'Economics',
    'Psychology',
    'Administration'
  ];

  const roleConfig = {
    admin: { color: 'error', icon: 'ðŸ‘‘', label: 'Admin' },
    hod: { color: 'info', icon: 'ðŸŽ“', label: 'HOD' },
    faculty: { color: 'warning', icon: 'ðŸ‘¨â€ðŸ«', label: 'Faculty' },
    student: { color: 'success', icon: 'ðŸŽ’', label: 'Student' },
    staff: { color: 'secondary', icon: 'ðŸ‘¨â€ðŸ’¼', label: 'Staff' }
  };

  const fetchUsers = async () => {
    setLoading(true);
    try {
      const result = await userService.fetchUsers();
      if (result.success) {
        const usersArray = Array.isArray(result.data) ? result.data : [];
        setUsers(usersArray);
      } else {
        showSnackbar(result.error, 'error');
        setUsers([]);
      }
    } catch (error) {
      console.error('Fetch users error:', error);
      showSnackbar('Failed to fetch users', 'error');
      setUsers([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  const showSnackbar = (message, severity = 'success') => {
    setSnackbar({ open: true, message, severity });
  };

  const handleRefresh = async () => {
    await fetchUsers();
    showSnackbar('Users refreshed successfully');
  };

  const handleAddUser = () => {
    setFormData({
      name: '',
      email: '',
      password: '',
      role: '',
      department: '',
      studentId: '',
      facultyId: '',
      adminId: '',
      staffId: '',
      status: 'Active'
    });
    setFormErrors({});
    setOpenDialog(true);
  };

  const handleEditUser = (user) => {
    setSelectedUser(user);
    setFormData({
      name: user.name,
      email: user.email,
      password: '',
      role: user.role,
      department: user.department || '',
      studentId: user.studentId || '',
      facultyId: user.facultyId || '',
      adminId: user.adminId || '',
      staffId: user.staffId || '',
      status: user.status
    });
    setFormErrors({});
    setOpenEditDialog(true);
  };

  const validateForm = () => {
    const errors = {};
    if (!formData.name.trim()) errors.name = 'Name is required';
    if (!formData.email.trim()) errors.email = 'Email is required';
    else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email))
      errors.email = 'Please enter a valid email address';
    if (!formData.role) errors.role = 'Role is required';
    if (!openEditDialog && !formData.password)
      errors.password = 'Password is required';
    else if (formData.password && formData.password.length < 6)
      errors.password = 'Password must be at least 6 characters long';
    if (formData.role === 'student' && !formData.studentId)
      errors.studentId = 'Student ID is required';
    if ((formData.role === 'faculty' || formData.role === 'hod') && !formData.facultyId)
      errors.facultyId = 'Faculty ID is required';
    if (formData.role === 'admin' && !formData.adminId)
      errors.adminId = 'Admin ID is required';
    if (formData.role === 'staff' && !formData.staffId)
      errors.staffId = 'Staff ID is required';
    setFormErrors(errors);
    return Object.keys(errors).length === 0;
  };

  const handleSubmit = async () => {
    if (!checkAuth()) return;
    if (!validateForm()) return;
    setActionLoading(true);
    try {
      const submitData = { ...formData };
      const result = await userService.createUser(submitData);
      if (result.success) {
        setOpenDialog(false);
        await fetchUsers();
        showSnackbar('User added successfully');
      } else {
        showSnackbar(result.error, 'error');
      }
    } catch (error) {
      console.error('Add user error:', error);
      showSnackbar('Failed to add user', 'error');
    } finally {
      setActionLoading(false);
    }
  };

  const handleUpdate = async () => {
    if (!validateForm()) return;
    setActionLoading(true);
    try {
      const submitData = { ...formData };
      if (!formData.password) delete submitData.password;
      const result = await userService.updateUser(selectedUser.id, submitData);
      if (result.success) {
        setOpenEditDialog(false);
        setSelectedUser(null);
        await fetchUsers();
        showSnackbar('User updated successfully');
      } else {
        showSnackbar(result.error, 'error');
      }
    } catch (error) {
      console.error('Update user error:', error);
      showSnackbar('Failed to update user', 'error');
    } finally {
      setActionLoading(false);
    }
  };

  const handleDeleteUser = async (userId) => {
    if (!window.confirm('Are you sure you want to delete this user?')) return;
    setActionLoading(true);
    try {
      const result = await userService.deleteUser(userId);
      if (result.success) {
        await fetchUsers();
        showSnackbar('User deleted successfully');
      } else {
        showSnackbar(result.error, 'error');
      }
    } catch (error) {
      console.error('Delete user error:', error);
      showSnackbar('Failed to delete user', 'error');
    } finally {
      setActionLoading(false);
    }
  };

  const handleToggleStatus = async (userId, currentStatus) => {
    setActionLoading(true);
    try {
      const result = await userService.toggleUserStatus(userId, currentStatus);
      if (result.success) {
        await fetchUsers();
        showSnackbar(`User status updated to ${result.data.newStatus}`);
      } else {
        showSnackbar(result.error, 'error');
      }
    } catch (error) {
      console.error('Toggle status error:', error);
      showSnackbar('Failed to update user status', 'error');
    } finally {
      setActionLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
    if (formErrors[name]) {
      setFormErrors((prev) => ({ ...prev, [name]: '' }));
    }
  };

  const getRoleColor = (role) => roleConfig[role]?.color || 'default';
  const getStatusColor = (status) => (status === 'Active' ? 'success' : 'error');

  const groupedUsers = Array.isArray(users)
    ? users.reduce((groups, user) => {
        const role = user.role;
        if (!groups[role]) groups[role] = [];
        groups[role].push(user);
        return groups;
      }, {})
    : {};

  const toggleGroup = (groupName) =>
    setExpandedGroups((prev) => ({ ...prev, [groupName]: !prev[groupName] }));

  const UserFormDialog = ({ open, onClose, onSubmit, title, loading, isEdit = false }) => (
    <Dialog
      open={open}
      onClose={loading ? undefined : onClose}
      maxWidth="md"
      fullWidth
      disableEscapeKeyDown={loading}
    >
      <DialogTitle>{title}</DialogTitle>
      <DialogContent>
        <Box sx={{ mt: 0.5 }}>
          <Grid container spacing={2}>
            <Grid item xs={12} sm={6}>
              <TextField
                required
                name="name"
                label="Full Name"
                value={formData.name}
                onChange={handleInputChange}
                fullWidth
                error={!!formErrors.name}
                helperText={formErrors.name}
                disabled={loading}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                required
                name="email"
                label="Email"
                type="email"
                value={formData.email}
                onChange={handleInputChange}
                fullWidth
                error={!!formErrors.email}
                helperText={formErrors.email}
                disabled={loading}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                required={!isEdit}
                name="password"
                label={isEdit ? 'New Password (optional)' : 'Password *'}
                type="password"
                value={formData.password}
                onChange={handleInputChange}
                fullWidth
                error={!!formErrors.password}
                helperText={formErrors.password}
                disabled={loading}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                required
                name="role"
                label="Role"
                select
                value={formData.role}
                onChange={handleInputChange}
                fullWidth
                error={!!formErrors.role}
                helperText={formErrors.role}
                disabled={loading}
              >
                <MenuItem value="admin">Admin</MenuItem>
                <MenuItem value="hod">HOD</MenuItem>
                <MenuItem value="faculty">Faculty</MenuItem>
                <MenuItem value="student">Student</MenuItem>
                <MenuItem value="staff">Staff</MenuItem>
              </TextField>
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                name="department"
                label="Department"
                select
                value={formData.department}
                onChange={handleInputChange}
                fullWidth
                disabled={loading}
              >
                <MenuItem value="">Select Department</MenuItem>
                {departments.map((dept) => (
                  <MenuItem key={dept} value={dept}>
                    {dept}
                  </MenuItem>
                ))}
              </TextField>
            </Grid>

            {formData.role === 'student' && (
              <Grid item xs={12}>
                <TextField
                  required
                  name="studentId"
                  label="Student ID"
                  value={formData.studentId}
                  onChange={handleInputChange}
                  fullWidth
                  error={!!formErrors.studentId}
                  helperText={formErrors.studentId}
                  disabled={loading}
                />
              </Grid>
            )}

            {(formData.role === 'faculty' || formData.role === 'hod') && (
              <Grid item xs={12}>
                <TextField
                  required
                  name="facultyId"
                  label="Faculty ID"
                  value={formData.facultyId}
                  onChange={handleInputChange}
                  fullWidth
                  error={!!formErrors.facultyId}
                  helperText={formErrors.facultyId}
                  disabled={loading}
                />
              </Grid>
            )}

            {formData.role === 'admin' && (
              <Grid item xs={12}>
                <TextField
                  required
                  name="adminId"
                  label="Admin ID"
                  value={formData.adminId}
                  onChange={handleInputChange}
                  fullWidth
                  error={!!formErrors.adminId}
                  helperText={formErrors.adminId}
                  disabled={loading}
                />
              </Grid>
            )}

            {formData.role === 'staff' && (
              <Grid item xs={12}>
                <TextField
                  required
                  name="staffId"
                  label="Staff ID"
                  value={formData.staffId}
                  onChange={handleInputChange}
                  fullWidth
                  error={!!formErrors.staffId}
                  helperText={formErrors.staffId}
                  disabled={loading}
                />
              </Grid>
            )}

            <Grid item xs={12}>
              <TextField
                name="status"
                label="Status"
                select
                value={formData.status}
                onChange={handleInputChange}
                fullWidth
                disabled={loading}
              >
                <MenuItem value="Active">Active</MenuItem>
                <MenuItem value="Inactive">Inactive</MenuItem>
              </TextField>
            </Grid>
          </Grid>
        </Box>
      </DialogContent>
      <DialogActions sx={{ p: 3 }}>
        <Button
          type="button"
          onClick={onClose}
          disabled={loading}
          variant="outlined"
        >
          Cancel
        </Button>
        <Button
          type="button"
          onClick={onSubmit}
          variant="contained"
          disabled={loading}
          startIcon={loading ? <CircularProgress size={20} /> : null}
          sx={{ minWidth: 100 }}
        >
          {loading ? 'Saving...' : isEdit ? 'Update' : 'Add User'}
        </Button>
      </DialogActions>
    </Dialog>
  );

  const GroupCard = ({ groupName, users }) => {
    const config = roleConfig[groupName] || { color: 'default', icon: 'ðŸ‘¤', label: groupName };
    return (
      <Card elevation={2} sx={{ borderLeft: `4px solid`, borderColor: `${config.color}.main`, mb: 2 }}>
        <CardContent sx={{ p: 0 }}>
          <Box
            sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', p: 2, cursor: 'pointer' }}
            onClick={() => toggleGroup(groupName)}
          >
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
              <Avatar sx={{ bgcolor: `${config.color}.main`, width: 48, height: 48 }}>
                <Typography variant="h6">{config.icon}</Typography>
              </Avatar>
              <Box>
                <Typography variant="h6" fontWeight="bold">
                  {config.label}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {users.length} {users.length === 1 ? 'user' : 'users'}
                </Typography>
              </Box>
            </Box>
            {expandedGroups[groupName] ? <ExpandLess /> : <ExpandMore />}
          </Box>
          <Collapse in={expandedGroups[groupName]}>
            <TableContainer component={Paper} variant="outlined">
              <Table size="small">
                <TableHead sx={{ bgcolor: 'grey.50' }}>
                  <TableRow>
                    <TableCell>Name</TableCell>
                    <TableCell>Email</TableCell>
                    <TableCell>ID</TableCell>
                    <TableCell>Department</TableCell>
                    <TableCell>Status</TableCell>
                    <TableCell>Actions</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {users.map((user) => (
                    <TableRow key={user.id} hover>
                      <TableCell>{user.name}</TableCell>
                      <TableCell>{user.email}</TableCell>
                      <TableCell>
                        <Chip label={user.studentId || user.facultyId || user.adminId || user.staffId || 'N/A'} size="small" />
                      </TableCell>
                      <TableCell>{user.department || 'N/A'}</TableCell>
                      <TableCell>
                        <Chip label={user.status} color={getStatusColor(user.status)} size="small" />
                      </TableCell>
                      <TableCell>
                        <IconButton onClick={() => handleEditUser(user)} disabled={actionLoading}><Edit /></IconButton>
                        <IconButton onClick={() => handleDeleteUser(user.id)} disabled={actionLoading}><Delete /></IconButton>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </Collapse>
        </CardContent>
      </Card>
    );
  };

  return (
    <Box sx={{ p: 3 }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 4, flexWrap: 'wrap', gap: 2 }}>
        <Typography variant="h4" fontWeight="bold" color="primary">
          ðŸ‘¥ User Management
        </Typography>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, flexWrap: 'wrap' }}>
          <Tabs value={viewMode} onChange={(e, newValue) => setViewMode(newValue)}>
            <Tab icon={<Dashboard />} label="Group View" value="groups" />
            <Tab icon={<ViewList />} label="Table View" value="table" />
          </Tabs>
          <Button variant="outlined" startIcon={<Refresh />} onClick={handleRefresh} disabled={loading || actionLoading}>
            Refresh
          </Button>
          <Button variant="contained" startIcon={<Add />} onClick={handleAddUser} disabled={actionLoading}>
            Add New User
          </Button>
        </Box>
      </Box>

      {loading && users.length === 0 && (
        <Box sx={{ display: 'flex', justifyContent: 'center', p: 8 }}>
          <CircularProgress size={60} />
        </Box>
      )}

      {!loading && users.length === 0 && (
        <Box sx={{ textAlign: 'center', p: 8 }}>
          <Typography>No users found</Typography>
          <Button variant="contained" startIcon={<Add />} onClick={handleAddUser}>
            Add First User
          </Button>
        </Box>
      )}

      {!loading && users.length > 0 && (
        viewMode === 'groups' ? (
          Object.entries(groupedUsers).map(([groupName, groupUsers]) => (
            <GroupCard key={groupName} groupName={groupName} users={groupUsers} />
          ))
        ) : (
          <Typography>Table view coming soon</Typography>
        )
      )}

      <UserFormDialog
        open={openDialog}
        onClose={() => setOpenDialog(false)}
        onSubmit={handleSubmit}
        title="Add New User"
        loading={actionLoading}
      />

      <UserFormDialog
        open={openEditDialog}
        onClose={() => {
          setOpenEditDialog(false);
          setSelectedUser(null);
        }}
        onSubmit={handleUpdate}
        title="Edit User"
        loading={actionLoading}
        isEdit
      />

      <Snackbar
        open={snackbar.open}
        autoHideDuration={6000}
        onClose={() => setSnackbar((prev) => ({ ...prev, open: false }))}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'right' }}
      >
        <Alert
          severity={snackbar.severity}
          onClose={() => setSnackbar((prev) => ({ ...prev, open: false }))}
          variant="filled"
          sx={{ width: '100%' }}
        >
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default UserManagementTab;
