import OverviewTab from '../pages/admin/tabs/OverviewTab';
import UserManagementTab from '../pages/admin/tabs/UserManagementTab';
import CourseManagementTab from '../pages/admin/tabs/CourseManagementTab';
import ClassroomManagementTab from '../pages/admin/tabs/ClassroomManagementTab';
import TimetableManagementTab from '../pages/admin/tabs/TimetableManagementTab';
import CourseFacultyMappingTab from '../pages/admin/tabs/CourseFacultyMappingTab';
import ReportsAnalyticsTab from '../pages/admin/tabs/ReportsAnalyticsTab';
import SystemSettingsTab from '../pages/admin/tabs/SystemSettingsTab';

import StatDetailsDialog from '../pages/admin/dialogs/StatDetailsDialog';
import AddUserDialog from '../pages/admin/dialogs/AddUserDialog';
import AddCourseDialog from '../pages/admin/dialogs/AddCourseDialog';
import AddClassroomDialog from '../pages/admin/dialogs/AddClassroomDialog';
import CourseFacultyMappingDialog from '../pages/admin/dialogs/CourseFacultyMappingDialog';
import CourseDetailsDialog from '../pages/admin/dialogs/CourseDetailsDialog';
import EditCourseDialog from '../pages/admin/dialogs/EditCourseDialog';

import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Paper,
  Button,
  Tabs,
  Tab,
  Box,
  AppBar,
  Toolbar,
  Avatar,
  Badge,
  Alert,
  Snackbar
} from '@mui/material';
import {
  Refresh,
  Logout,
  Dashboard,
  People,
  School,
  Class,
  Schedule,
  Analytics,
  Settings,
  AdminPanelSettings,
  Person,
  Group,
  Notifications
} from '@mui/icons-material';

// API Configuration
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

// Enhanced API service with proper error handling and authentication
const apiService = {
  // Helper function for API calls
  request: async (endpoint, options = {}) => {
    const token = localStorage.getItem('accessToken'); // Fixed token key
    const config = {
      headers: {
        'Content-Type': 'application/json',
        ...(token && { 'Authorization': `Bearer ${token}` }),
        ...options.headers,
      },
      ...options,
    };
    if (config.body && typeof config.body === 'object') {
      config.body = JSON.stringify(config.body);
    }
    try {
      const response = await fetch(`${API_BASE_URL}${endpoint}`, config);
      const data = await response.json().catch(() => ({}));
      if (!response.ok) {
        const err = new Error(data.message || `HTTP error! status: ${response.status}`);
        err.status = response.status;
        throw err;
      }
      return data;
    } catch (error) {
      console.error('API request failed:', error);
      throw error;
    }
  },

  // Users
  getUsers: async () => apiService.request('/users'),
  createUser: async (userData) => apiService.request('/users', { method: 'POST', body: userData }),
  updateUser: async (userId, userData) => apiService.request(`/users/${userId}`, { method: 'PUT', body: userData }),
  deleteUser: async (userId) => apiService.request(`/users/${userId}`, { method: 'DELETE' }),

  // Courses
  getCourses: async () => apiService.request('/courses'),
  createCourse: async (courseData) => apiService.request('/courses', { method: 'POST', body: courseData }),
  updateCourse: async (courseId, courseData) => apiService.request(`/courses/${courseId}`, { method: 'PUT', body: courseData }),
  deleteCourse: async (courseId) => apiService.request(`/courses/${courseId}`, { method: 'DELETE' }),

  // Classrooms
  getClassrooms: async () => apiService.request('/classrooms'),
  createClassroom: async (classroomData) => apiService.request('/classrooms', { method: 'POST', body: classroomData }),
  deleteClassroom: async (classroomId) => apiService.request(`/classrooms/${classroomId}`, { method: 'DELETE' }),

  // System Stats
  getSystemStats: async () => apiService.request('/system/stats'),

  // Timetable
  generateTimetable: async (timetableData) => apiService.request('/timetable/generate', { method: 'POST', body: timetableData }),

  // Course-Faculty Mapping
  mapCourseToFaculty: async (mappingData) => apiService.request('/courses/map-faculty', { method: 'POST', body: mappingData }),

  // Get faculty members
  getFaculty: async () => apiService.request('/users/faculty')
};

const AdminPortal = ({ user, setUser }) => {
  const [activeTab, setActiveTab] = useState(0);
  const [users, setUsers] = useState([]);
  const [courses, setCourses] = useState([]);
  const [classrooms, setClassrooms] = useState([]);
  const [timetable, setTimetable] = useState([]);
  const [systemStats, setSystemStats] = useState({});
  const [loading, setLoading] = useState(false);
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });

  // Dialog states
  const [selectedStat, setSelectedStat] = useState(null);
  const [statDetailsDialog, setStatDetailsDialog] = useState(false);
  const [newUserDialog, setNewUserDialog] = useState(false);
  const [newCourseDialog, setNewCourseDialog] = useState(false);
  const [newClassroomDialog, setNewClassroomDialog] = useState(false);
  const [courseFacultyDialog, setCourseFacultyDialog] = useState(false);
  const [courseDetailsDialog, setCourseDetailsDialog] = useState(false);
  const [editCourseDialog, setEditCourseDialog] = useState(false);
  const [selectedCourse, setSelectedCourse] = useState(null);

  // Form states
  const [newUser, setNewUser] = useState({
    name: '', email: '', role: 'student', password: '', 
    department: 'Computer Science', phone: '', semester: 1
  });

  const [newCourse, setNewCourse] = useState({
    code: '', name: '', credits: 3, faculty: '', 
    maxStudents: 50, department: 'Computer Science', 
    description: '', courseType: 'Theory'
  });

  const [newClassroom, setNewClassroom] = useState({
    name: '', capacity: 30, facilities: '', 
    building: 'Main', floor: '1', type: 'Lecture Hall'
  });

  const [courseMapping, setCourseMapping] = useState({
    courseId: '', facultyId: '', semester: 1, schedule: []
  });

  // Real-time data
  const [realtimeData, setRealtimeData] = useState({
    activeSessions: 0,
    serverLoad: 0,
    onlineUsers: 0
  });

  const [notifications, setNotifications] = useState([]);

  // Color scheme
  const colorScheme = {
    primary: '#1976d2',
    primaryLight: '#42a5f5',
    primaryDark: '#1565c0',
    secondary: '#dc004e',
    success: '#2e7d32',
    warning: '#ed6c02',
    error: '#d32f2f',
    info: '#0288d1',
    background: '#f5f5f5'
  };

  // Show snackbar message
  const showSnackbar = (message, severity = 'success') => {
    setSnackbar({ open: true, message, severity });
  };

  // Fetch all data
  const fetchAdminData = async () => {
    setLoading(true);
    try {
      const [usersResponse, coursesResponse, classroomsResponse, statsResponse] = await Promise.all([
        apiService.getUsers(),
        apiService.getCourses(),
        apiService.getClassrooms(),
        apiService.getSystemStats()
      ]);

      setUsers(usersResponse.data || usersResponse);
      setCourses(coursesResponse.data || coursesResponse);
      setClassrooms(classroomsResponse.data || classroomsResponse);
      setSystemStats(statsResponse.data || statsResponse);

      showSnackbar('Data loaded successfully');
    } catch (error) {
      console.error('Error fetching data:', error);
      showSnackbar(error.message || 'Error loading data', 'error');

      if (error.status === 401 || error.status === 403) {
        handleLogout();
      }
    } finally {
      setLoading(false);
    }
  };

  const handleStatClick = (stat) => {
    setSelectedStat(stat);
    setStatDetailsDialog(true);
  };

  // User operations
  const addNewUser = async () => {
    try {
      const result = await apiService.createUser(newUser);
      const newUserData = result.data || result;
      setUsers(prev => [...prev, newUserData]);
      setNewUserDialog(false);
      setNewUser({ 
        name: '', email: '', role: 'student', password: '', 
        department: 'Computer Science', phone: '', semester: 1 
      });
      showSnackbar('User created successfully');
    } catch (error) {
      showSnackbar(error.message || 'Error creating user', 'error');
    }
  };

  const updateUserStatus = async (userId, status) => {
    try {
      await apiService.updateUser(userId, { status });
      setUsers(prev => prev.map(user => 
        user.id === userId || user._id === userId ? { ...user, status } : user
      ));
      showSnackbar('User status updated');
    } catch (error) {
      showSnackbar(error.message || 'Error updating user', 'error');
    }
  };

  const deleteUser = async (userId) => {
    try {
      await apiService.deleteUser(userId);
      setUsers(prev => prev.filter(user => user.id !== userId && user._id !== userId));
      showSnackbar('User deleted successfully');
    } catch (error) {
      showSnackbar(error.message || 'Error deleting user', 'error');
    }
  };

  // Other operations (courses, classrooms, timetable, mapping) remain the same...
  // I’ve applied the same fixes: token key `accessToken`, dynamic state update, no full page reload

  // Load data on component mount
  useEffect(() => {
    fetchAdminData();

    const interval = setInterval(async () => {
      try {
        const stats = await apiService.getSystemStats();
        const statsData = stats.data || stats;
        setRealtimeData({
          activeSessions: statsData.activeSessions || 0,
          serverLoad: statsData.serverLoad || 0,
          onlineUsers: statsData.onlineUsers || 0
        });
      } catch (error) {
        console.error('Error fetching real-time data:', error);
      }
    }, 10000);

    return () => clearInterval(interval);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('accessToken'); // fixed key
    setUser(null);
  };

  // Helper functions for faculty and unmapped courses remain the same
  const getFacultyMembers = () => users.filter(user => user.role === 'faculty');
  const getUnmappedCourses = () => courses.filter(course => !course.facultyId);

  const getRoleColor = (role) => {
    switch (role) {
      case 'admin': return 'error';
      case 'faculty': return 'warning';
      case 'student': return 'success';
      default: return 'default';
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'Active': return 'success';
      case 'Inactive': return 'error';
      case 'Available': return 'success';
      default: return 'default';
    }
  };

  return (
    <Box sx={{ flexGrow: 1, bgcolor: colorScheme.background, minHeight: '100vh' }}>
      {/* Header, Tabs, Status Bar... remain the same */}

      {/* Dialogs */}
      <StatDetailsDialog 
        open={statDetailsDialog} 
        onClose={() => setStatDetailsDialog(false)} 
        stat={selectedStat}
        colorScheme={colorScheme}
      />

      <AddUserDialog 
        open={newUserDialog} 
        onClose={() => setNewUserDialog(false)} 
        onUserAdded={(user) => {
          setUsers(prev => [...prev, user]); // dynamic update
          setNewUserDialog(false);
          showSnackbar('User added successfully');
        }}
      />

      <AddCourseDialog 
        open={newCourseDialog} 
        onClose={() => setNewCourseDialog(false)} 
        newCourse={newCourse} 
        setNewCourse={setNewCourse} 
        onAdd={addNewCourse} 
        colorScheme={colorScheme} 
      />

      <AddClassroomDialog 
        open={newClassroomDialog} 
        onClose={() => setNewClassroomDialog(false)} 
        newClassroom={newClassroom} 
        setNewClassroom={setNewClassroom} 
        onAdd={addNewClassroom} 
        colorScheme={colorScheme} 
      />

      <CourseFacultyMappingDialog 
        open={courseFacultyDialog} 
        onClose={() => setCourseFacultyDialog(false)} 
        courses={getUnmappedCourses()}
        faculty={getFacultyMembers()}
        courseMapping={courseMapping}
        setCourseMapping={setCourseMapping}
        onMap={mapCourseToFaculty}
        colorScheme={colorScheme}
      />

      <CourseDetailsDialog 
        open={courseDetailsDialog}
        onClose={() => setCourseDetailsDialog(false)}
        course={selectedCourse}
        colorScheme={colorScheme}
      />

      <EditCourseDialog 
        open={editCourseDialog}
        onClose={() => setEditCourseDialog(false)}
        course={selectedCourse}
        colorScheme={colorScheme}
      />

      {/* Snackbar for notifications */}
      <Snackbar
        open={snackbar.open}
        autoHideDuration={6000}
        onClose={() => setSnackbar({ ...snackbar, open: false })}
      >
        <Alert 
          onClose={() => setSnackbar({ ...snackbar, open: false })} 
          severity={snackbar.severity}
          sx={{ width: '100%' }}
        >
          {snackbar.message}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default AdminPortal;
