import React, { useState, useEffect, useRef } from 'react';
import {
  Container,
  Typography,
  Paper,
  Grid,
  Card,
  CardContent,
  Button,
  Tabs,
  Tab,
  Box,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  FormControl,
  Select,
  MenuItem,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  LinearProgress,
  Alert,
  Snackbar,
  AppBar,
  Toolbar,
  IconButton,
  TextField,
  Avatar,
  Divider,
  InputLabel,
  OutlinedInput,
  InputAdornment,
  CircularProgress
} from '@mui/material';
import { 
  QrCode2, 
  Face, 
  Groups, 
  Schedule, 
  Analytics, 
  Refresh,
  Download,
  Print,
  CameraAlt,
  Stop,
  PlayArrow,
  CheckCircle,
  Logout,
  AccountCircle,
  Edit,
  Save,
  Cancel,
  Email,
  Phone,
  LocationOn,
  Person
} from '@mui/icons-material';
import { QRCodeSVG } from 'qrcode.react';

// Enhanced API Service with better error handling
const apiService = {
  baseUrl: process.env.REACT_APP_API_URL || 'http://localhost:5000/api',

  async request(endpoint, options = {}) {
    const token = localStorage.getItem('token');
    const config = {
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      },
      ...options,
    };

    if (config.body && typeof config.body === 'object') {
      config.body = JSON.stringify(config.body);
    }

    try {
      const response = await fetch(`${this.baseUrl}${endpoint}`, config);

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      return data;
    } catch (error) {
      console.error('API request failed:', error);
      throw new Error(`Network error: ${error.message}`);
    }
  },

  // Faculty endpoints
  async getFacultyProfile() {
    return this.request('/faculty/profile');
  },

  async updateFacultyProfile(profileData) {
    return this.request('/faculty/profile', {
      method: 'PUT',
      body: profileData,
    });
  },

  // Course endpoints - FIXED: Proper course mapping
  async getFacultyCourses() {
    try {
      const response = await this.request('/faculty/courses');
      console.log('Faculty courses response:', response);
      return response.courses || response.data || response;
    } catch (error) {
      console.error('Error fetching faculty courses:', error);
      throw error;
    }
  },

  async getCourseStudents(courseId) {
    return this.request(`/courses/${courseId}/students`);
  },

  // Attendance endpoints
  async startAttendanceSession(sessionData) {
    return this.request('/attendance/sessions', {
      method: 'POST',
      body: sessionData,
    });
  },

  async markAttendance(attendanceData) {
    return this.request('/attendance/mark', {
      method: 'POST',
      body: attendanceData,
    });
  },

  async getAttendanceReports(courseId, date) {
    const params = new URLSearchParams();
    if (courseId) params.append('courseId', courseId);
    if (date) params.append('date', date);

    return this.request(`/attendance/reports?${params}`);
  },

  // Schedule endpoints
  async getFacultySchedule() {
    return this.request('/faculty/schedule');
  },

  // Face recognition endpoints
  async recognizeFace(imageData) {
    return this.request('/face/recognize', {
      method: 'POST',
      body: { image: imageData },
    });
  },
};

// Real Face Recognition Component (same as before)
const RealFaceRecognition = ({ onAttendanceMarked, isActive, setSnackbar, course }) => {
  const [isScanning, setIsScanning] = useState(false);
  const [detectedFaces, setDetectedFaces] = useState([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const videoRef = useRef(null);
  const streamRef = useRef(null);
  const canvasRef = useRef(null);
  const detectionIntervalRef = useRef(null);

  const startFaceRecognition = async () => {
    try {
      setIsScanning(true);
      setDetectedFaces([]);

      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { 
          facingMode: 'user',
          width: { ideal: 640 },
          height: { ideal: 480 }
        } 
      });

      streamRef.current = stream;

      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        await videoRef.current.play();
      }

      // Start face detection interval
      detectionIntervalRef.current = setInterval(captureAndDetectFace, 3000);

    } catch (error) {
      console.error('Error accessing camera:', error);
      setSnackbar({
        open: true,
        message: 'Cannot access camera. Please check permissions.',
        severity: 'error'
      });
      setIsScanning(false);
    }
  };

  const captureAndDetectFace = async () => {
    if (!isScanning || isProcessing) return;

    try {
      setIsProcessing(true);

      const canvas = canvasRef.current;
      const video = videoRef.current;

      if (canvas && video) {
        const context = canvas.getContext('2d');
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;
        context.drawImage(video, 0, 0, canvas.width, canvas.height);

        const imageData = canvas.toDataURL('image/jpeg');

        // Simulate face recognition - replace with actual API call
        // const result = await apiService.recognizeFace(imageData);

        // For demo - simulate detection after 2 seconds
        setTimeout(() => {
          const sampleStudents = [
            { student_id: 'S001', name: 'Alice Johnson' },
            { student_id: 'S002', name: 'Bob Smith' },
            { student_id: 'S003', name: 'Carol Davis' },
          ];

          if (detectedFaces.length < sampleStudents.length) {
            const student = sampleStudents[detectedFaces.length];
            setDetectedFaces(prev => [...prev, student]);
            onAttendanceMarked(student.student_id, 'Present', 'Face Recognition', student.name);
          }
          setIsProcessing(false);
        }, 2000);

      }
    } catch (error) {
      console.error('Face recognition error:', error);
      setIsProcessing(false);
    }
  };

  const stopFaceRecognition = () => {
    setIsScanning(false);
    setIsProcessing(false);

    if (detectionIntervalRef.current) {
      clearInterval(detectionIntervalRef.current);
      detectionIntervalRef.current = null;
    }

    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }

    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
  };

  useEffect(() => {
    return () => {
      stopFaceRecognition();
    };
  }, []);

  return (
    <Paper sx={{ p: 3, border: '1px solid #1976d2' }}>
      <Typography variant="h6" gutterBottom color="primary">
        <CameraAlt sx={{ mr: 1 }} />
        Real-time Face Recognition
      </Typography>

      <Box sx={{ 
        width: '100%', 
        height: 300,
        border: '2px solid #1976d2',
        borderRadius: 2,
        overflow: 'hidden',
        mx: 'auto',
        my: 2,
        backgroundColor: '#000',
        position: 'relative'
      }}>
        {isScanning ? (
          <video
            ref={videoRef}
            style={{
              width: '100%',
              height: '100%',
              objectFit: 'cover'
            }}
            playsInline
            muted
          />
        ) : (
          <Box sx={{ 
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'center', 
            height: '100%',
            color: 'white'
          }}>
            <Typography variant="body1" align="center">
              Camera feed will appear here
              <br />
              when recognition starts
            </Typography>
          </Box>
        )}

        {isScanning && (
          <Box sx={{
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            background: 'rgba(0,0,0,0.3)'
          }}>
            <Typography variant="h6" color="white">
              {isProcessing ? 'ðŸ”„ Processing...' : 'ðŸ” Scanning for faces...'}
            </Typography>
          </Box>
        )}
      </Box>

      <canvas ref={canvasRef} style={{ display: 'none' }} />

      <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center', mb: 3 }}>
        <Button
          variant="contained"
          startIcon={<PlayArrow />}
          onClick={startFaceRecognition}
          disabled={isScanning}
          color="primary"
        >
          Start Recognition
        </Button>
        <Button
          variant="outlined"
          startIcon={<Stop />}
          onClick={stopFaceRecognition}
          disabled={!isScanning}
          color="primary"
        >
          Stop Recognition
        </Button>
      </Box>

      <Alert severity="info" sx={{ mb: 2, bgcolor: '#e3f2fd' }}>
        Make sure students are facing the camera clearly. The system will automatically detect and mark attendance.
      </Alert>

      {detectedFaces.length > 0 && (
        <Box>
          <Typography variant="subtitle1" gutterBottom color="primary">
            âœ… Recognized Students ({detectedFaces.length})
          </Typography>
          <List dense>
            {detectedFaces.map((student, index) => (
              <ListItem key={`${student.student_id}-${index}`}>
                <ListItemIcon>
                  <Avatar sx={{ width: 32, height: 32, bgcolor: 'primary.main' }}>
                    <Person />
                  </Avatar>
                </ListItemIcon>
                <ListItemText 
                  primary={student.name}
                  secondary={`ID: ${student.student_id} - Marked Present`}
                />
                <CheckCircle color="primary" />
              </ListItem>
            ))}
          </List>
        </Box>
      )}
    </Paper>
  );
};

// QR Attendance Component (same as before)
const QRAttendanceComponent = ({ 
  qrCodeHash, 
  onRefresh, 
  onSimulateScan, 
  scannedCount, 
  isActive,
  course,
  sessionTimeLeft 
}) => {
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const qrValue = qrCodeHash ? JSON.stringify({
    type: 'attendance',
    courseCode: course?.course_code,
    courseName: course?.course_name,
    sessionId: qrCodeHash,
    timestamp: new Date().toISOString(),
    institution: 'Smart Classroom System'
  }) : '';

  return (
    <Paper sx={{ p: 3, textAlign: 'center', border: '1px solid #1976d2' }}>
      <Typography variant="h6" gutterBottom color="primary">
        <QrCode2 sx={{ mr: 1 }} />
        QR Code Attendance
      </Typography>

      <Box sx={{ 
        width: 256, 
        height: 256, 
        mx: 'auto', 
        my: 3,
        border: '2px solid #1976d2',
        borderRadius: 2,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#ffffff',
        overflow: 'hidden',
        p: 2
      }}>
        {qrCodeHash ? (
          <QRCodeSVG 
            value={qrValue}
            size={200}
            level="H"
            includeMargin={true}
            bgColor="#FFFFFF"
            fgColor="#1976d2"
          />
        ) : (
          <Typography variant="body2" color="textSecondary">
            Generate QR Code to start
          </Typography>
        )}
      </Box>

      <Box sx={{ mb: 3 }}>
        <Typography variant="body2" color="textSecondary" gutterBottom>
          Students should scan this QR code with their mobile devices
        </Typography>
        <Typography variant="caption" color="textSecondary">
          Session: {isActive ? 'Active' : 'Inactive'} â€¢ Scanned: {scannedCount}
        </Typography>
        {isActive && sessionTimeLeft && (
          <Typography variant="caption" color="primary" display="block" sx={{ mt: 1, fontWeight: 'bold' }}>
            â° Expires in: {formatTime(sessionTimeLeft)}
          </Typography>
        )}
        {qrCodeHash && (
          <Typography variant="caption" color="textSecondary" display="block" sx={{ mt: 1 }}>
            Course: {course?.course_code} â€¢ Session ID: {qrCodeHash.substring(0, 8)}...
          </Typography>
        )}
      </Box>

      <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center', flexWrap: 'wrap' }}>
        <Button
          variant="contained"
          startIcon={<Refresh />}
          onClick={onRefresh}
          disabled={!isActive}
          color="primary"
        >
          Refresh QR
        </Button>
        <Button
          variant="outlined"
          onClick={onSimulateScan}
          disabled={!isActive}
          color="primary"
        >
          Simulate Student Scan
        </Button>
      </Box>

      <Alert severity="info" sx={{ mt: 2, bgcolor: '#e3f2fd' }}>
        QR Code will expire in 5 minutes. Students must scan during active session.
      </Alert>

      {isActive && (
        <Box sx={{ mt: 2, p: 2, backgroundColor: '#e3f2fd', borderRadius: 1, border: '1px solid #1976d2' }}>
          <Typography variant="body2" color="primary" sx={{ fontWeight: 'bold' }}>
            âœ… QR Session Active - Students can scan now
          </Typography>
        </Box>
      )}
    </Paper>
  );
};

