const FacultyPortal = ({ user, setUser }) => {
  const [activeTab, setActiveTab] = useState(0);
  const [courses, setCourses] = useState([]);
  const [attendanceDialog, setAttendanceDialog] = useState(false);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [qrCodeHash, setQrCodeHash] = useState('');
  const [attendanceMethod, setAttendanceMethod] = useState('qr');
  const [attendanceRecords, setAttendanceRecords] = useState({});
  const [manualAttendance, setManualAttendance] = useState({});
  const [scheduleData, setScheduleData] = useState([]);
  const [reportsData, setReportsData] = useState({});
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });
  const [qrSessionActive, setQrSessionActive] = useState(false);
  const [scannedCount, setScannedCount] = useState(0);
  const [qrSessionTimer, setQrSessionTimer] = useState(300);
  const [facultyProfile, setFacultyProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [coursesLoading, setCoursesLoading] = useState(false);

  // Logout function
  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    window.location.href = '/login';
  };

  // Enhanced data fetching with better error handling
  const fetchFacultyData = async () => {
    try {
      setLoading(true);

      // Fetch profile
      const profileData = await apiService.getFacultyProfile();
      setFacultyProfile(profileData);

      // Fetch courses with retry logic
      await fetchFacultyCourses();

      // Fetch schedule
      try {
        const scheduleData = await apiService.getFacultySchedule();
        setScheduleData(scheduleData.schedule || scheduleData || []);
      } catch (scheduleError) {
        console.warn('Could not fetch schedule:', scheduleError);
        setScheduleData([]);
      }

      // Initialize empty reports data
      setReportsData({
        overallAttendance: 0,
        presentToday: 0,
        lateToday: 0,
        absentToday: 0
      });

    } catch (error) {
      console.error('Failed to fetch faculty data:', error);
      setSnackbar({
        open: true,
        message: 'Failed to load data. Please refresh the page.',
        severity: 'error'
      });
    } finally {
      setLoading(false);
    }
  };

  // Separate function for fetching courses with better error handling
  const fetchFacultyCourses = async () => {
    try {
      setCoursesLoading(true);
      console.log('Fetching faculty courses...');

      const coursesData = await apiService.getFacultyCourses();
      console.log('Raw courses data:', coursesData);

      // Handle different response formats
      let coursesArray = [];

      if (Array.isArray(coursesData)) {
        coursesArray = coursesData;
      } else if (coursesData && Array.isArray(coursesData.courses)) {
        coursesArray = coursesData.courses;
      } else if (coursesData && Array.isArray(coursesData.data)) {
        coursesArray = coursesData.data;
      } else if (coursesData && coursesData.courses) {
        // Handle object with courses property
        coursesArray = [coursesData.courses].flat();
      } else {
        console.warn('Unexpected courses data format:', coursesData);
        coursesArray = [];
      }

      console.log('Processed courses array:', coursesArray);

      // Validate and set courses
      if (coursesArray.length > 0) {
        setCourses(coursesArray);

        // Initialize attendance records for each course
        const initialRecords = {};
        for (const course of coursesArray) {
          try {
            const students = await apiService.getCourseStudents(course.id || course._id);
            initialRecords[course.id || course._id] = students.map(student => ({
              id: student.student_id || student.id,
              student_name: student.name,
              student_id: student.student_id,
              status: 'Absent',
              time: '-',
              method: '-'
            }));
          } catch (studentError) {
            console.warn(`Could not fetch students for course ${course.courseCode}:`, studentError);
            initialRecords[course.id || course._id] = [];
          }
        }
        setAttendanceRecords(initialRecords);
      } else {
        setCourses([]);
        setSnackbar({
          open: true,
          message: 'No courses assigned. Please contact administrator.',
          severity: 'info'
        });
      }

    } catch (error) {
      console.error('Error fetching faculty courses:', error);
      setSnackbar({
        open: true,
        message: 'Failed to load courses. Please try again.',
        severity: 'error'
      });
      setCourses([]);
    } finally {
      setCoursesLoading(false);
    }
  };

  useEffect(() => {
    fetchFacultyData();
  }, []);

  useEffect(() => {
    let timer;
    if (qrSessionActive && qrSessionTimer > 0) {
      timer = setInterval(() => {
        setQrSessionTimer(prev => prev - 1);
      }, 1000);
    } else if (qrSessionTimer === 0 && qrSessionActive) {
      setQrSessionActive(false);
      setSnackbar({
        open: true,
        message: 'QR Code session expired. Please generate a new one.',
        severity: 'warning'
      });
    }
    return () => clearInterval(timer);
  }, [qrSessionActive, qrSessionTimer]);

  const generateQRCode = async (course) => {
    try {
      const sessionData = {
        courseId: course.id || course._id,
        courseCode: course.courseCode || course.course_code,
        courseName: course.courseName || course.course_name,
        duration: 300,
        method: 'qr'
      };

      const result = await apiService.startAttendanceSession(sessionData);
      const hash = result.sessionId || `QR_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

      setQrCodeHash(hash);
      setQrSessionActive(true);
      setQrSessionTimer(300);
      setScannedCount(0);

      setSnackbar({
        open: true,
        message: 'QR Code generated successfully! Students can now scan it.',
        severity: 'success'
      });

      return hash;
    } catch (error) {
      setSnackbar({
        open: true,
        message: 'Failed to generate QR code. Please try again.',
        severity: 'error'
      });
    }
  };

  const refreshQRCode = () => {
    if (selectedCourse) {
      generateQRCode(selectedCourse);
      setSnackbar({
        open: true,
        message: 'New QR Code generated!',
        severity: 'info'
      });
    }
  };

  const startAttendanceSession = async (course, method = 'qr') => {
    setSelectedCourse(course);
    setAttendanceMethod(method);

    if (method === 'qr') {
      await generateQRCode(course);
    }

    setAttendanceDialog(true);
  };

  const markAttendance = async (studentId, status = 'Present', method = 'Manual', studentName = '') => {
    if (!selectedCourse) return;

    try {
      const attendanceData = {
        studentId,
        courseId: selectedCourse.id || selectedCourse._id,
        status,
        method,
        timestamp: new Date().toISOString()
      };

      await apiService.markAttendance(attendanceData);

      const time = new Date().toLocaleTimeString('en-US', { 
        hour12: true, 
        hour: 'numeric', 
        minute: '2-digit' 
      });

      const courseId = selectedCourse.id || selectedCourse._id;

      setAttendanceRecords(prev => ({
        ...prev,
        [courseId]: prev[courseId]?.map(record =>
          record.student_id === studentId
            ? { ...record, status, time, method }
            : record
        ) || []
      }));

      if (method === 'QR Code') {
        setScannedCount(prev => prev + 1);
      }

      setSnackbar({
        open: true,
        message: `Attendance marked for ${studentName || studentId}`,
        severity: 'success'
      });
    } catch (error) {
      setSnackbar({
        open: true,
        message: 'Failed to mark attendance. Please try again.',
        severity: 'error'
      });
    }
  };

  const markManualAttendance = (studentId, status) => {
    const courseId = selectedCourse.id || selectedCourse._id;
    const student = attendanceRecords[courseId]?.find(s => s.student_id === studentId);
    setManualAttendance(prev => ({
      ...prev,
      [studentId]: status
    }));
    markAttendance(studentId, status, 'Manual', student?.student_name);
  };

  const submitManualAttendance = async () => {
    try {
      for (const [studentId, status] of Object.entries(manualAttendance)) {
        if (status !== 'Absent') {
          const courseId = selectedCourse.id || selectedCourse._id;
          const student = attendanceRecords[courseId]?.find(s => s.student_id === studentId);
          await markAttendance(studentId, status, 'Manual', student?.student_name);
        }
      }

      setSnackbar({
        open: true,
        message: 'Manual attendance submitted successfully!',
        severity: 'success'
      });
      setAttendanceDialog(false);
      setManualAttendance({});
    } catch (error) {
      setSnackbar({
        open: true,
        message: 'Failed to submit attendance. Please try again.',
        severity: 'error'
      });
    }
  };

  const simulateQRScan = () => {
    if (!selectedCourse || !qrSessionActive) return;

    const courseId = selectedCourse.id || selectedCourse._id;
    const students = attendanceRecords[courseId] || [];
    const absentStudents = students.filter(s => s.status === 'Absent');

    if (absentStudents.length > 0) {
      const randomStudent = absentStudents[Math.floor(Math.random() * absentStudents.length)];
      markAttendance(randomStudent.student_id, 'Present', 'QR Code', randomStudent.student_name);
    } else {
      setSnackbar({
        open: true,
        message: 'All students have already been marked present!',
        severity: 'info'
      });
    }
  };

  const closeAttendanceSession = () => {
    setAttendanceDialog(false);
    setQrSessionActive(false);
    setSelectedCourse(null);
    setScannedCount(0);
    setQrSessionTimer(300);
    setManualAttendance({});
    setSnackbar({
      open: true,
      message: 'Attendance session closed',
      severity: 'info'
    });
  };

  const downloadAttendanceReport = async (courseId) => {
    try {
      const course = courses.find(c => c.id === courseId || c._id === courseId);
      const reports = await apiService.getAttendanceReports(courseId);

      const csvContent = [
        ['Student ID', 'Student Name', 'Status', 'Time', 'Method', 'Date'],
        ...(reports.records || []).map(record => [
          record.student_id,
          record.student_name,
          record.status,
          record.time,
          record.method,
          new Date(record.timestamp).toLocaleDateString()
        ])
      ].map(row => row.join(',')).join('\n');

      const blob = new Blob([csvContent], { type: 'text/csv' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `${course.courseCode || course.course_code}_attendance_${new Date().toISOString().split('T')[0]}.csv`;
      a.click();
      URL.revokeObjectURL(url);

      setSnackbar({
        open: true,
        message: 'Attendance report downloaded successfully!',
        severity: 'success'
      });
    } catch (error) {
      setSnackbar({
        open: true,
        message: 'Failed to download report. Please try again.',
        severity: 'error'
      });
    }
  };

  const updateFacultyProfile = async (profileData) => {
    const updatedProfile = await apiService.updateFacultyProfile(profileData);
    setFacultyProfile(updatedProfile);
    return updatedProfile;
  };

  // Refresh courses specifically
  const handleRefreshCourses = async () => {
    await fetchFacultyCourses();
    setSnackbar({
      open: true,
      message: 'Courses refreshed successfully!',
      severity: 'success'
    });
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', flexDirection: 'column' }}>
        <CircularProgress size={60} />
        <Typography variant="h6" sx={{ mt: 2 }}>
          Loading Faculty Portal...
        </Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ flexGrow: 1 }}>
      <AppBar position="static" sx={{ bgcolor: '#1976d2', mb: 3 }}>
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            ðŸ‘¨â€ðŸ« Faculty Portal - Smart Classroom
          </Typography>
          <Typography variant="body2" sx={{ mr: 2 }}>
            Welcome, Dr. {facultyProfile?.name}
          </Typography>
          <IconButton color="inherit">
            <AccountCircle />
          </IconButton>
          <Button 
            color="inherit" 
            startIcon={<Logout />}
            onClick={handleLogout}
            sx={{ ml: 2 }}
          >
            Logout
          </Button>
        </Toolbar>
      </AppBar>

      <Container maxWidth="lg" sx={{ mb: 4 }}>
        <Paper sx={{ width: '100%', mb: 3, border: '1px solid #1976d2' }}>
          <Tabs 
            value={activeTab} 
            onChange={(e, newValue) => setActiveTab(newValue)} 
            variant="scrollable"
            textColor="primary"
            indicatorColor="primary"
          >
            <Tab icon={<Schedule />} label="My Schedule" />
            <Tab icon={<Groups />} label="My Courses" />
            <Tab icon={<QrCode2 />} label="Take Attendance" />
            <Tab icon={<Analytics />} label="Attendance Reports" />
            <Tab icon={<Face />} label="Manual Attendance" />
            <Tab icon={<AccountCircle />} label="My Profile" />
          </Tabs>
        </Paper>

        {/* My Schedule Tab */}
        {activeTab === 0 && (
          <Box>
            <Typography variant="h5" gutterBottom sx={{ mb: 3, color: 'primary.main' }}>
              My Weekly Schedule
            </Typography>
            {scheduleData.length > 0 ? (
              <Paper sx={{ p: 3, border: '1px solid #1976d2' }}>
                <TableContainer>
                  <Table>
                    <TableHead sx={{ bgcolor: '#1976d2' }}>
                      <TableRow>
                        <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>Day</TableCell>
                        <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>Course Code</TableCell>
                        <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>Time</TableCell>
                        <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>Room</TableCell>
                        <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>Type</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {scheduleData.map((schedule, index) => (
                        <TableRow key={schedule.id || index} hover>
                          <TableCell>
                            <Typography variant="body1" fontWeight="bold">
                              {schedule.day}
                            </Typography>
                          </TableCell>
                          <TableCell>
                            <Chip 
                              label={schedule.courseCode || schedule.course_code} 
                              color="primary" 
                              variant="outlined"
                              size="small"
                            />
                          </TableCell>
                          <TableCell>
                            <Typography variant="body2">
                              {schedule.time}
                            </Typography>
                          </TableCell>
                          <TableCell>
                            <Typography variant="body2">
                              {schedule.room}
                            </Typography>
                          </TableCell>
                          <TableCell>
                            <Chip 
                              label={schedule.type}
                              color={
                                schedule.type === 'Lecture' ? 'primary' :
                                schedule.type === 'Lab' ? 'secondary' : 'default'
                              }
                              size="small"
                            />
                          </TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                </TableContainer>
              </Paper>
            ) : (
              <Paper sx={{ p: 4, textAlign: 'center', border: '1px solid #1976d2' }}>
                <Typography variant="h6" color="textSecondary">
                  No schedule available
                </Typography>
                <Typography variant="body2" color="textSecondary" sx={{ mt: 1 }}>
                  Your schedule will appear here once assigned by the administrator.
                </Typography>
              </Paper>
            )}
          </Box>
        )}

        {/* My Courses Tab - FIXED COURSE MAPPING */}
        {activeTab === 1 && (
          <Box>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
              <Typography variant="h5" color="primary.main">My Courses</Typography>
              <Button 
                startIcon={<Refresh />} 
                variant="outlined" 
                color="primary" 
                onClick={handleRefreshCourses}
                disabled={coursesLoading}
              >
                {coursesLoading ? <CircularProgress size={20} /> : 'Refresh Courses'}
              </Button>
            </Box>

            {coursesLoading ? (
              <Box sx={{ display: 'flex', justifyContent: 'center', p: 4 }}>
                <CircularProgress />
              </Box>
            ) : courses.length > 0 ? (
              <Grid container spacing={3}>
                {courses.map((course, index) => (
                  <Grid item xs={12} md={6} key={course.id || course._id || index}>
                    <CourseCard 
                      course={course} 
                      onStartAttendance={startAttendanceSession}
                    />
                  </Grid>
                ))}
              </Grid>
            ) : (
              <Paper sx={{ p: 4, textAlign: 'center', border: '1px solid #1976d2' }}>
                <Groups sx={{ fontSize: 60, color: 'text.secondary', mb: 2 }} />
                <Typography variant="h6" color="textSecondary">
                  No Courses Assigned
                </Typography>
                <Typography variant="body2" color="textSecondary" sx={{ mt: 1, mb: 3 }}>
                  You haven't been assigned any courses yet. Please contact the administrator.
                </Typography>
                <Button 
                  variant="contained" 
                  startIcon={<Refresh />}
                  onClick={handleRefreshCourses}
                >
                  Check Again
                </Button>
              </Paper>
            )}
          </Box>
        )}

        {/* Take Attendance Tab */}
        {activeTab === 2 && (
          <Box>
            <Typography variant="h5" gutterBottom sx={{ mb: 3, color: 'primary.main' }}>
              Quick Attendance
            </Typography>
            {courses.length > 0 ? (
              <Grid container spacing={3}>
                {courses.map((course, index) => (
                  <Grid item xs={12} md={4} key={course.id || course._id || index}>
                    <Card sx={{ 
                      textAlign: 'center', 
                      p: 3, 
                      border: '1px solid #1976d2',
                      '&:hover': { border: '2px solid #1976d2' }
                    }}>
                      <CardContent>
                        <Typography variant="h6" color="primary">
                          {course.courseCode || course.course_code}
                        </Typography>
                        <Typography variant="body2" color="textSecondary" sx={{ mb: 2 }}>
                          {course.courseName || course.course_name}
                        </Typography>
                        <Box sx={{ display: 'flex', flexDirection: 'column', gap: 1 }}>
                          <Button 
                            variant="contained" 
                            onClick={() => startAttendanceSession(course, 'qr')}
                            startIcon={<QrCode2 />}
                            color="primary"
                          >
                            QR Attendance
                          </Button>
                          <Button 
                            variant="outlined" 
                            onClick={() => startAttendanceSession(course, 'face')}
                            startIcon={<Face />}
                            color="primary"
                          >
                            Face Recognition
                          </Button>
                          <Button 
                            variant="outlined" 
                            onClick={() => startAttendanceSession(course, 'manual')}
                            color="primary"
                          >
                            Manual Entry
                          </Button>
                        </Box>
                      </CardContent>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            ) : (
              <Paper sx={{ p: 4, textAlign: 'center', border: '1px solid #1976d2' }}>
                <Typography variant="h6" color="textSecondary">
                  No courses available for attendance
                </Typography>
                <Typography variant="body2" color="textSecondary" sx={{ mt: 1 }}>
                  Please check the "My Courses" tab or contact administrator.
                </Typography>
              </Paper>
            )}
          </Box>
        )}

        {/* Attendance Reports Tab */}
        {activeTab === 3 && (
          <Box>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
              <Typography variant="h5" color="primary.main">Attendance Reports</Typography>
              <Box sx={{ display: 'flex', gap: 1 }}>
                <Button startIcon={<Download />} variant="outlined" color="primary">
                  Export All
                </Button>
                <Button startIcon={<Print />} variant="outlined" color="primary">
                  Print
                </Button>
              </Box>
            </Box>

            <Grid container spacing={3} sx={{ mb: 3 }}>
              <Grid item xs={12} md={3}>
                <Card sx={{ 
                  textAlign: 'center', 
                  p: 2, 
                  bgcolor: '#1976d2', 
                  color: 'white',
                  border: '2px solid #1565c0'
                }}>
                  <Typography variant="h4">{reportsData.overallAttendance || 0}%</Typography>
                  <Typography variant="body2">Overall Attendance</Typography>
                </Card>
              </Grid>
              <Grid item xs={12} md={3}>
                <Card sx={{ 
                  textAlign: 'center', 
                  p: 2, 
                  bgcolor: '#1976d2', 
                  color: 'white',
                  border: '2px solid #1565c0'
                }}>
                  <Typography variant="h4">{reportsData.presentToday || 0}</Typography>
                  <Typography variant="body2">Present Today</Typography>
                </Card>
              </Grid>
              <Grid item xs={12} md={3}>
                <Card sx={{ 
                  textAlign: 'center', 
                  p: 2, 
                  bgcolor: '#1976d2', 
                  color: 'white',
                  border: '2px solid #1565c0'
                }}>
                  <Typography variant="h4">{reportsData.lateToday || 0}</Typography>
                  <Typography variant="body2">Late Today</Typography>
                </Card>
              </Grid>
              <Grid item xs={12} md={3}>
                <Card sx={{ 
                  textAlign: 'center', 
                  p: 2, 
                  bgcolor: '#1976d2', 
                  color: 'white',
                  border: '2px solid #1565c0'
                }}>
                  <Typography variant="h4">{reportsData.absentToday || 0}</Typography>
                  <Typography variant="body2">Absent Today</Typography>
                </Card>
              </Grid>
            </Grid>

            {courses.length > 0 ? (
              <Grid container spacing={3}>
                {courses.map((course, index) => {
                  const courseId = course.id || course._id;
                  const records = attendanceRecords[courseId] || [];
                  const presentCount = records.filter(r => r.status === 'Present').length;
                  const attendancePercentage = records.length > 0 ? Math.round((presentCount / records.length) * 100) : 0;

                  return (
                    <Grid item xs={12} md={6} key={courseId || index}>
                      <Card sx={{ border: '1px solid #1976d2' }}>
                        <CardContent>
                          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                            <Typography variant="h6" color="primary">
                              {course.courseCode || course.course_code} - {course.courseName || course.course_name}
                            </Typography>
                            <Button 
                              size="small" 
                              startIcon={<Download />}
                              onClick={() => downloadAttendanceReport(courseId)}
                              color="primary"
                            >
                              Export
                            </Button>
                          </Box>

                          <Box sx={{ mb: 2 }}>
                            <Typography variant="body2" gutterBottom>
                              Attendance: {attendancePercentage}% ({presentCount}/{records.length})
                            </Typography>
                            <LinearProgress 
                              variant="determinate" 
                              value={attendancePercentage} 
                              color={
                                attendancePercentage >= 80 ? 'primary' :
                                attendancePercentage >= 60 ? 'secondary' : 'error'
                              }
                              sx={{ height: 8, borderRadius: 4 }}
                            />
                          </Box>

                          {records.length > 0 ? (
                            <TableContainer>
                              <Table size="small">
                                <TableHead sx={{ bgcolor: '#e3f2fd' }}>
                                  <TableRow>
                                    <TableCell sx={{ fontWeight: 'bold' }}>Student</TableCell>
                                    <TableCell sx={{ fontWeight: 'bold' }}>Status</TableCell>
                                    <TableCell sx={{ fontWeight: 'bold' }}>Time</TableCell>
                                    <TableCell sx={{ fontWeight: 'bold' }}>Method</TableCell>
                                  </TableRow>
                                </TableHead>
                                <TableBody>
                                  {records.slice(0, 5).map((record) => (
                                    <TableRow key={record.id} hover>
                                      <TableCell>
                                        <Typography variant="body2">{record.student_name}</Typography>
                                        <Typography variant="caption" color="textSecondary">
                                          {record.student_id}
                                        </Typography>
                                      </TableCell>
                                      <TableCell>
                                        <Chip 
                                          label={record.status}
                                          color={
                                            record.status === 'Present' ? 'primary' :
                                            record.status === 'Late' ? 'secondary' : 'default'
                                          }
                                          size="small"
                                          variant="outlined"
                                        />
                                      </TableCell>
                                      <TableCell>
                                        <Typography variant="caption">
                                          {record.time}
                                        </Typography>
                                      </TableCell>
                                      <TableCell>
                                        <Typography variant="caption">
                                          {record.method}
                                        </Typography>
                                      </TableCell>
                                    </TableRow>
                                  ))}
                                </TableBody>
                              </Table>
                            </TableContainer>
                          ) : (
                            <Typography variant="body2" color="textSecondary" sx={{ textAlign: 'center', py: 2 }}>
                              No attendance records yet
                            </Typography>
                          )}
                        </CardContent>
                      </Card>
                    </Grid>
                  );
                })}
              </Grid>
            ) : (
              <Paper sx={{ p: 4, textAlign: 'center', border: '1px solid #1976d2' }}>
                <Typography variant="h6" color="textSecondary">
                  No courses available for reports
                </Typography>
              </Paper>
            )}
          </Box>
        )}

        {/* Manual Attendance Tab */}
        {activeTab === 4 && (
          <Box>
            <Typography variant="h5" gutterBottom sx={{ mb: 3, color: 'primary.main' }}>
              Manual Attendance Entry
            </Typography>
            <Paper sx={{ p: 3, border: '1px solid #1976d2' }}>
              <Typography variant="h6" gutterBottom color="primary">
                Select Course for Manual Attendance
              </Typography>

              {courses.length > 0 ? (
                <>
                  <Grid container spacing={2} sx={{ mb: 3 }}>
                    {courses.map((course, index) => (
                      <Grid item xs={12} md={4} key={course.id || course._id || index}>
                        <Card 
                          sx={{ 
                            p: 2, 
                            cursor: 'pointer',
                            border: selectedCourse?.id === course.id ? '2px solid #1976d2' : '1px solid #1976d2',
                            backgroundColor: selectedCourse?.id === course.id ? '#e3f2fd' : 'white',
                            transition: 'all 0.3s ease'
                          }}
                          onClick={() => setSelectedCourse(course)}
                        >
                          <Typography variant="h6" color="primary">
                            {course.courseCode || course.course_code}
                          </Typography>
                          <Typography variant="body2">
                            {course.courseName || course.course_name}
                          </Typography>
                        </Card>
                      </Grid>
                    ))}
                  </Grid>

                  {selectedCourse && (
                    <Box>
                      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                        <Typography variant="h6" color="primary">
                          Mark Attendance for {selectedCourse.courseCode || selectedCourse.course_code} - {selectedCourse.courseName || selectedCourse.course_name}
                        </Typography>
                        <Button variant="contained" onClick={submitManualAttendance} color="primary">
                          Submit Attendance
                        </Button>
                      </Box>

                      <TableContainer component={Paper} sx={{ border: '1px solid #1976d2' }}>
                        <Table>
                          <TableHead sx={{ bgcolor: '#1976d2' }}>
                            <TableRow>
                              <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>Student Name</TableCell>
                              <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>Student ID</TableCell>
                              <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>Status</TableCell>
                              <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>Current Status</TableCell>
                            </TableRow>
                          </TableHead>
                          <TableBody>
                            {(attendanceRecords[selectedCourse.id || selectedCourse._id] || []).map((record) => (
                              <TableRow key={record.id} hover>
                                <TableCell>{record.student_name}</TableCell>
                                <TableCell>{record.student_id}</TableCell>
                                <TableCell>
                                  <FormControl size="small" fullWidth>
                                    <Select
                                      value={manualAttendance[record.student_id] || record.status}
                                      onChange={(e) => markManualAttendance(record.student_id, e.target.value)}
                                      color="primary"
                                    >
                                      <MenuItem value="Present">Present</MenuItem>
                                      <MenuItem value="Late">Late</MenuItem>
                                      <MenuItem value="Absent">Absent</MenuItem>
                                    </Select>
                                  </FormControl>
                                </TableCell>
                                <TableCell>
                                  <Chip 
                                    label={record.status}
                                    color={
                                      record.status === 'Present' ? 'primary' :
                                      record.status === 'Late' ? 'secondary' : 'default'
                                    }
                                    size="small"
                                    variant="outlined"
                                  />
                                </TableCell>
                              </TableRow>
                            ))}
                          </TableBody>
                        </Table>
                      </TableContainer>
                    </Box>
                  )}
                </>
              ) : (
                <Typography variant="body2" color="textSecondary" sx={{ textAlign: 'center', py: 4 }}>
                  No courses available for manual attendance
                </Typography>
              )}
            </Paper>
          </Box>
        )}

        {/* Faculty Profile Tab */}
        {activeTab === 5 && facultyProfile && (
          <FacultyProfile 
            faculty={facultyProfile}
            onUpdateProfile={updateFacultyProfile}
            setSnackbar={setSnackbar}
          />
        )}

        {/* Attendance Dialog */}
        <Dialog 
          open={attendanceDialog} 
          onClose={closeAttendanceSession}
          maxWidth="md"
          fullWidth
        >
          <DialogTitle sx={{ bgcolor: '#1976d2', color: 'white' }}>
            Take Attendance - {selectedCourse?.courseCode || selectedCourse?.course_code}
            <Typography variant="body2" sx={{ color: 'white' }}>
              {selectedCourse?.courseName || selectedCourse?.course_name}
            </Typography>
          </DialogTitle>
          <DialogContent>
            {/* Method Selection */}
            <Box sx={{ mb: 3, display: 'flex', gap: 1, flexWrap: 'wrap' }}>
              <Button 
                variant={attendanceMethod === 'qr' ? 'contained' : 'outlined'}
                onClick={() => {
                  setAttendanceMethod('qr');
                  if (selectedCourse) generateQRCode(selectedCourse);
                }}
                startIcon={<QrCode2 />}
                color="primary"
              >
                QR Code
              </Button>
              <Button 
                variant={attendanceMethod === 'face' ? 'contained' : 'outlined'}
                onClick={() => setAttendanceMethod('face')}
                startIcon={<Face />}
                color="primary"
              >
                Face Recognition
              </Button>
              <Button 
                variant={attendanceMethod === 'manual' ? 'contained' : 'outlined'}
                onClick={() => setAttendanceMethod('manual')}
                color="primary"
              >
                Manual Entry
              </Button>
            </Box>

            {/* QR Code Method */}
            {attendanceMethod === 'qr' && (
              <QRAttendanceComponent
                qrCodeHash={qrCodeHash}
                onRefresh={refreshQRCode}
                onSimulateScan={simulateQRScan}
                scannedCount={scannedCount}
                isActive={qrSessionActive}
                course={selectedCourse}
                sessionTimeLeft={qrSessionTimer}
              />
            )}

            {/* Face Recognition Method */}
            {attendanceMethod === 'face' && (
              <RealFaceRecognition
                onAttendanceMarked={markAttendance}
                isActive={attendanceMethod === 'face'}
                setSnackbar={setSnackbar}
                course={selectedCourse}
              />
            )}

            {/* Manual Method */}
            {attendanceMethod === 'manual' && (
              <Paper sx={{ p: 3, border: '1px solid #1976d2' }}>
                <Typography variant="h6" gutterBottom color="primary">
                  Manual Attendance Entry
                </Typography>
                <TableContainer>
                  <Table>
                    <TableHead sx={{ bgcolor: '#e3f2fd' }}>
                      <TableRow>
                        <TableCell sx={{ fontWeight: 'bold' }}>Student Name</TableCell>
                        <TableCell sx={{ fontWeight: 'bold' }}>Student ID</TableCell>
                        <TableCell sx={{ fontWeight: 'bold' }}>Status</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {(attendanceRecords[selectedCourse?.id || selectedCourse?._id] || []).map((record) => (
                        <TableRow key={record.id} hover>
                          <TableCell>{record.student_name}</TableCell>
                          <TableCell>{record.student_id}</TableCell>
                          <TableCell>
                            <FormControl size="small" fullWidth>
                              <Select
                                value={manualAttendance[record.student_id] || record.status}
                                onChange={(e) => markManualAttendance(record.student_id, e.target.value)}
                                color="primary"
                              >
                                <MenuItem value="Present">Present</MenuItem>
                                <MenuItem value="Late">Late</MenuItem>
                                <MenuItem value="Absent">Absent</MenuItem>
                              </Select>
                            </FormControl>
                          </TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                </TableContainer>
              </Paper>
            )}

            {/* Current Attendance Status */}
            <Paper sx={{ p: 2, mt: 2, border: '1px solid #1976d2', bgcolor: '#e3f2fd' }}>
              <Typography variant="h6" gutterBottom color="primary">
                Current Attendance Status
              </Typography>
              <Grid container spacing={2}>
                <Grid item xs={12} md={4}>
                  <Typography variant="body2">
                    Total Students: {attendanceRecords[selectedCourse?.id || selectedCourse?._id]?.length || 0}
                  </Typography>
                </Grid>
                <Grid item xs={12} md={4}>
                  <Typography variant="body2" color="primary" sx={{ fontWeight: 'bold' }}>
                    Present: {attendanceRecords[selectedCourse?.id || selectedCourse?._id]?.filter(r => r.status === 'Present').length || 0}
                  </Typography>
                </Grid>
                <Grid item xs={12} md={4}>
                  <Typography variant="body2" color="error" sx={{ fontWeight: 'bold' }}>
                    Absent: {attendanceRecords[selectedCourse?.id || selectedCourse?._id]?.filter(r => r.status === 'Absent').length || 0}
                  </Typography>
                </Grid>
              </Grid>
            </Paper>
          </DialogContent>
          <DialogActions>
            <Button onClick={closeAttendanceSession} color="primary">
              Close Session
            </Button>
            <Button variant="contained" onClick={submitManualAttendance} color="primary">
              Submit Attendance
            </Button>
          </DialogActions>
        </Dialog>

        {/* Snackbar for notifications */}
        <Snackbar
          open={snackbar.open}
          autoHideDuration={4000}
          onClose={() => setSnackbar({ ...snackbar, open: false })}
          anchorOrigin={{ vertical: 'bottom', horizontal: 'left' }}
        >
          <Alert 
            severity={snackbar.severity} 
            onClose={() => setSnackbar({ ...snackbar, open: false })}
            sx={{ 
              bgcolor: snackbar.severity === 'success' ? '#4caf50' : 
                      snackbar.severity === 'error' ? '#f44336' :
                      snackbar.severity === 'warning' ? '#ff9800' : '#2196f3',
              color: 'white'
            }}
          >
            {snackbar.message}
          </Alert>
        </Snackbar>
      </Container>
    </Box>
  );
};

export default FacultyPortal;
