import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Button,
  Grid,
  Card,
  CardContent,
  Chip,
  IconButton,
  Paper,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Switch,
  FormControlLabel,
  Alert,
  Snackbar,
  CircularProgress,
  Tooltip,
  Tabs,
  Tab,
  Avatar,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Divider,
  Backdrop,
  Fab,
  Badge,
  LinearProgress
} from '@mui/material';
import {
  Add,
  Refresh,
  Edit,
  Delete,
  Person,
  Class,
  Link,
  LinkOff,
  Search,
  FilterList,
  AutoFixHigh,
  CheckCircle,
  Warning,
  School,
  Group,
  Map,
  Visibility,
  Download,
  Upload,
  Clear,
  Save,
  Cancel,
  ArrowForward,
  ArrowBack
} from '@mui/icons-material';

const CourseFacultyMappingTab = ({ 
  courses = [], 
  faculty = [], 
  onMapCourseToFaculty,
  onUnmapCourseFromFaculty,
  onAutoMapCourses,
  onSaveMappings,
  loading = false 
}) => {
  const [localCourses, setLocalCourses] = useState([]);
  const [localFaculty, setLocalFaculty] = useState([]);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [viewMode, setViewMode] = useState('unmapped');
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedDepartment, setSelectedDepartment] = useState('all');
  const [selectedFaculty, setSelectedFaculty] = useState(null);
  const [mappingDialogOpen, setMappingDialogOpen] = useState(false);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [autoMapProgress, setAutoMapProgress] = useState(0);
  const [mappingHistory, setMappingHistory] = useState([]);
  const [pendingMappings, setPendingMappings] = useState([]);
  const [bulkSelection, setBulkSelection] = useState([]);

  // Initialize with real data
  useEffect(() => {
    if (courses && courses.length > 0) {
      setLocalCourses([...courses]);
    }
    if (faculty && faculty.length > 0) {
      setLocalFaculty([...faculty]);
    }
  }, [courses, faculty]);

  // Get departments from courses and faculty
  const departments = [...new Set([
    ...courses.map(course => course.department),
    ...faculty.map(f => f.department)
  ])].filter(Boolean);

  // Filter courses based on view mode and search
  const filteredCourses = localCourses.filter(course => {
    if (!course) return false;

    const matchesSearch = course.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         course.code?.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesDepartment = selectedDepartment === 'all' || course.department === selectedDepartment;

    switch (viewMode) {
      case 'unmapped':
        return matchesSearch && matchesDepartment && !course.facultyId;
      case 'mapped':
        return matchesSearch && matchesDepartment && course.facultyId;
      case 'all':
      default:
        return matchesSearch && matchesDepartment;
    }
  });

  // Get faculty by department
  const getFacultyByDepartment = (department) => {
    return localFaculty.filter(f => f.department === department);
  };

  // Get mapped faculty for a course
  const getMappedFaculty = (course) => {
    if (!course.facultyId) return null;
    return localFaculty.find(f => f.id === course.facultyId || f._id === course.facultyId);
  };

  // Handle manual mapping
  const handleMapCourse = (course) => {
    setSelectedCourse(course);
    setMappingDialogOpen(true);
  };

  // Handle auto mapping for all unmapped courses
  const handleAutoMapAll = async () => {
    setAutoMapProgress(0);

    try {
      const unmappedCourses = localCourses.filter(course => !course.facultyId);
      let progress = 0;

      for (const course of unmappedCourses) {
        const suitableFaculty = findSuitableFaculty(course);
        if (suitableFaculty) {
          await handleMapCourseToFaculty(course, suitableFaculty, false);
        }
        progress += 1;
        setAutoMapProgress(Math.round((progress / unmappedCourses.length) * 100));
        await new Promise(resolve => setTimeout(resolve, 100)); // Small delay for progress visibility
      }

      setSuccess(`Auto-mapped ${unmappedCourses.length} courses successfully!`);
    } catch (err) {
      setError('Failed to auto-map courses: ' + err.message);
    } finally {
      setAutoMapProgress(0);
    }
  };

  // Find suitable faculty for a course
  const findSuitableFaculty = (course) => {
    if (!course.department) return null;

    // First, try to find faculty from same department with no or few courses
    const departmentFaculty = getFacultyByDepartment(course.department);

    // Count current course load for each faculty
    const facultyLoad = {};
    localCourses.forEach(c => {
      if (c.facultyId) {
        facultyLoad[c.facultyId] = (facultyLoad[c.facultyId] || 0) + 1;
      }
    });

    // Find faculty with least courses from same department
    const suitableFaculty = departmentFaculty
      .filter(f => !facultyLoad[f.id] || facultyLoad[f.id] < 5) // Max 5 courses per faculty
      .sort((a, b) => (facultyLoad[a.id] || 0) - (facultyLoad[b.id] || 0))[0];

    return suitableFaculty || departmentFaculty[0]; // Fallback to first faculty in department
  };

  // Map course to faculty
  const handleMapCourseToFaculty = async (course, faculty, showNotification = true) => {
    try {
      const mappingData = {
        courseId: course.id || course._id,
        facultyId: faculty.id || faculty._id,
        courseCode: course.code,
        facultyName: faculty.name,
        department: course.department
      };

      // Call backend API
      if (onMapCourseToFaculty) {
        await onMapCourseToFaculty(mappingData);
      }

      // Update local state
      setLocalCourses(prev => prev.map(c => 
        (c.id === course.id || c._id === course.id) 
          ? { ...c, facultyId: faculty.id || faculty._id, faculty: faculty }
          : c
      ));

      // Add to history
      setMappingHistory(prev => [...prev, {
        type: 'map',
        course: course.code,
        faculty: faculty.name,
        timestamp: new Date().toISOString()
      }]);

      if (showNotification) {
        setSuccess(`Course ${course.code} mapped to ${faculty.name} successfully!`);
      }

    } catch (err) {
      setError('Failed to map course: ' + err.message);
      throw err;
    }
  };

  // Unmap course from faculty
  const handleUnmapCourse = async (course) => {
    try {
      // Call backend API
      if (onUnmapCourseFromFaculty) {
        await onUnmapCourseFromFaculty(course.id || course._id);
      }

      // Update local state
      setLocalCourses(prev => prev.map(c => 
        (c.id === course.id || c._id === course.id) 
          ? { ...c, facultyId: null, faculty: null }
          : c
      ));

      // Add to history
      setMappingHistory(prev => [...prev, {
        type: 'unmap',
        course: course.code,
        faculty: course.faculty?.name,
        timestamp: new Date().toISOString()
      }]);

      setSuccess(`Course ${course.code} unmapped successfully!`);

    } catch (err) {
      setError('Failed to unmap course: ' + err.message);
    }
  };

  // Bulk operations
  const handleBulkMap = async (faculty) => {
    if (bulkSelection.length === 0) return;

    try {
      let successCount = 0;

      for (const courseId of bulkSelection) {
        const course = localCourses.find(c => c.id === courseId || c._id === courseId);
        if (course) {
          try {
            await handleMapCourseToFaculty(course, faculty, false);
            successCount++;
          } catch (err) {
            console.error(`Failed to map course ${course.code}:`, err);
          }
        }
      }

      setBulkSelection([]);
      setSuccess(`Successfully mapped ${successCount} courses to ${faculty.name}`);

    } catch (err) {
      setError('Failed to bulk map courses: ' + err.message);
    }
  };

  // Statistics
  const stats = {
    totalCourses: localCourses.length,
    mappedCourses: localCourses.filter(c => c.facultyId).length,
    unmappedCourses: localCourses.filter(c => !c.facultyId).length,
    totalFaculty: localFaculty.length,
    facultyWithCourses: new Set(localCourses.filter(c => c.facultyId).map(c => c.facultyId)).size
  };

  // Course Card Component
  const CourseCard = ({ course }) => {
    const mappedFaculty = getMappedFaculty(course);

    return (
      <Card 
        elevation={2}
        sx={{ 
          height: '100%',
          border: '1px solid',
          borderColor: mappedFaculty ? 'success.light' : 'grey.300',
          transition: 'all 0.3s ease',
          '&:hover': {
            transform: 'translateY(-2px)',
            boxShadow: 4
          }
        }}
      >
        <CardContent>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 2 }}>
            <Box>
              <Typography variant="h6" color="primary" sx={{ fontFamily: 'monospace', fontWeight: 'bold' }}>
                {course.code}
              </Typography>
              <Typography variant="body1" fontWeight="600" sx={{ lineHeight: 1.2, mb: 1 }}>
                {course.name}
              </Typography>
            </Box>
            <Chip 
              label={course.courseType || 'Lecture'} 
              color={course.courseType === 'Lab' ? 'secondary' : 'primary'} 
              size="small" 
            />
          </Box>

          <Box sx={{ mb: 2 }}>
            <Typography variant="body2" color="text.secondary" gutterBottom>
               {course.credits || 0} Credits   {course.department || 'N/A'}
            </Typography>
            <Typography variant="body2" color="text.secondary">
               Capacity: {course.enrolled || 0}/{course.max || 30} students
            </Typography>
          </Box>

          {/* Faculty Mapping Section */}
          <Box sx={{ 
            p: 2, 
            bgcolor: mappedFaculty ? 'success.light' : 'warning.light',
            borderRadius: 1,
            mb: 2
          }}>
            {mappedFaculty ? (
              <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <Avatar sx={{ width: 32, height: 32, bgcolor: 'primary.main' }}>
                    {mappedFaculty.name?.charAt(0)}
                  </Avatar>
                  <Box>
                    <Typography variant="body2" fontWeight="600">
                      {mappedFaculty.name}
                    </Typography>
                    <Typography variant="caption" color="text.secondary">
                      {mappedFaculty.department}
                    </Typography>
                  </Box>
                </Box>
                <Tooltip title="Unmap Course">
                  <IconButton 
                    size="small" 
                    color="error"
                    onClick={() => handleUnmapCourse(course)}
                    disabled={loading}
                  >
                    <LinkOff fontSize="small" />
                  </IconButton>
                </Tooltip>
              </Box>
            ) : (
              <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                <Typography variant="body2" fontWeight="600">
                  Not Assigned
                </Typography>
                <Button
                  variant="outlined"
                  size="small"
                  startIcon={<Link />}
                  onClick={() => handleMapCourse(course)}
                  disabled={loading}
                >
                  Assign
                </Button>
              </Box>
            )}
          </Box>

          {/* Bulk Selection Checkbox */}
          {viewMode === 'unmapped' && !mappedFaculty && (
            <FormControlLabel
              control={
                <Switch
                  size="small"
                  checked={bulkSelection.includes(course.id || course._id)}
                  onChange={(e) => {
                    if (e.target.checked) {
                      setBulkSelection(prev => [...prev, course.id || course._id]);
                    } else {
                      setBulkSelection(prev => prev.filter(id => id !== (course.id || course._id)));
                    }
                  }}
                />
              }
              label="Bulk Select"
            />
          )}
        </CardContent>
      </Card>
    );
  };

  // Faculty List Component
  const FacultyList = () => (
    <Card elevation={2}>
      <CardContent>
        <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
          <Group /> Available Faculty
        </Typography>
        <List dense>
          {localFaculty.map((facultyMember) => {
            const courseCount = localCourses.filter(c => c.facultyId === (facultyMember.id || facultyMember._id)).length;

            return (
              <ListItem
                key={facultyMember.id || facultyMember._id}
                sx={{
                  border: '1px solid',
                  borderColor: 'divider',
                  borderRadius: 1,
                  mb: 1,
                  bgcolor: selectedFaculty?.id === facultyMember.id ? 'action.selected' : 'background.paper'
                }}
                onClick={() => setSelectedFaculty(facultyMember)}
              >
                <ListItemIcon>
                  <Avatar sx={{ width: 40, height: 40, bgcolor: 'primary.main' }}>
                    {facultyMember.name?.charAt(0)}
                  </Avatar>
                </ListItemIcon>
                <ListItemText
                  primary={
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                      <Typography variant="body1" fontWeight="600">
                        {facultyMember.name}
                      </Typography>
                      <Chip 
                        label={`${courseCount} courses`} 
                        size="small" 
                        color={courseCount > 4 ? 'warning' : 'success'}
                        variant="outlined"
                      />
                    </Box>
                  }
                  secondary={
                    <Box>
                      <Typography variant="body2" color="text.secondary">
                        {facultyMember.department}  {facultyMember.email}
                      </Typography>
                      <Typography variant="caption" color="text.secondary">
                        {facultyMember.qualification}
                      </Typography>
                    </Box>
                  }
                />
                {bulkSelection.length > 0 && (
                  <Tooltip title={`Map ${bulkSelection.length} selected courses to ${facultyMember.name}`}>
                    <Button
                      variant="outlined"
                      size="small"
                      onClick={() => handleBulkMap(facultyMember)}
                      disabled={loading}
                    >
                      Map {bulkSelection.length}
                    </Button>
                  </Tooltip>
                )}
              </ListItem>
            );
          })}
        </List>
      </CardContent>
    </Card>
  );

  // Mapping Dialog
  const MappingDialog = () => (
    <Dialog open={mappingDialogOpen} onClose={() => setMappingDialogOpen(false)} maxWidth="md" fullWidth>
      <DialogTitle>
        Map Course to Faculty
      </DialogTitle>
      <DialogContent>
        {selectedCourse && (
          <Box>
            <Typography variant="h6" gutterBottom>
              {selectedCourse.code} - {selectedCourse.name}
            </Typography>
            <Typography variant="body2" color="text.secondary" gutterBottom>
              Department: {selectedCourse.department}  Credits: {selectedCourse.credits}
            </Typography>

            <Divider sx={{ my: 2 }} />

            <Typography variant="subtitle1" gutterBottom>
              Select Faculty:
            </Typography>

            <Grid container spacing={2}>
              {getFacultyByDepartment(selectedCourse.department).map((facultyMember) => (
                <Grid item xs={12} key={facultyMember.id || facultyMember._id}>
                  <Card 
                    variant="outlined"
                    sx={{
                      cursor: 'pointer',
                      border: '2px solid',
                      borderColor: 'transparent',
                      '&:hover': {
                        borderColor: 'primary.main',
                        bgcolor: 'action.hover'
                      }
                    }}
                    onClick={() => {
                      handleMapCourseToFaculty(selectedCourse, facultyMember);
                      setMappingDialogOpen(false);
                    }}
                  >
                    <CardContent>
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                        <Avatar sx={{ bgcolor: 'primary.main' }}>
                          {facultyMember.name?.charAt(0)}
                        </Avatar>
                        <Box sx={{ flex: 1 }}>
                          <Typography variant="body1" fontWeight="600">
                            {facultyMember.name}
                          </Typography>
                          <Typography variant="body2" color="text.secondary">
                            {facultyMember.email}
                          </Typography>
                          <Typography variant="caption" color="text.secondary">
                            {facultyMember.qualification}
                          </Typography>
                        </Box>
                        <Chip 
                          label={`${localCourses.filter(c => c.facultyId === (facultyMember.id || facultyMember._id)).length} courses`} 
                          size="small" 
                          color="primary"
                          variant="outlined"
                        />
                      </Box>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>

            {getFacultyByDepartment(selectedCourse.department).length === 0 && (
              <Typography color="text.secondary" sx={{ textAlign: 'center', py: 4 }}>
                No faculty available in {selectedCourse.department} department
              </Typography>
            )}
          </Box>
        )}
      </DialogContent>
      <DialogActions>
        <Button onClick={() => setMappingDialogOpen(false)}>Cancel</Button>
      </DialogActions>
    </Dialog>
  );

  return (
    <Box sx={{ p: 3 }}>
      {/* Header */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Box>
          <Typography variant="h4" fontWeight="bold" color="primary" gutterBottom>
             Course-Faculty Mapping
          </Typography>
          <Typography variant="body1" color="text.secondary">
            Map courses to faculty members and manage teaching assignments
          </Typography>
        </Box>
        <Box sx={{ display: 'flex', gap: 1 }}>
          <Button
            variant="outlined"
            startIcon={<Download />}
            onClick={() => {/* Export functionality */}}
          >
            Export
          </Button>
          <Button
            variant="contained"
            startIcon={<Save />}
            onClick={onSaveMappings}
            disabled={loading}
          >
            Save All
          </Button>
        </Box>
      </Box>

      {/* Statistics */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={2.4}>
          <Card>
            <CardContent sx={{ textAlign: 'center' }}>
              <School sx={{ fontSize: 40, color: 'primary.main', mb: 1 }} />
              <Typography variant="h4" fontWeight="bold">
                {stats.totalCourses}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Total Courses
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={2.4}>
          <Card>
            <CardContent sx={{ textAlign: 'center' }}>
              <CheckCircle sx={{ fontSize: 40, color: 'success.main', mb: 1 }} />
              <Typography variant="h4" fontWeight="bold" color="success.main">
                {stats.mappedCourses}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Mapped Courses
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={2.4}>
          <Card>
            <CardContent sx={{ textAlign: 'center' }}>
              <Warning sx={{ fontSize: 40, color: 'warning.main', mb: 1 }} />
              <Typography variant="h4" fontWeight="bold" color="warning.main">
                {stats.unmappedCourses}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Unmapped Courses
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={2.4}>
          <Card>
            <CardContent sx={{ textAlign: 'center' }}>
              <Group sx={{ fontSize: 40, color: 'info.main', mb: 1 }} />
              <Typography variant="h4" fontWeight="bold">
                {stats.totalFaculty}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Total Faculty
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={2.4}>
          <Card>
            <CardContent sx={{ textAlign: 'center' }}>
              <Map sx={{ fontSize: 40, color: 'secondary.main', mb: 1 }} />
              <Typography variant="h4" fontWeight="bold">
                {stats.facultyWithCourses}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Active Faculty
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Controls */}
      <Card sx={{ mb: 3 }} elevation={2}>
        <CardContent>
          <Grid container spacing={3} alignItems="center">
            <Grid item xs={12} md={3}>
              <TextField
                fullWidth
                size="small"
                placeholder="Search courses..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                InputProps={{
                  startAdornment: <Search sx={{ mr: 1, color: 'text.secondary' }} />,
                }}
              />
            </Grid>
            <Grid item xs={12} md={2}>
              <FormControl fullWidth size="small">
                <InputLabel>Department</InputLabel>
                <Select
                  value={selectedDepartment}
                  onChange={(e) => setSelectedDepartment(e.target.value)}
                  label="Department"
                >
                  <MenuItem value="all">All Departments</MenuItem>
                  {departments.map(dept => (
                    <MenuItem key={dept} value={dept}>{dept}</MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} md={2}>
              <FormControl fullWidth size="small">
                <InputLabel>View Mode</InputLabel>
                <Select
                  value={viewMode}
                  onChange={(e) => {
                    setViewMode(e.target.value);
                    setBulkSelection([]);
                  }}
                  label="View Mode"
                >
                  <MenuItem value="unmapped">Unmapped</MenuItem>
                  <MenuItem value="mapped">Mapped</MenuItem>
                  <MenuItem value="all">All Courses</MenuItem>
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} md={5}>
              <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap' }}>
                <Button
                  variant="contained"
                  startIcon={<AutoFixHigh />}
                  onClick={handleAutoMapAll}
                  disabled={loading || stats.unmappedCourses === 0}
                  sx={{ flex: 1 }}
                >
                  Auto Map All
                </Button>

                {bulkSelection.length > 0 && (
                  <Button
                    variant="outlined"
                    color="secondary"
                    startIcon={<Clear />}
                    onClick={() => setBulkSelection([])}
                  >
                    Clear ({bulkSelection.length})
                  </Button>
                )}

                <Button
                  variant="outlined"
                  startIcon={<Refresh />}
                  onClick={() => {
                    setLocalCourses([...courses]);
                    setLocalFaculty([...faculty]);
                  }}
                  disabled={loading}
                >
                  Refresh
                </Button>
              </Box>
            </Grid>
          </Grid>

          {/* Auto-map Progress */}
          {autoMapProgress > 0 && (
            <Box sx={{ mt: 2 }}>
              <Typography variant="body2" gutterBottom>
                Auto-mapping courses... {autoMapProgress}%
              </Typography>
              <LinearProgress 
                variant="determinate" 
                value={autoMapProgress} 
                sx={{ height: 8, borderRadius: 4 }}
              />
            </Box>
          )}
        </CardContent>
      </Card>

      {/* Main Content */}
      <Grid container spacing={3}>
        {/* Courses Grid */}
        <Grid item xs={12} md={8}>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
            <Typography variant="h6">
              {viewMode === 'unmapped' ? ' Unmapped Courses' : 
               viewMode === 'mapped' ? ' Mapped Courses' : ' All Courses'}
              <Typography variant="body2" color="text.secondary" component="span" sx={{ ml: 1 }}>
                ({filteredCourses.length} courses)
              </Typography>
            </Typography>

            {viewMode === 'unmapped' && bulkSelection.length > 0 && (
              <Typography variant="body2" color="primary" fontWeight="600">
                {bulkSelection.length} courses selected for bulk mapping
              </Typography>
            )}
          </Box>

          {filteredCourses.length === 0 ? (
            <Paper sx={{ p: 8, textAlign: 'center' }}>
              <Class sx={{ fontSize: 80, color: 'text.secondary', mb: 2, opacity: 0.5 }} />
              <Typography variant="h6" color="text.secondary" gutterBottom>
                No courses found
              </Typography>
              <Typography variant="body2" color="text.secondary">
                {searchTerm || selectedDepartment !== 'all' 
                  ? 'Try adjusting your search terms or filters' 
                  : viewMode === 'unmapped' 
                    ? 'All courses are mapped!' 
                    : 'No courses available'}
              </Typography>
            </Paper>
          ) : (
            <Grid container spacing={2}>
              {filteredCourses.map(course => (
                <Grid item xs={12} sm={6} lg={4} key={course.id || course._id}>
                  <CourseCard course={course} />
                </Grid>
              ))}
            </Grid>
          )}
        </Grid>

        {/* Faculty Sidebar */}
        <Grid item xs={12} md={4}>
          <FacultyList />

          {/* Recent Activity */}
          {mappingHistory.length > 0 && (
            <Card elevation={2} sx={{ mt: 3 }}>
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  Recent Activity
                </Typography>
                <List dense>
                  {mappingHistory.slice(-5).reverse().map((activity, index) => (
                    <ListItem key={index}>
                      <ListItemIcon>
                        {activity.type === 'map' ? 
                          <Link color="success" /> : 
                          <LinkOff color="error" />
                        }
                      </ListItemIcon>
                      <ListItemText
                        primary={`${activity.course} ${activity.type === 'map' ? 'mapped to' : 'unmapped from'} ${activity.faculty}`}
                        secondary={new Date(activity.timestamp).toLocaleString()}
                      />
                    </ListItem>
                  ))}
                </List>
              </CardContent>
            </Card>
          )}
        </Grid>
      </Grid>

      {/* Mapping Dialog */}
      <MappingDialog />

      {/* Notifications */}
      <Snackbar open={!!error} autoHideDuration={6000} onClose={() => setError(null)}>
        <Alert severity="error" onClose={() => setError(null)}>
          {error}
        </Alert>
      </Snackbar>

      <Snackbar open={!!success} autoHideDuration={4000} onClose={() => setSuccess(null)}>
        <Alert severity="success" onClose={() => setSuccess(null)}>
          {success}
        </Alert>
      </Snackbar>

      {/* Loading Backdrop */}
      <Backdrop open={loading} sx={{ zIndex: 9999 }}>
        <CircularProgress color="inherit" />
      </Backdrop>
    </Box>
  );
};

export default CourseFacultyMappingTab;
