import React, { useState, useEffect } from 'react';
import {
  Box,
  Typography,
  Button,
  Grid,
  Card,
  CardContent,
  Chip,
  IconButton,
  Paper,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Switch,
  FormControlLabel,
  Alert,
  Snackbar,
  CircularProgress,
  Tooltip,
  Tabs,
  Tab,
  RadioGroup,
  Radio,
  FormLabel,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  List,
  ListItem,
  ListItemText,
  Divider,
  Backdrop,
  Fab
} from '@mui/material';
import {
  Add,
  Refresh,
  Edit,
  Delete,
  AutoAwesome,
  Settings,
  Download,
  Print,
  Share,
  Visibility,
  Schedule,
  Class,
  Person,
  MeetingRoom,
  ExpandMore,
  FilterList,
  Search,
  Save,
  Clear,
  AutoFixHigh,
  EditCalendar,
  ContentCopy,
  Undo,
  Redo,
  Warning,
  CheckCircle
} from '@mui/icons-material';

// Mock data - Replace with actual API calls
const mockData = {
  courses: [
    { id: 1, code: 'CS101', name: 'Introduction to Programming', credits: 3, department: 'Computer Science' },
    { id: 2, code: 'MATH201', name: 'Calculus I', credits: 4, department: 'Mathematics' },
    { id: 3, code: 'PHY101', name: 'Physics Fundamentals', credits: 3, department: 'Physics' },
    { id: 4, code: 'CS201', name: 'Data Structures', credits: 3, department: 'Computer Science' },
    { id: 5, code: 'MATH202', name: 'Calculus II', credits: 4, department: 'Mathematics' }
  ],
  faculties: [
    { id: 1, name: 'Dr. Smith', department: 'Computer Science', maxHours: 20 },
    { id: 2, name: 'Dr. Johnson', department: 'Mathematics', maxHours: 18 },
    { id: 3, name: 'Dr. Brown', department: 'Physics', maxHours: 16 },
    { id: 4, name: 'Prof. Davis', department: 'Computer Science', maxHours: 22 }
  ],
  classrooms: [
    { id: 1, name: 'Room 101', capacity: 30, type: 'Lecture', facilities: ['Projector', 'Whiteboard'] },
    { id: 2, name: 'Lab A', capacity: 20, type: 'Lab', facilities: ['Computers', 'Projector'] },
    { id: 3, name: 'Room 201', capacity: 40, type: 'Lecture', facilities: ['Projector', 'Sound System'] },
    { id: 4, name: 'Lab B', capacity: 25, type: 'Lab', facilities: ['Computers', 'Whiteboard'] }
  ],
  timeSlots: [
    '08:00-09:00', '09:00-10:00', '10:00-11:00', '11:00-12:00',
    '12:00-13:00', '13:00-14:00', '14:00-15:00', '15:00-16:00', '16:00-17:00'
  ],
  days: ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday']
};

const TimetableManagementTab = ({ colorScheme }) => {
  const [timetable, setTimetable] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [viewMode, setViewMode] = useState('auto');
  const [selectedDepartment, setSelectedDepartment] = useState('all');
  const [constraints, setConstraints] = useState({
    avoidBackToBack: true,
    maxHoursPerDay: 6,
    minGapBetweenClasses: 1,
    preferMorningSlots: true,
    avoidEveningClasses: false
  });
  const [editMode, setEditMode] = useState(false);
  const [scheduleDialogOpen, setScheduleDialogOpen] = useState(false);
  const [selectedSlot, setSelectedSlot] = useState(null);
  const [conflicts, setConflicts] = useState([]);
  const [generationProgress, setGenerationProgress] = useState(0);
  const [history, setHistory] = useState([]);
  const [historyIndex, setHistoryIndex] = useState(-1);

  // Initialize timetable structure
  useEffect(() => {
    initializeTimetable();
  }, []);

  const initializeTimetable = () => {
    const initialTimetable = {};
    mockData.days.forEach(day => {
      initialTimetable[day] = {};
      mockData.timeSlots.forEach(slot => {
        initialTimetable[day][slot] = null;
      });
    });
    setTimetable(initialTimetable);
    addToHistory(initialTimetable);
  };

  const addToHistory = (newTimetable) => {
    const newHistory = history.slice(0, historyIndex + 1);
    newHistory.push(JSON.parse(JSON.stringify(newTimetable)));
    setHistory(newHistory);
    setHistoryIndex(newHistory.length - 1);
  };

  const undo = () => {
    if (historyIndex > 0) {
      setHistoryIndex(historyIndex - 1);
      setTimetable(JSON.parse(JSON.stringify(history[historyIndex - 1])));
    }
  };

  const redo = () => {
    if (historyIndex < history.length - 1) {
      setHistoryIndex(historyIndex + 1);
      setTimetable(JSON.parse(JSON.stringify(history[historyIndex + 1])));
    }
  };

  const generateTimetableAutomatically = async () => {
    setLoading(true);
    setGenerationProgress(0);

    try {
      // Simulate generation process
      for (let i = 0; i <= 100; i += 10) {
        setGenerationProgress(i);
        await new Promise(resolve => setTimeout(resolve, 200));
      }

      // Generate timetable using constraints
      const generatedTimetable = JSON.parse(JSON.stringify(timetable));
      const availableSlots = [];

      mockData.days.forEach(day => {
        mockData.timeSlots.forEach(slot => {
          availableSlots.push({ day, slot });
        });
      });

      // Simple scheduling algorithm
      mockData.courses.forEach(course => {
        const faculty = mockData.faculties.find(f => f.department === course.department);
        const classroom = mockData.classrooms.find(c => c.type === 'Lecture' && c.capacity >= 30);

        if (faculty && classroom && availableSlots.length > 0) {
          const randomSlot = availableSlots[Math.floor(Math.random() * availableSlots.length)];
          generatedTimetable[randomSlot.day][randomSlot.slot] = {
            course: course,
            faculty: faculty,
            classroom: classroom,
            type: 'Lecture'
          };
          // Remove used slot
          const index = availableSlots.findIndex(s => s.day === randomSlot.day && s.slot === randomSlot.slot);
          availableSlots.splice(index, 1);
        }
      });

      setTimetable(generatedTimetable);
      addToHistory(generatedTimetable);
      setSuccess('Timetable generated successfully!');
      detectConflicts(generatedTimetable);
    } catch (err) {
      setError('Failed to generate timetable');
    } finally {
      setLoading(false);
      setGenerationProgress(0);
    }
  };

  const detectConflicts = (timetableToCheck) => {
    const newConflicts = [];
    const facultySchedule = {};
    const classroomSchedule = {};

    mockData.days.forEach(day => {
      mockData.timeSlots.forEach(slot => {
        const scheduledClass = timetableToCheck[day][slot];
        if (scheduledClass) {
          // Check faculty conflicts
          const facultyKey = `${scheduledClass.faculty.id}-${day}-${slot}`;
          if (facultySchedule[facultyKey]) {
            newConflicts.push({
              type: 'faculty',
              message: `${scheduledClass.faculty.name} is double-booked at ${day} ${slot}`,
              day,
              slot,
              resource: scheduledClass.faculty
            });
          } else {
            facultySchedule[facultyKey] = true;
          }

          // Check classroom conflicts
          const classroomKey = `${scheduledClass.classroom.id}-${day}-${slot}`;
          if (classroomSchedule[classroomKey]) {
            newConflicts.push({
              type: 'classroom',
              message: `${scheduledClass.classroom.name} is double-booked at ${day} ${slot}`,
              day,
              slot,
              resource: scheduledClass.classroom
            });
          } else {
            classroomSchedule[classroomKey] = true;
          }
        }
      });
    });

    setConflicts(newConflicts);
  };

  const handleManualSchedule = (day, slot) => {
    setSelectedSlot({ day, slot });
    setScheduleDialogOpen(true);
  };

  const saveManualSchedule = (scheduleData) => {
    const newTimetable = JSON.parse(JSON.stringify(timetable));
    newTimetable[selectedSlot.day][selectedSlot.slot] = scheduleData;
    setTimetable(newTimetable);
    addToHistory(newTimetable);
    detectConflicts(newTimetable);
    setScheduleDialogOpen(false);
    setSelectedSlot(null);
    setSuccess('Class scheduled successfully!');
  };

  const clearSlot = (day, slot) => {
    const newTimetable = JSON.parse(JSON.stringify(timetable));
    newTimetable[day][slot] = null;
    setTimetable(newTimetable);
    addToHistory(newTimetable);
    detectConflicts(newTimetable);
    setSuccess('Slot cleared successfully!');
  };

  const exportTimetable = (format) => {
    // Implement export functionality
    setSuccess(`Timetable exported as ${format.toUpperCase()} successfully!`);
  };

  const getDepartmentColor = (department) => {
    const colors = {
      'Computer Science': '#1976d2',
      'Mathematics': '#2e7d32',
      'Physics': '#ed6c02',
      'Chemistry': '#7b1fa2',
      'Biology': '#00838f'
    };
    return colors[department] || '#757575';
  };

  const TimetableGridView = () => (
    <Box sx={{ mt: 3 }}>
      <TableContainer component={Paper} elevation={2}>
        <Table sx={{ minWidth: 800 }}>
          <TableHead>
            <TableRow>
              <TableCell sx={{ fontWeight: 'bold', bgcolor: 'primary.main', color: 'white' }}>
                Time / Day
              </TableCell>
              {mockData.days.map(day => (
                <TableCell key={day} align="center" sx={{ fontWeight: 'bold', bgcolor: 'primary.main', color: 'white' }}>
                  {day}
                </TableCell>
              ))}
            </TableRow>
          </TableHead>
          <TableBody>
            {mockData.timeSlots.map(slot => (
              <TableRow key={slot}>
                <TableCell sx={{ fontWeight: 'bold', bgcolor: 'grey.50' }}>
                  {slot}
                </TableCell>
                {mockData.days.map(day => {
                  const scheduledClass = timetable[day]?.[slot];
                  const hasConflict = conflicts.some(conflict => 
                    conflict.day === day && conflict.slot === slot
                  );

                  return (
                    <TableCell
                      key={`${day}-${slot}`}
                      sx={{
                        minWidth: 150,
                        height: 80,
                        border: '1px solid',
                        borderColor: 'divider',
                        bgcolor: hasConflict ? 'error.light' : (scheduledClass ? 'success.light' : 'background.paper'),
                        cursor: editMode ? 'pointer' : 'default',
                        position: 'relative',
                        '&:hover': editMode ? {
                          bgcolor: 'action.hover',
                          '& .action-buttons': { opacity: 1 }
                        } : {}
                      }}
                      onClick={() => editMode && handleManualSchedule(day, slot)}
                    >
                      {scheduledClass ? (
                        <Box>
                          <Typography variant="caption" fontWeight="bold" display="block">
                            {scheduledClass.course.code}
                          </Typography>
                          <Typography variant="caption" display="block">
                            {scheduledClass.course.name}
                          </Typography>
                          <Typography variant="caption" color="text.secondary" display="block">
                            {scheduledClass.faculty.name}
                          </Typography>
                          <Typography variant="caption" color="text.secondary" display="block">
                            {scheduledClass.classroom.name}
                          </Typography>
                          <Chip
                            label={scheduledClass.type}
                            size="small"
                            sx={{
                              height: 16,
                              fontSize: '0.6rem',
                              bgcolor: getDepartmentColor(scheduledClass.course.department)
                            }}
                          />
                        </Box>
                      ) : (
                        <Typography variant="caption" color="text.secondary">
                          Available
                        </Typography>
                      )}

                      {editMode && scheduledClass && (
                        <Box className="action-buttons" sx={{ opacity: 0, position: 'absolute', top: 4, right: 4, transition: 'opacity 0.2s' }}>
                          <Tooltip title="Clear Slot">
                            <IconButton size="small" onClick={(e) => { e.stopPropagation(); clearSlot(day, slot); }}>
                              <Clear fontSize="small" />
                            </IconButton>
                          </Tooltip>
                        </Box>
                      )}
                    </TableCell>
                  );
                })}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Box>
  );

  const ScheduleDialog = () => (
    <Dialog open={scheduleDialogOpen} onClose={() => setScheduleDialogOpen(false)} maxWidth="md" fullWidth>
      <DialogTitle>Schedule Class</DialogTitle>
      <DialogContent>
        <Typography variant="subtitle1" gutterBottom>
          Scheduling for {selectedSlot?.day} at {selectedSlot?.slot}
        </Typography>
        <Grid container spacing={2} sx={{ mt: 1 }}>
          <Grid item xs={12}>
            <FormControl fullWidth>
              <InputLabel>Select Course</InputLabel>
              <Select label="Select Course">
                {mockData.courses.map(course => (
                  <MenuItem key={course.id} value={course.id}>
                    {course.code} - {course.name}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12}>
            <FormControl fullWidth>
              <InputLabel>Select Faculty</InputLabel>
              <Select label="Select Faculty">
                {mockData.faculties.map(faculty => (
                  <MenuItem key={faculty.id} value={faculty.id}>
                    {faculty.name} - {faculty.department}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12}>
            <FormControl fullWidth>
              <InputLabel>Select Classroom</InputLabel>
              <Select label="Select Classroom">
                {mockData.classrooms.map(classroom => (
                  <MenuItem key={classroom.id} value={classroom.id}>
                    {classroom.name} ({classroom.type}, Capacity: {classroom.capacity})
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
          <Grid item xs={12}>
            <FormControl fullWidth>
              <InputLabel>Class Type</InputLabel>
              <Select label="Class Type" defaultValue="Lecture">
                <MenuItem value="Lecture">Lecture</MenuItem>
                <MenuItem value="Lab">Lab</MenuItem>
                <MenuItem value="Tutorial">Tutorial</MenuItem>
              </Select>
            </FormControl>
          </Grid>
        </Grid>
      </DialogContent>
      <DialogActions>
        <Button onClick={() => setScheduleDialogOpen(false)}>Cancel</Button>
        <Button variant="contained" onClick={() => saveManualSchedule({
          course: mockData.courses[0],
          faculty: mockData.faculties[0],
          classroom: mockData.classrooms[0],
          type: 'Lecture'
        })}>
          Schedule
        </Button>
      </DialogActions>
    </Dialog>
  );

  const ConstraintsPanel = () => (
    <Accordion>
      <AccordionSummary expandIcon={<ExpandMore />}>
        <Typography variant="h6">âš™ï¸ Scheduling Constraints</Typography>
      </AccordionSummary>
      <AccordionDetails>
        <Grid container spacing={3}>
          <Grid item xs={12} sm={6}>
            <FormControlLabel
              control={
                <Switch
                  checked={constraints.avoidBackToBack}
                  onChange={(e) => setConstraints({...constraints, avoidBackToBack: e.target.checked})}
                />
              }
              label="Avoid Back-to-Back Classes"
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <FormControlLabel
              control={
                <Switch
                  checked={constraints.preferMorningSlots}
                  onChange={(e) => setConstraints({...constraints, preferMorningSlots: e.target.checked})}
                />
              }
              label="Prefer Morning Slots"
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              type="number"
              label="Max Hours Per Day"
              value={constraints.maxHoursPerDay}
              onChange={(e) => setConstraints({...constraints, maxHoursPerDay: parseInt(e.target.value)})}
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              type="number"
              label="Min Gap Between Classes (hours)"
              value={constraints.minGapBetweenClasses}
              onChange={(e) => setConstraints({...constraints, minGapBetweenClasses: parseInt(e.target.value)})}
            />
          </Grid>
        </Grid>
      </AccordionDetails>
    </Accordion>
  );

  const ConflictsPanel = () => (
    conflicts.length > 0 && (
      <Card sx={{ mt: 2, border: '2px solid', borderColor: 'error.main' }}>
        <CardContent>
          <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
            <Warning color="error" sx={{ mr: 1 }} />
            <Typography variant="h6" color="error">
              Schedule Conflicts ({conflicts.length})
            </Typography>
          </Box>
          <List dense>
            {conflicts.map((conflict, index) => (
              <ListItem key={index}>
                <ListItemText
                  primary={conflict.message}
                  secondary={`${conflict.day} at ${conflict.slot}`}
                />
              </ListItem>
            ))}
          </List>
        </CardContent>
      </Card>
    )
  );

  return (
    <Box sx={{ p: 3 }}>
      {/* Header */}
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Box>
          <Typography variant="h4" sx={{ color: colorScheme.primary, mb: 1, fontWeight: 'bold' }}>
            ðŸ“… Timetable Management
          </Typography>
          <Typography variant="body1" color="text.secondary">
            Generate and manage academic schedules automatically or manually
          </Typography>
        </Box>
        <Box sx={{ display: 'flex', gap: 1 }}>
          <Button
            variant="outlined"
            startIcon={<Download />}
            onClick={() => exportTimetable('pdf')}
          >
            Export
          </Button>
          <Button
            variant="outlined"
            startIcon={<Print />}
            onClick={() => exportTimetable('print')}
          >
            Print
          </Button>
        </Box>
      </Box>

      {/* Control Panel */}
      <Card sx={{ mb: 3 }} elevation={2}>
        <CardContent>
          <Grid container spacing={3} alignItems="center">
            <Grid item xs={12} md={4}>
              <FormControl fullWidth>
                <InputLabel>Generation Mode</InputLabel>
                <Select
                  value={viewMode}
                  onChange={(e) => setViewMode(e.target.value)}
                  label="Generation Mode"
                >
                  <MenuItem value="auto">
                    <Box sx={{ display: 'flex', alignItems: 'center' }}>
                      <AutoAwesome sx={{ mr: 1 }} />
                      Automatic Generation
                    </Box>
                  </MenuItem>
                  <MenuItem value="manual">
                    <Box sx={{ display: 'flex', alignItems: 'center' }}>
                      <EditCalendar sx={{ mr: 1 }} />
                      Manual Editing
                    </Box>
                  </MenuItem>
                </Select>
              </FormControl>
            </Grid>

            <Grid item xs={12} md={4}>
              <FormControl fullWidth>
                <InputLabel>Department Filter</InputLabel>
                <Select
                  value={selectedDepartment}
                  onChange={(e) => setSelectedDepartment(e.target.value)}
                  label="Department Filter"
                >
                  <MenuItem value="all">All Departments</MenuItem>
                  <MenuItem value="Computer Science">Computer Science</MenuItem>
                  <MenuItem value="Mathematics">Mathematics</MenuItem>
                  <MenuItem value="Physics">Physics</MenuItem>
                </Select>
              </FormControl>
            </Grid>

            <Grid item xs={12} md={4}>
              <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap' }}>
                <Button
                  variant="contained"
                  startIcon={<AutoFixHigh />}
                  onClick={generateTimetableAutomatically}
                  disabled={loading}
                  sx={{ flex: 1 }}
                >
                  {loading ? 'Generating...' : 'Auto Generate'}
                </Button>

                <Tooltip title={editMode ? "Exit Edit Mode" : "Enter Edit Mode"}>
                  <Button
                    variant={editMode ? "contained" : "outlined"}
                    color={editMode ? "secondary" : "primary"}
                    onClick={() => setEditMode(!editMode)}
                    startIcon={<Edit />}
                  >
                    {editMode ? 'Editing' : 'Edit'}
                  </Button>
                </Tooltip>
              </Box>
            </Grid>
          </Grid>

          {/* Progress Bar */}
          {loading && (
            <Box sx={{ mt: 2 }}>
              <Typography variant="body2" gutterBottom>
                Generating timetable... {generationProgress}%
              </Typography>
              <Box sx={{ width: '100%', bgcolor: 'grey.200', borderRadius: 1 }}>
                <Box
                  sx={{
                    width: `${generationProgress}%`,
                    height: 8,
                    bgcolor: 'primary.main',
                    borderRadius: 1,
                    transition: 'width 0.3s ease'
                  }}
                />
              </Box>
            </Box>
          )}
        </CardContent>
      </Card>

      {/* Action Buttons */}
      <Box sx={{ display: 'flex', gap: 1, mb: 3, flexWrap: 'wrap' }}>
        <Tooltip title="Undo">
          <span>
            <IconButton onClick={undo} disabled={historyIndex <= 0}>
              <Undo />
            </IconButton>
          </span>
        </Tooltip>

        <Tooltip title="Redo">
          <span>
            <IconButton onClick={redo} disabled={historyIndex >= history.length - 1}>
              <Redo />
            </IconButton>
          </span>
        </Tooltip>

        <Tooltip title="Clear All">
          <Button
            variant="outlined"
            startIcon={<Clear />}
            onClick={initializeTimetable}
          >
            Clear All
          </Button>
        </Tooltip>

        <Tooltip title="Copy Timetable">
          <Button
            variant="outlined"
            startIcon={<ContentCopy />}
            onClick={() => navigator.clipboard.writeText(JSON.stringify(timetable))}
          >
            Copy
          </Button>
        </Tooltip>
      </Box>

      {/* Constraints Panel */}
      <ConstraintsPanel />

      {/* Conflicts Panel */}
      <ConflictsPanel />

      {/* Timetable Display */}
      <Card sx={{ mt: 3 }} elevation={2}>
        <CardContent>
          <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
            <Typography variant="h6">
              {editMode ? 'âœï¸ Edit Timetable' : 'ðŸ“‹ Generated Timetable'}
            </Typography>
            <Chip
              icon={conflicts.length > 0 ? <Warning /> : <CheckCircle />}
              label={conflicts.length > 0 ? `${conflicts.length} Conflicts` : 'No Conflicts'}
              color={conflicts.length > 0 ? 'error' : 'success'}
              variant="outlined"
            />
          </Box>
          <TimetableGridView />
        </CardContent>
      </Card>

      {/* Statistics */}
      <Grid container spacing={3} sx={{ mt: 2 }}>
        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent>
              <Typography color="text.secondary" gutterBottom>
                Total Scheduled Classes
              </Typography>
              <Typography variant="h4">
                {Object.values(timetable).flatMap(day => Object.values(day)).filter(Boolean).length}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent>
              <Typography color="text.secondary" gutterBottom>
                Available Slots
              </Typography>
              <Typography variant="h4">
                {mockData.days.length * mockData.timeSlots.length - Object.values(timetable).flatMap(day => Object.values(day)).filter(Boolean).length}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent>
              <Typography color="text.secondary" gutterBottom>
                Faculty Utilization
              </Typography>
              <Typography variant="h4">
                {Math.round((Object.values(timetable).flatMap(day => Object.values(day)).filter(Boolean).length / (mockData.faculties.length * 20)) * 100)}%
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item xs={12} sm={6} md={3}>
          <Card>
            <CardContent>
              <Typography color="text.secondary" gutterBottom>
                Classroom Utilization
              </Typography>
              <Typography variant="h4">
                {Math.round((Object.values(timetable).flatMap(day => Object.values(day)).filter(Boolean).length / (mockData.classrooms.length * 40)) * 100)}%
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      {/* Schedule Dialog */}
      <ScheduleDialog />

      {/* Notifications */}
      <Snackbar open={!!error} autoHideDuration={6000} onClose={() => setError(null)}>
        <Alert severity="error" onClose={() => setError(null)}>
          {error}
        </Alert>
      </Snackbar>

      <Snackbar open={!!success} autoHideDuration={4000} onClose={() => setSuccess(null)}>
        <Alert severity="success" onClose={() => setSuccess(null)}>
          {success}
        </Alert>
      </Snackbar>

      {/* Loading Backdrop */}
      <Backdrop open={loading} sx={{ zIndex: 9999 }}>
        <CircularProgress color="inherit" />
      </Backdrop>

      {/* Floating Action Button for Mobile */}
      <Fab
        color="primary"
        aria-label="generate timetable"
        sx={{
          position: 'fixed',
          bottom: 24,
          right: 24,
          display: { xs: 'flex', md: 'none' }
        }}
        onClick={generateTimetableAutomatically}
        disabled={loading}
      >
        {loading ? <CircularProgress size={24} color="inherit" /> : <AutoFixHigh />}
      </Fab>
    </Box>
  );
};

export default TimetableManagementTab;
