            users={users}
            courses={courses}
            colorScheme={colorScheme}
          />
        )}

        {/* System Settings Tab */}
        {activeTab === 7 && (
          <SystemSettingsTab 
            systemStats={systemStats}
            colorScheme={colorScheme}
          />
        )}

      </Container>

      {/* Dialogs */}
      <StatDetailsDialog 
        open={statDetailsDialog} 
        onClose={() => setStatDetailsDialog(false)} 
        stat={selectedStat}
        studentGroups={studentGroups}
        colorScheme={colorScheme}
      />

      <AddUserDialog open={newUserDialog} onClose={() => setNewUserDialog(false)} newUser={newUser} setNewUser={setNewUser} onAdd={addNewUser} colorScheme={colorScheme} />
      <AddCourseDialog open={newCourseDialog} onClose={() => setNewCourseDialog(false)} newCourse={newCourse} setNewCourse={setNewCourse} onAdd={addNewCourse} colorScheme={colorScheme} />
      <AddClassroomDialog open={newClassroomDialog} onClose={() => setNewClassroomDialog(false)} newClassroom={newClassroom} setNewClassroom={setNewClassroom} onAdd={addNewClassroom} colorScheme={colorScheme} />
      <CourseFacultyMappingDialog 
        open={courseFacultyDialog} 
        onClose={() => setCourseFacultyDialog(false)} 
        courses={getUnmappedCourses()}
        faculty={getFacultyMembers()}
        courseMapping={courseMapping}
        setCourseMapping={setCourseMapping}
        onMap={mapCourseToFaculty}
        colorScheme={colorScheme}
      />
    </Box>
  );
};

