import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Paper,
  AppBar,
  Toolbar,
  Button,
  Tabs,
  Tab,
  Box,
  Avatar,
  Badge,
  Chip
} from '@mui/material';
import {
  Dashboard,
  People,
  School,
  Class,
  Schedule,
  Map,
  Analytics,
  Settings,
  AdminPanelSettings,
  Person,
  Group,
  Notifications,
  Logout,
  Refresh
} from '@mui/icons-material';

// Import tab components
import OverviewTab from './tabs/OverviewTab';
import UserManagementTab from './tabs/UserManagementTab';
import CourseManagementTab from './tabs/CourseManagementTab';
import ClassroomManagementTab from './tabs/ClassroomManagementTab';
import TimetableManagementTab from './tabs/TimetableManagementTab';
import CourseFacultyMappingTab from './tabs/CourseFacultyMappingTab';
import ReportsAnalyticsTab from './tabs/ReportsAnalyticsTab';
import SystemSettingsTab from './tabs/SystemSettingsTab';

// Import dialog components
import StatDetailsDialog from './dialogs/StatDetailsDialog';
import AddUserDialog from './dialogs/AddUserDialog';
import AddCourseDialog from './dialogs/AddCourseDialog';
import AddClassroomDialog from './dialogs/AddClassroomDialog';
import CourseFacultyMappingDialog from './dialogs/CourseFacultyMappingDialog';
import CourseDetailsDialog from './dialogs/CourseDetailsDialog';
import EditCourseDialog from './dialogs/EditCourseDialog';

const AdminPortal = ({ user, setUser }) => {
  const [activeTab, setActiveTab] = useState(0);
  const [users, setUsers] = useState([]);
  const [courses, setCourses] = useState([]);
  const [classrooms, setClassrooms] = useState([]);
  const [timetable, setTimetable] = useState([]);
  const [systemStats, setSystemStats] = useState({});
  const [notifications, setNotifications] = useState([]);
  const [recentActivities, setRecentActivities] = useState([]);
  const [studentGroups, setStudentGroups] = useState([]);
  const [realtimeData, setRealtimeData] = useState({
    activeSessions: 0,
    currentTime: new Date(),
    serverLoad: 0,
    onlineUsers: 0
  });

  // Dialog states
  const [selectedStat, setSelectedStat] = useState(null);
  const [statDetailsDialog, setStatDetailsDialog] = useState(false);
  const [newUserDialog, setNewUserDialog] = useState(false);
  const [newCourseDialog, setNewCourseDialog] = useState(false);
  const [newClassroomDialog, setNewClassroomDialog] = useState(false);
  const [courseFacultyDialog, setCourseFacultyDialog] = useState(false);
  const [courseDetailsDialog, setCourseDetailsDialog] = useState(false);
  const [editCourseDialog, setEditCourseDialog] = useState(false);

  // Form states
  const [newUser, setNewUser] = useState({
    name: '', email: '', role: 'student', password: '', 
    department: 'Computer Science', phone: '', semester: 1
  });

  const [newCourse, setNewCourse] = useState({
    code: '', name: '', credits: 3, faculty: '', 
    maxStudents: 50, department: 'Computer Science', 
    description: '', courseType: 'Theory'
  });

  const [newClassroom, setNewClassroom] = useState({
    name: '', capacity: 30, facilities: '', 
    building: 'Main', floor: '1', type: 'Lecture Hall'
  });

  const [courseMapping, setCourseMapping] = useState({
    courseId: '', facultyId: '', semester: 1, schedule: []
  });

  const [selectedCourse, setSelectedCourse] = useState(null);

  // Color scheme
  const colorScheme = {
    primary: '#1976d2',
    primaryLight: '#42a5f5',
    primaryDark: '#1565c0',
    secondary: '#dc004e',
    success: '#2e7d32',
    warning: '#ed6c02',
    error: '#d32f2f',
    info: '#0288d1',
    background: '#f5f5f5'
  };

  // Data loading functions
  const fetchAdminData = async () => {
    try {
      // Your existing data loading logic
      const sampleUsers = [
        { id: 1, name: 'Alice Johnson', email: 'alice@college.edu', role: 'student', status: 'Active', 
          studentId: 'S2023001', department: 'Computer Science', joinDate: '2023-08-15', semester: 3 },
        // ... more users
      ];
      setUsers(sampleUsers);
      // ... set other data
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const loadSystemStats = () => {
    setSystemStats({
      totalStudents: 1254,
      totalFaculty: 48,
      totalCourses: 86,
      totalClassrooms: 32,
      // ... other stats
    });
  };

  // Dialog handlers
  const handleStatClick = (stat) => {
    setSelectedStat(stat);
    setStatDetailsDialog(true);
  };

  const handleViewCourseDetails = (course) => {
    setSelectedCourse(course);
    setCourseDetailsDialog(true);
  };

  const handleEditCourse = (course) => {
    setSelectedCourse(course);
    setEditCourseDialog(true);
  };

  const handleMapCourseFaculty = (course) => {
    setCourseMapping({ ...courseMapping, courseId: course.id });
    setCourseFacultyDialog(true);
  };

  // CRUD operations
  const addNewUser = () => {
    const newUserObj = {
      id: Date.now(),
      ...newUser,
      status: 'Active',
      studentId: newUser.role === 'student' ? `S${2023000 + users.length + 1}` : undefined,
      joinDate: new Date().toISOString().split('T')[0]
    };
    setUsers([...users, newUserObj]);
    setNewUserDialog(false);
    setNewUser({ name: '', email: '', role: 'student', password: '', department: 'Computer Science', phone: '', semester: 1 });
  };

  const addNewCourse = () => {
    const newCourseObj = {
      id: Date.now(),
      ...newCourse,
      enrolled: 0,
      semester: 'Fall 2024',
      faculty: '',
      facultyId: ''
    };
    setCourses([...courses, newCourseObj]);
    setNewCourseDialog(false);
    setNewCourse({ code: '', name: '', credits: 3, faculty: '', maxStudents: 50, department: 'Computer Science', description: '', courseType: 'Theory' });
  };

  const addNewClassroom = () => {
    const newClassroomObj = {
      id: Date.now(),
      ...newClassroom,
      status: 'Available'
    };
    setClassrooms([...classrooms, newClassroomObj]);
    setNewClassroomDialog(false);
    setNewClassroom({ name: '', capacity: 30, facilities: '', building: 'Main', floor: '1', type: 'Lecture Hall' });
  };

  const deleteItem = (id, type) => {
    if (type === 'user') {
      setUsers(users.filter(user => user.id !== id));
    } else if (type === 'course') {
      setCourses(courses.filter(course => course.id !== id));
    } else if (type === 'classroom') {
      setClassrooms(classrooms.filter(classroom => classroom.id !== id));
    }
  };

  const toggleUserStatus = (userId) => {
    setUsers(users.map(user => 
      user.id === userId 
        ? { ...user, status: user.status === 'Active' ? 'Inactive' : 'Active' }
        : user
    ));
  };

  // Utility functions
  const getRoleColor = (role) => {
    switch (role) {
      case 'admin': return 'error';
      case 'faculty': return 'warning';
      case 'student': return 'success';
      default: return 'default';
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'Active': return 'success';
      case 'Inactive': return 'error';
      case 'Available': return 'success';
      default: return 'default';
    }
  };

  const getFacultyMembers = () => users.filter(user => user.role === 'faculty');
  const getUnmappedCourses = () => courses.filter(course => !course.facultyId);

  useEffect(() => {
    fetchAdminData();
    loadSystemStats();

    // Real-time updates
    const realtimeInterval = setInterval(() => {
      setRealtimeData(prev => ({
        activeSessions: Math.floor(Math.random() * 50) + 20,
        currentTime: new Date(),
        serverLoad: Math.floor(Math.random() * 30) + 40,
        onlineUsers: Math.floor(Math.random() * 100) + 150
      }));
    }, 5000);

    return () => clearInterval(realtimeInterval);
  }, []);

  return (
    <Box sx={{ flexGrow: 1, bgcolor: colorScheme.background, minHeight: '100vh' }}>
      {/* Header */}
      <AppBar position="static" elevation={2} sx={{ bgcolor: colorScheme.primary }}>
        <Toolbar>
          <AdminPanelSettings sx={{ mr: 2 }} />
          <Typography variant="h6" component="div" sx={{ flexGrow: 1, fontWeight: 'bold' }}>
            üéì Smart Classroom Admin Portal
          </Typography>

          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
            <Badge badgeContent={notifications.filter(n => !n.read).length} color="error">
              <Notifications sx={{ color: 'white' }} />
            </Badge>
            <Avatar sx={{ width: 32, height: 32, bgcolor: colorScheme.primaryLight }}>
              {user?.name?.charAt(0) || 'A'}
            </Avatar>
            <Typography variant="body2">{user?.name}</Typography>
            <Button color="inherit" startIcon={<Logout />} onClick={() => setUser(null)}>
              Logout
            </Button>
          </Box>
        </Toolbar>
      </AppBar>

      <Container maxWidth="xl" sx={{ mt: 3, mb: 4 }}>
        {/* Real-time Status Bar */}
        <Paper sx={{ 
          p: 2, 
          mb: 3, 
          bgcolor: colorScheme.primary, 
          color: 'white',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center'
        }}>
          <Box sx={{ display: 'flex', alignItems: 'center', gap: 3 }}>
            <Box>
              <Typography variant="caption" sx={{ opacity: 0.9 }}>Current Time</Typography>
              <Typography variant="body1" sx={{ fontWeight: 'bold' }}>
                {new Date().toLocaleTimeString()}
              </Typography>
            </Box>
            <Box>
              <Typography variant="caption" sx={{ opacity: 0.9 }}>System Status</Typography>
              <Typography variant="body1" sx={{ fontWeight: 'bold', color: colorScheme.success }}>
                ‚óè All Systems Operational
              </Typography>
            </Box>
          </Box>
          <Button 
            variant="contained" 
            size="small" 
            sx={{ 
              bgcolor: 'white', 
              color: colorScheme.primary,
              '&:hover': { bgcolor: '#f5f5f5' }
            }}
            startIcon={<Refresh />}
            onClick={fetchAdminData}
          >
            Refresh Data
          </Button>
        </Paper>

        {/* Main Navigation Tabs */}
        <Paper sx={{ width: '100%', mb: 3, borderRadius: 2 }}>
          <Tabs 
            value={activeTab} 
            onChange={(e, newValue) => setActiveTab(newValue)} 
            variant="scrollable" 
            scrollButtons="auto"
          >
            <Tab icon={<Dashboard />} label="Overview" />
            <Tab icon={<People />} label="User Management" />
            <Tab icon={<School />} label="Course Management" />
            <Tab icon={<Class />} label="Classroom Management" />
            <Tab icon={<Schedule />} label="Timetable Management" />
            <Tab icon={<Map />} label="Course-Faculty Mapping" />
            <Tab icon={<Analytics />} label="Reports & Analytics" />
            <Tab icon={<Settings />} label="System Settings" />
          </Tabs>
        </Paper>

        {/* Tab Content */}
        {activeTab === 0 && (
          <OverviewTab 
            advancedStats={[]} // Pass your stats data
            systemStats={systemStats}
            realtimeData={realtimeData}
            onStatClick={handleStatClick}
            onAddUser={() => setNewUserDialog(true)}
            colorScheme={colorScheme}
          />
        )}

        {activeTab === 1 && (
          <UserManagementTab 
            users={users}
            onAddUser={() => setNewUserDialog(true)}
            onDeleteUser={(id) => deleteItem(id, 'user')}
            onToggleStatus={toggleUserStatus}
            getRoleColor={getRoleColor}
            getStatusColor={getStatusColor}
            colorScheme={colorScheme}
          />
        )}

        {activeTab === 2 && (
          <CourseManagementTab 
            courses={courses}
            onAddCourse={() => setNewCourseDialog(true)}
            onDeleteCourse={(id) => deleteItem(id, 'course')}
            onViewDetails={handleViewCourseDetails}
            onEditCourse={handleEditCourse}
            onMapCourse={handleMapCourseFaculty}
            colorScheme={colorScheme}
          />
        )}

        {activeTab === 3 && (
          <ClassroomManagementTab 
            classrooms={classrooms}
            onAddClassroom={() => setNewClassroomDialog(true)}
            onDeleteClassroom={(id) => deleteItem(id, 'classroom')}
            getStatusColor={getStatusColor}
            colorScheme={colorScheme}
          />
        )}

        {/* Add other tabs similarly */}

      </Container>

      {/* Dialogs */}
      <StatDetailsDialog 
        open={statDetailsDialog} 
        onClose={() => setStatDetailsDialog(false)} 
        stat={selectedStat}
        colorScheme={colorScheme}
      />

      <AddUserDialog 
        open={newUserDialog} 
        onClose={() => setNewUserDialog(false)} 
        newUser={newUser} 
        setNewUser={setNewUser} 
        onAdd={addNewUser} 
        colorScheme={colorScheme} 
      />

      <AddCourseDialog 
        open={newCourseDialog} 
        onClose={() => setNewCourseDialog(false)} 
        newCourse={newCourse} 
        setNewCourse={setNewCourse} 
        onAdd={addNewCourse} 
        colorScheme={colorScheme} 
      />

      <AddClassroomDialog 
        open={newClassroomDialog} 
        onClose={() => setNewClassroomDialog(false)} 
        newClassroom={newClassroom} 
        setNewClassroom={setNewClassroom} 
        onAdd={addNewClassroom} 
        colorScheme={colorScheme} 
      />

      <CourseFacultyMappingDialog 
        open={courseFacultyDialog} 
        onClose={() => setCourseFacultyDialog(false)} 
        courses={getUnmappedCourses()}
        faculty={getFacultyMembers()}
        courseMapping={courseMapping}
        setCourseMapping={setCourseMapping}
        onMap={() => {}} // Add your mapping logic
        colorScheme={colorScheme}
      />

      <CourseDetailsDialog 
        open={courseDetailsDialog}
        onClose={() => setCourseDetailsDialog(false)}
        course={selectedCourse}
        colorScheme={colorScheme}
      />

      <EditCourseDialog 
        open={editCourseDialog}
        onClose={() => setEditCourseDialog(false)}
        course={selectedCourse}
        colorScheme={colorScheme}
      />
    </Box>
  );
};

export default AdminPortal;
