import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Paper,
  Grid,
  Card,
  CardContent,
  Button,
  Tabs,
  Tab,
  Box,
  TextField,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Chip,
  IconButton,
  Alert,
  AppBar,
  Toolbar,
  Avatar,
  Switch,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Stepper,
  Step,
  StepLabel,
  StepContent,
  LinearProgress,
  Tooltip,
  CardActions,
  Divider,
  CircularProgress,
  Badge,
  Collapse
} from '@mui/material';
import {
  Add,
  Edit,
  Delete,
  Refresh,
  Logout,
  Dashboard,
  People,
  School,
  Class,
  Schedule,
  Analytics,
  Settings,
  AdminPanelSettings,
  Person,
  Group,
  Computer,
  Map,
  AutoAwesome,
  CheckCircle,
  Warning,
  TrendingUp,
  Notifications,
  CalendarToday,
  BarChart,
  Speed,
  Security,
  Cloud,
  Storage,
  Wifi,
  BugReport,
  ExpandMore,
  ExpandLess,
  ViewList
} from '@mui/icons-material';
import TimetableManagementTab from '../utils/TimetableManagementTab';

const AdminPortal = ({ user, setUser }) => {
  const [activeTab, setActiveTab] = useState(0);
  const [users, setUsers] = useState([]);
  const [courses, setCourses] = useState([]);
  const [classrooms, setClassrooms] = useState([]);
  const [timetable, setTimetable] = useState([]);
  const [systemStats, setSystemStats] = useState({});
  const [generateTimetableData, setGenerateTimetableData] = useState({
    semester: 1,
    academicYear: new Date().getFullYear(),
    optimizeFor: 'no_conflicts'
  });

  // Add these new state variables
  const [selectedStat, setSelectedStat] = useState(null);
  const [statDetailsDialog, setStatDetailsDialog] = useState(false);
  const [studentGroups, setStudentGroups] = useState([]);

  // Dialog states
  const [newUserDialog, setNewUserDialog] = useState(false);
  const [newCourseDialog, setNewCourseDialog] = useState(false);
  const [newClassroomDialog, setNewClassroomDialog] = useState(false);
  const [courseFacultyDialog, setCourseFacultyDialog] = useState(false);
  const [timetableResult, setTimetableResult] = useState(null);
  const [currentView, setCurrentView] = useState('admin');
  const [timetableStep, setTimetableStep] = useState(0);
  const [notifications, setNotifications] = useState([]);
  const [recentActivities, setRecentActivities] = useState([]);

  // Form states
  const [newUser, setNewUser] = useState({
    name: '', email: '', role: 'student', password: '', 
    department: 'Computer Science', phone: '', semester: 1
  });

  const [newCourse, setNewCourse] = useState({
    code: '', name: '', credits: 3, faculty: '', 
    maxStudents: 50, department: 'Computer Science', 
    description: '', courseType: 'Theory'
  });

  const [newClassroom, setNewClassroom] = useState({
    name: '', capacity: 30, facilities: '', 
    building: 'Main', floor: '1', type: 'Lecture Hall'
  });

  const [courseMapping, setCourseMapping] = useState({
    courseId: '', facultyId: '', semester: 1, schedule: []
  });

  // Advanced Quick Stats Data for Overview Tab
  const advancedStats = [
    { 
      icon: <People sx={{ fontSize: 40, mb: 1 }} />, 
      value: "1,254", 
      label: "Total Students",
      color: 'primary.main',
      change: "+5.2%",
      trend: "up",
      description: "Active enrolled students"
    },
    { 
      icon: <Group sx={{ fontSize: 40, mb: 1 }} />, 
      value: "48", 
      label: "Faculty Members",
      color: 'secondary.main',
      change: "+2",
      trend: "up",
      description: "Teaching staff"
    },
    { 
      icon: <School sx={{ fontSize: 40, mb: 1 }} />, 
      value: "86", 
      label: "Active Courses",
      color: 'success.main',
      change: "+3",
      trend: "up",
      description: "This semester"
    },
    { 
      icon: <Class sx={{ fontSize: 40, mb: 1 }} />, 
      value: "32", 
      label: "Classrooms",
      color: 'warning.main',
      change: "100%",
      trend: "stable",
      description: "Available rooms"
    },
    { 
      icon: <CalendarToday sx={{ fontSize: 40, mb: 1 }} />, 
      value: "94%", 
      label: "Attendance",
      color: 'info.main',
      change: "+2.1%",
      trend: "up",
      description: "Average weekly"
    },
    { 
      icon: <BarChart sx={{ fontSize: 40, mb: 1 }} />, 
      value: "A-", 
      label: "Performance",
      color: 'error.main',
      change: "+0.3",
      trend: "up",
      description: "Overall grade"
    }
  ];

  const systemHealthStats = [
    {
      icon: <Cloud sx={{ fontSize: 30 }} />,
      label: "Server Uptime",
      value: "99.8%",
      status: "healthy",
      color: "success.main"
    },
    {
      icon: <Storage sx={{ fontSize: 30 }} />,
      label: "Database",
      value: "64%",
      status: "warning",
      color: "warning.main",
      description: "Storage usage"
    },
    {
      icon: <Wifi sx={{ fontSize: 30 }} />,
      label: "Network",
      value: "Optimal",
      status: "healthy",
      color: "success.main"
    },
    {
      icon: <Security sx={{ fontSize: 30 }} />,
      label: "Security",
      value: "Protected",
      status: "healthy",
      color: "success.main"
    }
  ];

  useEffect(() => {
    fetchAdminData();
    loadSystemStats();
    loadNotifications();
    loadRecentActivities();
    loadStudentGroups();
  }, []);

  const loadStudentGroups = () => {
    const groups = [
      {
        department: 'CSE Core',
        totalStudents: 450,
        male: 280,
        female: 170,
        averageAttendance: 92,
        courses: ['Data Structures', 'Algorithms', 'Database Systems'],
        facultyCount: 12
      },
      {
        department: 'CSE AI & ML',
        totalStudents: 320,
        male: 200,
        female: 120,
        averageAttendance: 94,
        courses: ['Machine Learning', 'Deep Learning', 'Natural Language Processing'],
        facultyCount: 8
      },
      {
        department: 'Data Science',
        totalStudents: 280,
        male: 160,
        female: 120,
        averageAttendance: 91,
        courses: ['Data Analytics', 'Big Data', 'Statistical Methods'],
        facultyCount: 6
      },
      {
        department: 'Cyber Security',
        totalStudents: 204,
        male: 140,
        female: 64,
        averageAttendance: 89,
        courses: ['Network Security', 'Cryptography', 'Ethical Hacking'],
        facultyCount: 5
      }
    ];
    setStudentGroups(groups);
  };

  const handleStatClick = (stat) => {
    setSelectedStat(stat);
    setStatDetailsDialog(true);
  };

  const fetchAdminData = async () => {
    try {
      const token = localStorage.getItem('token');
      const [usersRes, coursesRes, classroomsRes, timetableRes] = await Promise.all([
        fetch('http://localhost:3001/api/admin/users', {
          headers: { 'Authorization': `Bearer ${token}` }
        }),
        fetch('http://localhost:3001/api/admin/courses', {
          headers: { 'Authorization': `Bearer ${token}` }
        }),
        fetch('http://localhost:3001/api/admin/classrooms', {
          headers: { 'Authorization': `Bearer ${token}` }
        }),
        fetch('http://localhost:3001/api/admin/timetable', {
          headers: { 'Authorization': `Bearer ${token}` }
        })
      ]);

      if (usersRes.ok) setUsers(await usersRes.json());
      if (coursesRes.ok) setCourses(await coursesRes.json());
      if (classroomsRes.ok) setClassrooms(await classroomsRes.json());
      if (timetableRes.ok) setTimetable(await timetableRes.json());

    } catch (error) {
      console.error('Error fetching data:', error);
      loadSampleData();
    }
  };

  const loadSystemStats = () => {
    setSystemStats({
      totalStudents: 1254,
      totalFaculty: 48,
      totalCourses: 86,
      totalClassrooms: 32,
      averageAttendance: 85,
      systemUptime: '99.8%',
      activeSessions: 23,
      pendingApprovals: 5,
      timetableGenerated: true,
      conflictsResolved: 12,
      serverLoad: '64%',
      responseTime: '128ms'
    });
  };

  const loadNotifications = () => {
    setNotifications([
      { id: 1, type: 'warning', message: '3 classrooms need maintenance', time: '2 hours ago', read: false },
      { id: 2, type: 'info', message: 'New timetable generated successfully', time: '5 hours ago', read: true },
      { id: 3, type: 'error', message: 'Database backup required', time: '1 day ago', read: false },
      { id: 4, type: 'success', message: 'System update completed', time: '2 days ago', read: true }
    ]);
  };

  const loadRecentActivities = () => {
    setRecentActivities([
      { id: 1, action: 'User Created', user: 'John Doe', time: '10 minutes ago', icon: <Person /> },
      { id: 2, action: 'Course Updated', user: 'Dr. Smith', time: '25 minutes ago', icon: <School /> },
      { id: 3, action: 'Timetable Generated', user: 'System', time: '1 hour ago', icon: <Schedule /> },
      { id: 4, action: 'Classroom Assigned', user: 'Admin', time: '2 hours ago', icon: <Class /> }
    ]);
  };

  const loadSampleData = () => {
    const sampleUsers = [
      { id: 1, name: 'Alice Johnson', email: 'alice@college.edu', role: 'student', status: 'Active', 
        studentId: 'S2023001', department: 'Computer Science', joinDate: '2023-08-15', semester: 3 },
      { id: 2, name: 'Dr. John Smith', email: 'john.smith@college.edu', role: 'faculty', status: 'Active', 
        facultyId: 'F1001', department: 'Computer Science', joinDate: '2020-01-10', specialization: 'Algorithms' },
      { id: 3, name: 'Bob Wilson', email: 'bob@college.edu', role: 'student', status: 'Inactive', 
        studentId: 'S2023002', department: 'Mathematics', joinDate: '2023-08-15', semester: 2 },
      { id: 4, name: 'Dr. Sarah Chen', email: 'sarah.chen@college.edu', role: 'faculty', status: 'Active', 
        facultyId: 'F1002', department: 'Physics', joinDate: '2019-06-20', specialization: 'Quantum Mechanics' },
      { id: 5, name: 'Admin User', email: 'admin@college.edu', role: 'admin', status: 'Active', 
        adminId: 'A1001', department: 'Administration', joinDate: '2018-01-01' },
      { id: 6, name: 'Dr. Robert Brown', email: 'robert.brown@college.edu', role: 'hod', status: 'Active', 
        facultyId: 'F1003', department: 'Computer Science', joinDate: '2018-03-15', specialization: 'Head of Department' },
      { id: 7, name: 'Technical Staff', email: 'tech@college.edu', role: 'staff', status: 'Active', 
        staffId: 'T1001', department: 'IT Support', joinDate: '2022-05-10' },
    ];

    const sampleCourses = [
      { id: 1, code: 'CS101', name: 'Introduction to Programming', credits: 3, 
        faculty: 'Dr. John Smith', facultyId: 'F1001', enrolled: 45, max: 50, 
        department: 'Computer Science', semester: 'Fall 2024', courseType: 'Theory' },
      { id: 2, code: 'MATH201', name: 'Calculus II', credits: 4, 
        faculty: 'Dr. Sarah Johnson', facultyId: 'F1003', enrolled: 38, max: 40, 
        department: 'Mathematics', semester: 'Fall 2024', courseType: 'Theory' },
      { id: 3, code: 'PHY101', name: 'Physics I', credits: 4, 
        faculty: 'Dr. Michael Brown', facultyId: 'F1004', enrolled: 30, max: 35, 
        department: 'Physics', semester: 'Fall 2024', courseType: 'Lab' },
    ];

    const sampleClassrooms = [
      { id: 1, name: 'Room 101', capacity: 50, facilities: 'Projector, AC, Whiteboard', 
        building: 'Main', floor: '1', type: 'Lecture Hall', status: 'Available' },
      { id: 2, name: 'Lab A', capacity: 30, facilities: 'Computers, Projector, Network', 
        building: 'Science', floor: '2', type: 'Computer Lab', status: 'Available' },
      { id: 3, name: 'Auditorium', capacity: 200, facilities: 'Projector, Sound System, AC', 
        building: 'Main', floor: 'G', type: 'Auditorium', status: 'Maintenance' },
    ];

    const sampleTimetable = [
      { id: 1, courseCode: 'CS101', courseName: 'Introduction to Programming', 
        faculty: 'Dr. John Smith', room: 'Room 101', day: 'Monday', 
        startTime: '09:00', endTime: '10:30', semester: 'Fall 2024' },
      { id: 2, courseCode: 'MATH201', courseName: 'Calculus II', 
        faculty: 'Dr. Sarah Johnson', room: 'Room 205', day: 'Tuesday', 
        startTime: '11:00', endTime: '12:30', semester: 'Fall 2024' },
      { id: 3, courseCode: 'PHY101', courseName: 'Physics I', 
        faculty: 'Dr. Michael Brown', room: 'Lab A', day: 'Wednesday', 
        startTime: '14:00', endTime: '15:30', semester: 'Fall 2024' },
    ];

    setUsers(sampleUsers);
    setCourses(sampleCourses);
    setClassrooms(sampleClassrooms);
    setTimetable(sampleTimetable);
  };

  const generateTimetable = async (optimizeFor = 'no_conflicts') => {
    try {
      setTimetableResult({ loading: true, message: 'Starting timetable generation...' });
      setTimetableStep(0);

      // Simulate timetable generation steps
      const steps = [
        'Analyzing course data...',
        'Checking faculty availability...',
        'Allocating classrooms...',
        'Resolving conflicts...',
        'Optimizing schedule...',
        'Finalizing timetable...'
      ];

      for (let i = 0; i < steps.length; i++) {
        setTimetableResult({ loading: true, message: steps[i] });
        setTimetableStep(i);
        await new Promise(resolve => setTimeout(resolve, 1000));
      }

      const result = {
        success: true,
        message: 'Timetable generated successfully!',
        conflicts: 3,
        scheduledClasses: 42,
        totalClasses: 45,
        optimization: '95% efficient',
        details: [
          { course: 'CS101', room: 'Room 101', time: 'Mon 9:00-10:30', faculty: 'Dr. John Smith' },
          { course: 'MATH201', room: 'Room 205', time: 'Tue 11:00-12:30', faculty: 'Dr. Sarah Johnson' },
          { course: 'PHY101', room: 'Lab A', time: 'Wed 2:00-3:30', faculty: 'Dr. Michael Brown' },
        ]
      };

      setTimetableResult(result);
      setTimetableStep(6);

    } catch (error) {
      setTimetableResult({
        success: false,
        message: 'Error generating timetable. Please try again.'
      });
    }
  };

  const mapCourseToFaculty = () => {
    const { courseId, facultyId, semester } = courseMapping;
    const course = courses.find(c => c.id == courseId);
    const faculty = users.find(u => u.id == facultyId);

    if (course && faculty) {
      setCourses(courses.map(c => 
        c.id == courseId 
          ? { ...c, faculty: faculty.name, facultyId: faculty.facultyId }
          : c
      ));
      setCourseFacultyDialog(false);
      setCourseMapping({ courseId: '', facultyId: '', semester: 1, schedule: [] });
      alert(`Successfully mapped ${course.code} to ${faculty.name}`);
    }
  };

  const addNewUser = () => {
    const newUserObj = {
      id: Date.now(),
      ...newUser,
      status: 'Active',
      studentId: newUser.role === 'student' ? `S${2023000 + users.length + 1}` : undefined,
      facultyId: newUser.role === 'faculty' ? `F${1000 + users.length + 1}` : undefined,
      adminId: newUser.role === 'admin' ? `A${1000 + users.length + 1}` : undefined,
      staffId: newUser.role === 'staff' ? `T${1000 + users.length + 1}` : undefined,
      joinDate: new Date().toISOString().split('T')[0]
    };
    setUsers([...users, newUserObj]);
    setNewUserDialog(false);
    resetNewUserForm();
  };

  const addNewCourse = () => {
    const newCourseObj = {
      id: Date.now(),
      ...newCourse,
      enrolled: 0,
      semester: 'Fall 2024',
      faculty: '',
      facultyId: ''
    };
    setCourses([...courses, newCourseObj]);
    setNewCourseDialog(false);
    resetNewCourseForm();
  };

  const addNewClassroom = () => {
    const newClassroomObj = {
      id: Date.now(),
      ...newClassroom,
      status: 'Available'
    };
    setClassrooms([...classrooms, newClassroomObj]);
    setNewClassroomDialog(false);
    resetNewClassroomForm();
  };

  const deleteItem = (id, type) => {
    if (type === 'user') {
      setUsers(users.filter(user => user.id !== id));
    } else if (type === 'course') {
      setCourses(courses.filter(course => course.id !== id));
    } else if (type === 'classroom') {
      setClassrooms(classrooms.filter(classroom => classroom.id !== id));
    } else if (type === 'timetable') {
      setTimetable(timetable.filter(item => item.id !== id));
    }
  };

  const toggleUserStatus = (userId) => {
    setUsers(users.map(user => 
      user.id === userId 
        ? { ...user, status: user.status === 'Active' ? 'Inactive' : 'Active' }
        : user
    ));
  };

  // Form resets
  const resetNewUserForm = () => setNewUser({ name: '', email: '', role: 'student', password: '', department: 'Computer Science', phone: '', semester: 1 });
  const resetNewCourseForm = () => setNewCourse({ code: '', name: '', credits: 3, faculty: '', maxStudents: 50, department: 'Computer Science', description: '', courseType: 'Theory' });
  const resetNewClassroomForm = () => setNewClassroom({ name: '', capacity: 30, facilities: '', building: 'Main', floor: '1', type: 'Lecture Hall' });

  // Role switching
  const switchToFacultyView = () => setCurrentView('faculty');
  const switchToStudentView = () => setCurrentView('student');
  const switchToAdminView = () => setCurrentView('admin');

  // Logout
  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    window.location.href = '/login';
  };

  // Utility functions
  const getRoleColor = (role) => {
    switch (role) {
      case 'admin': return 'error';
      case 'faculty': return 'warning';
      case 'student': return 'success';
      case 'hod': return 'info';
      case 'staff': return 'secondary';
      default: return 'default';
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'Active': return 'success';
      case 'Inactive': return 'error';
      case 'Maintenance': return 'warning';
      case 'Available': return 'success';
      default: return 'default';
    }
  };

  const getFacultyMembers = () => users.filter(user => user.role === 'faculty');
  const getUnmappedCourses = () => courses.filter(course => !course.facultyId);

  // Render different views based on currentView
  if (currentView === 'faculty') {
    return <FacultyView onSwitchToAdmin={switchToAdminView} />;
  }

  if (currentView === 'student') {
    return <StudentView onSwitchToAdmin={switchToAdminView} />;
  }

  // Main Admin View
  return (
    <Box sx={{ flexGrow: 1 }}>
      {/* Header */}
      <AppBar position="static" elevation={2}>
        <Toolbar>
          <AdminPanelSettings sx={{ mr: 2 }} />
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Smart Classroom Admin Portal
          </Typography>

          <Box sx={{ mr: 3, display: 'flex', gap: 1 }}>
            <Button color="inherit" startIcon={<Person />} onClick={switchToStudentView} variant="outlined" size="small">
              Student View
            </Button>
            <Button color="inherit" startIcon={<Group />} onClick={switchToFacultyView} variant="outlined" size="small">
              Faculty View
            </Button>
          </Box>

          <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
            <Badge badgeContent={notifications.filter(n => !n.read).length} color="error">
              <Notifications sx={{ color: 'white' }} />
            </Badge>
            <Avatar sx={{ width: 32, height: 32, bgcolor: 'secondary.main' }}>
              {user?.name?.charAt(0) || 'A'}
            </Avatar>
            <Typography variant="body2">{user?.name}</Typography>
            <Button color="inherit" startIcon={<Logout />} onClick={handleLogout}>
              Logout
            </Button>
          </Box>
        </Toolbar>
      </AppBar>

      <Container maxWidth="xl" sx={{ mt: 3, mb: 4 }}>
        {/* Main Navigation Tabs */}
        <Paper sx={{ width: '100%', mb: 3, borderRadius: 2 }}>
          <Tabs value={activeTab} onChange={(e, newValue) => setActiveTab(newValue)} variant="scrollable" scrollButtons="auto">
            <Tab icon={<Dashboard />} label="Overview" />
            <Tab icon={<People />} label="User Management" />
            <Tab icon={<School />} label="Course Management" />
            <Tab icon={<Class />} label="Classroom Management" />
            <Tab icon={<Schedule />} label="Timetable Management" />
            <Tab icon={<Map />} label="Course-Faculty Mapping" />
            <Tab icon={<Analytics />} label="Reports & Analytics" />
            <Tab icon={<Settings />} label="System Settings" />
          </Tabs>
        </Paper>

        {/* Overview Tab with Advanced Stats */}
        {activeTab === 0 && (
          <OverviewTab 
            advancedStats={advancedStats}
            systemHealthStats={systemHealthStats}
            systemStats={systemStats}
            notifications={notifications}
            recentActivities={recentActivities}
            onAddUser={() => setNewUserDialog(true)}
            onGenerateTimetable={generateTimetable}
            onStatClick={handleStatClick}
          />
        )}

        {/* User Management Tab */}
        {activeTab === 1 && (
          <UserManagementTab 
            users={users} 
            onRefresh={fetchAdminData}
            onAddUser={() => setNewUserDialog(true)}
            onEditUser={(user) => {/* Implement edit */}}
            onDeleteUser={(id) => deleteItem(id, 'user')}
            onToggleStatus={toggleUserStatus}
            getRoleColor={getRoleColor}
            getStatusColor={getStatusColor}
          />
        )}

        {/* Course Management Tab */}
        {activeTab === 2 && (
          <CourseManagementTab 
            courses={courses} 
            onRefresh={fetchAdminData}
            onAddCourse={() => setNewCourseDialog(true)}
            onDeleteCourse={(id) => deleteItem(id, 'course')}
            onMapCourse={() => setCourseFacultyDialog(true)}
          />
        )}

        {/* Classroom Management Tab */}
        {activeTab === 3 && (
          <ClassroomManagementTab 
            classrooms={classrooms} 
            onRefresh={fetchAdminData}
            onAddClassroom={() => setNewClassroomDialog(true)}
            onDeleteClassroom={(id) => deleteItem(id, 'classroom')}
            getStatusColor={getStatusColor}
          />
        )}

        {/* Timetable Management Tab */}
        {activeTab === 4 && (
          <TimetableManagementTab 
            timetable={timetable}
            generateTimetableData={generateTimetableData}
            setGenerateTimetableData={setGenerateTimetableData}
            timetableResult={timetableResult}
            timetableStep={timetableStep}
            onGenerateTimetable={generateTimetable}
            onDeleteTimetable={(id) => deleteItem(id, 'timetable')}
          />
        )}

        {/* Course-Faculty Mapping Tab */}
        {activeTab === 5 && (
          <CourseFacultyMappingTab 
            courses={courses}
            faculty={getFacultyMembers()}
            onMapCourse={() => setCourseFacultyDialog(true)}
          />
        )}

        {/* Reports & Analytics Tab */}
        {activeTab === 6 && (
          <ReportsAnalyticsTab 
            systemStats={systemStats}
            users={users}
            courses={courses}
          />
        )}

        {/* System Settings Tab */}
        {activeTab === 7 && (
          <SystemSettingsTab 
            systemStats={systemStats}
          />
        )}

      </Container>

      {/* Dialogs */}
      <StatDetailsDialog 
        open={statDetailsDialog} 
        onClose={() => setStatDetailsDialog(false)} 
        stat={selectedStat}
        studentGroups={studentGroups}
      />

      <AddUserDialog open={newUserDialog} onClose={() => setNewUserDialog(false)} newUser={newUser} setNewUser={setNewUser} onAdd={addNewUser} />
      <AddCourseDialog open={newCourseDialog} onClose={() => setNewCourseDialog(false)} newCourse={newCourse} setNewCourse={setNewCourse} onAdd={addNewCourse} />
      <AddClassroomDialog open={newClassroomDialog} onClose={() => setNewClassroomDialog(false)} newClassroom={newClassroom} setNewClassroom={setNewClassroom} onAdd={addNewClassroom} />
      <CourseFacultyMappingDialog 
        open={courseFacultyDialog} 
        onClose={() => setCourseFacultyDialog(false)} 
        courses={getUnmappedCourses()}
        faculty={getFacultyMembers()}
        courseMapping={courseMapping}
        setCourseMapping={setCourseMapping}
        onMap={mapCourseToFaculty}
      />
    </Box>
  );
};

