}) => {
  const [expandedGroups, setExpandedGroups] = useState({});
  const [viewMode, setViewMode] = useState('groups'); // 'groups' or 'table'

  // Group users by role
  const groupedUsers = users.reduce((groups, user) => {
    const role = user.role;
    if (!groups[role]) {
      groups[role] = [];
    }
    groups[role].push(user);
    return groups;
  }, {});

  // Define group order and colors
  const groupConfig = {
    'admin': { color: 'error', icon: 'ðŸ‘‘', label: 'Admin' },
    'hod': { color: 'info', icon: 'ðŸŽ“', label: 'HOD' },
    'faculty': { color: 'warning', icon: 'ðŸ‘¨â€ðŸ«', label: 'Faculty' },
    'student': { color: 'success', icon: 'ðŸŽ’', label: 'Student' },
    'staff': { color: 'secondary', icon: 'ðŸ‘¨â€ðŸ’¼', label: 'Staff' }
  };

  const toggleGroup = (groupName) => {
    setExpandedGroups(prev => ({
      ...prev,
      [groupName]: !prev[groupName]
    }));
  };

  const GroupCard = ({ groupName, users }) => {
    const config = groupConfig[groupName] || { color: 'default', icon: 'ðŸ‘¤', label: groupName };

    return (
      <Card 
        elevation={2}
        sx={{ 
          borderLeft: `4px solid`,
          borderColor: `${config.color}.main`,
          mb: 2
        }}
      >
        <CardContent sx={{ p: 0 }}>
          {/* Group Header */}
          <Box
            sx={{
              display: 'flex',
              justifyContent: 'space-between',
              alignItems: 'center',
              p: 2,
              cursor: 'pointer',
              backgroundColor: 'background.default'
            }}
            onClick={() => toggleGroup(groupName)}
          >
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
              <Avatar sx={{ bgcolor: `${config.color}.main`, width: 48, height: 48 }}>
                <Typography variant="h6">{config.icon}</Typography>
              </Avatar>
              <Box>
                <Typography variant="h6" fontWeight="bold">
                  {config.label}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  {users.length} {users.length === 1 ? 'user' : 'users'}
                </Typography>
              </Box>
            </Box>
            <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
              <Chip 
                label={config.label} 
                color={config.color} 
                size="small" 
              />
              {expandedGroups[groupName] ? <ExpandLess /> : <ExpandMore />}
            </Box>
          </Box>

          {/* Group Users Table */}
          <Collapse in={expandedGroups[groupName]}>
            <TableContainer component={Paper} variant="outlined">
              <Table size="small">
                <TableHead>
                  <TableRow>
                    <TableCell sx={{ fontWeight: 'bold' }}>Name</TableCell>
                    <TableCell sx={{ fontWeight: 'bold' }}>Email</TableCell>
                    <TableCell sx={{ fontWeight: 'bold' }}>ID</TableCell>
                    <TableCell sx={{ fontWeight: 'bold' }}>Department</TableCell>
                    <TableCell sx={{ fontWeight: 'bold' }}>Status</TableCell>
                    <TableCell sx={{ fontWeight: 'bold' }}>Actions</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {users.map((user) => (
                    <TableRow key={user.id} hover>
                      <TableCell>
                        <Box sx={{ display: 'flex', alignItems: 'center' }}>
                          <Avatar sx={{ width: 32, height: 32, mr: 2, bgcolor: 'primary.main' }}>
                            {user.name.charAt(0)}
                          </Avatar>
                          {user.name}
                        </Box>
                      </TableCell>
                      <TableCell>{user.email}</TableCell>
                      <TableCell>
                        {user.studentId || user.facultyId || user.adminId || user.staffId || 'N/A'}
                      </TableCell>
                      <TableCell>{user.department}</TableCell>
                      <TableCell>
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                          <Chip label={user.status} color={getStatusColor(user.status)} size="small" />
                          <Switch 
                            checked={user.status === 'Active'} 
                            onChange={() => onToggleStatus(user.id)} 
                            size="small" 
                          />
                        </Box>
                      </TableCell>
                      <TableCell>
                        <IconButton color="primary" size="small">
                          <Edit />
                        </IconButton>
                        <IconButton 
                          color="error" 
                          size="small" 
                          onClick={() => onDeleteUser(user.id)}
                        >
                          <Delete />
                        </IconButton>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </Collapse>
        </CardContent>
      </Card>
    );
  };

  const TableView = () => (
    <TableContainer component={Paper} elevation={2}>
      <Table>
        <TableHead sx={{ bgcolor: 'primary.main' }}>
          <TableRow>
            <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>Name</TableCell>
            <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>Email</TableCell>
            <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>ID</TableCell>
            <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>Role</TableCell>
            <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>Department</TableCell>
            <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>Status</TableCell>
            <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>Actions</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {users.map((user) => (
            <TableRow key={user.id} hover>
              <TableCell>
                <Box sx={{ display: 'flex', alignItems: 'center' }}>
                  <Avatar sx={{ width: 32, height: 32, mr: 2, bgcolor: 'primary.main' }}>
                    {user.name.charAt(0)}
                  </Avatar>
                  {user.name}
                </Box>
              </TableCell>
              <TableCell>{user.email}</TableCell>
              <TableCell>
                {user.studentId || user.facultyId || user.adminId || user.staffId || 'N/A'}
              </TableCell>
              <TableCell>
                <Chip label={user.role} color={getRoleColor(user.role)} size="small" />
              </TableCell>
              <TableCell>{user.department}</TableCell>
              <TableCell>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                  <Chip label={user.status} color={getStatusColor(user.status)} size="small" />
                  <Switch 
                    checked={user.status === 'Active'} 
                    onChange={() => onToggleStatus(user.id)} 
                    size="small" 
                  />
                </Box>
              </TableCell>
              <TableCell>
                <IconButton color="primary" size="small">
                  <Edit />
                </IconButton>
                <IconButton 
                  color="error" 
                  size="small" 
                  onClick={() => onDeleteUser(user.id)}
                >
                  <Delete />
                </IconButton>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );

  return (
    <Box>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h4">ðŸ‘¥ User Management</Typography>
        <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
          <Tabs 
            value={viewMode} 
            onChange={(e, newValue) => setViewMode(newValue)}
            sx={{ mr: 2 }}
          >
            <Tab 
              icon={<Dashboard />} 
              label="Group View" 
              value="groups" 
              iconPosition="start"
            />
            <Tab 
              icon={<ViewList />} 
              label="Table View" 
              value="table" 
              iconPosition="start"
            />
          </Tabs>
          <Button variant="outlined" startIcon={<Refresh />} onClick={onRefresh} sx={{ mr: 1 }}>
            Refresh
          </Button>
          <Button variant="contained" startIcon={<Add />} onClick={onAddUser}>
            Add New User
          </Button>
        </Box>
      </Box>

      {viewMode === 'groups' ? (
        <>
          {/* Group cards will show directly without the overview section */}
          {Object.entries(groupedUsers).map(([groupName, groupUsers]) => (
            <GroupCard 
              key={groupName} 
              groupName={groupName} 
              users={groupUsers} 
            />
          ))}
        </>
      ) : (
        <TableView />
      )}
    </Box>
  );
};
