import { userAPI } from './api';

// User service with business logic
export const userService = {
  // Get all users with error handling
  fetchUsers: async () => {
    try {
      const users = await userAPI.getUsers();
      return { success: true, data: users };
    } catch (error) {
      return { success: false, error: error.message };
    }
  },

  // Add new user with validation
  createUser: async (userData) => {
    try {
      // Basic validation
      if (!userData.name || !userData.email || !userData.role) {
        return { success: false, error: 'Name, email, and role are required' };
      }

      // Email validation
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (!emailRegex.test(userData.email)) {
        return { success: false, error: 'Please enter a valid email address' };
      }

      const result = await userAPI.addUser(userData);
      return { success: true, data: result };
    } catch (error) {
      return { success: false, error: error.message };
    }
  },

  // Update user
  updateUser: async (id, userData) => {
    try {
      if (!id) {
        return { success: false, error: 'User ID is required' };
      }

      const result = await userAPI.updateUser(id, userData);
      return { success: true, data: result };
    } catch (error) {
      return { success: false, error: error.message };
    }
  },

  // Delete user
  deleteUser: async (id) => {
    try {
      if (!id) {
        return { success: false, error: 'User ID is required' };
      }

      const result = await userAPI.deleteUser(id);
      return { success: true, data: result };
    } catch (error) {
      return { success: false, error: error.message };
    }
  },

  // Toggle user status
  toggleUserStatus: async (id, currentStatus) => {
    try {
      if (!id) {
        return { success: false, error: 'User ID is required' };
      }

      const newStatus = currentStatus === 'Active' ? 'Inactive' : 'Active';
      const result = await userAPI.updateUserStatus(id, newStatus);
      return { success: true, data: { ...result, newStatus } };
    } catch (error) {
      return { success: false, error: error.message };
    }
  },

  // Health check
  checkHealth: async () => {
    try {
      const result = await userAPI.healthCheck();
      return { success: true, data: result };
    } catch (error) {
      return { success: false, error: error.message };
    }
  }
};
