import React, { useState, useEffect, useRef } from 'react';
import {
  Container,
  Typography,
  Paper,
  Grid,
  Card,
  CardContent,
  Button,
  Tabs,
  Tab,
  Box,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  FormControl,
  Select,
  MenuItem,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  LinearProgress,
  Alert,
  Snackbar,
  AppBar,
  Toolbar,
  IconButton,
  TextField,
  Avatar,
  Divider,
  InputLabel,
  OutlinedInput,
  InputAdornment
} from '@mui/material';
import { 
  QrCode2, 
  Face, 
  Groups, 
  Schedule, 
  Analytics, 
  Refresh,
  Download,
  Print,
  CameraAlt,
  Stop,
  PlayArrow,
  CheckCircle,
  Logout,
  AccountCircle,
  Edit,
  Save,
  Cancel,
  Email,
  Phone,
  LocationOn,
  Person
} from '@mui/icons-material';
import { QRCodeSVG } from 'qrcode.react';

// API Service for backend communication
const apiService = {
  baseUrl: process.env.REACT_APP_API_URL || 'http://localhost:5000/api',

  async request(endpoint, options = {}) {
    const token = localStorage.getItem('token');
    const config = {
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      },
      ...options,
    };

    if (config.body && typeof config.body === 'object') {
      config.body = JSON.stringify(config.body);
    }

    try {
      const response = await fetch(`${this.baseUrl}${endpoint}`, config);
      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Request failed');
      }

      return data;
    } catch (error) {
      console.error('API request failed:', error);
      throw error;
    }
  },

  // Faculty endpoints
  async getFacultyProfile() {
    return this.request('/faculty/profile');
  },

  async updateFacultyProfile(profileData) {
    return this.request('/faculty/profile', {
      method: 'PUT',
      body: profileData,
    });
  },

  // Course endpoints
  async getFacultyCourses() {
    return this.request('/faculty/courses');
  },

  async getCourseStudents(courseId) {
    return this.request(`/courses/${courseId}/students`);
  },

  // Attendance endpoints
  async startAttendanceSession(sessionData) {
    return this.request('/attendance/sessions', {
      method: 'POST',
      body: sessionData,
    });
  },

  async markAttendance(attendanceData) {
    return this.request('/attendance/mark', {
      method: 'POST',
      body: attendanceData,
    });
  },

  async getAttendanceReports(courseId, date) {
    const params = new URLSearchParams();
    if (courseId) params.append('courseId', courseId);
    if (date) params.append('date', date);

    return this.request(`/attendance/reports?${params}`);
  },

  // Face recognition endpoints
  async registerFace(imageData) {
    return this.request('/face/register', {
      method: 'POST',
      body: { image: imageData },
    });
  },

  async recognizeFace(imageData) {
    return this.request('/face/recognize', {
      method: 'POST',
      body: { image: imageData },
    });
  },
};

// Enhanced Real Face Recognition Component
const RealFaceRecognition = ({ onAttendanceMarked, isActive, setSnackbar, course }) => {
  const [isScanning, setIsScanning] = useState(false);
  const [detectedFaces, setDetectedFaces] = useState([]);
  const [isProcessing, setIsProcessing] = useState(false);
  const videoRef = useRef(null);
  const streamRef = useRef(null);
  const canvasRef = useRef(null);
  const detectionIntervalRef = useRef(null);

  const startFaceRecognition = async () => {
    try {
      setIsScanning(true);
      setDetectedFaces([]);

      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: { 
          facingMode: 'user',
          width: { ideal: 640 },
          height: { ideal: 480 }
        } 
      });

      streamRef.current = stream;

      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        await videoRef.current.play();
      }

      // Start face detection interval
      detectionIntervalRef.current = setInterval(captureAndDetectFace, 3000);

    } catch (error) {
      console.error('Error accessing camera:', error);
      setSnackbar({
        open: true,
        message: 'Cannot access camera. Please check permissions.',
        severity: 'error'
      });
      setIsScanning(false);
    }
  };

  const captureAndDetectFace = async () => {
    if (!isScanning || isProcessing) return;

    try {
      setIsProcessing(true);

      // Capture image from video stream
      const canvas = canvasRef.current;
      const video = videoRef.current;

      if (canvas && video) {
        const context = canvas.getContext('2d');
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;
        context.drawImage(video, 0, 0, canvas.width, canvas.height);

        // Convert to base64 for API
        const imageData = canvas.toDataURL('image/jpeg');

        // Send to face recognition API
        const result = await apiService.recognizeFace(imageData);

        if (result.success && result.student) {
          const student = result.student;

          // Check if student already detected
          if (!detectedFaces.find(s => s.student_id === student.student_id)) {
            setDetectedFaces(prev => [...prev, student]);

            // Mark attendance
            await onAttendanceMarked(
              student.student_id, 
              'Present', 
              'Face Recognition',
              student.name
            );

            setSnackbar({
              open: true,
              message: `Face recognized: ${student.name}`,
              severity: 'success'
            });
          }
        }
      }
    } catch (error) {
      console.error('Face recognition error:', error);
      // Continue scanning even if one detection fails
    } finally {
      setIsProcessing(false);
    }
  };

  const stopFaceRecognition = () => {
    setIsScanning(false);
    setIsProcessing(false);

    if (detectionIntervalRef.current) {
      clearInterval(detectionIntervalRef.current);
      detectionIntervalRef.current = null;
    }

    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }

    if (videoRef.current) {
      videoRef.current.srcObject = null;
    }
  };

  useEffect(() => {
    return () => {
      stopFaceRecognition();
    };
  }, []);

  return (
    <Paper sx={{ p: 3, border: '1px solid #1976d2' }}>
      <Typography variant="h6" gutterBottom color="primary">
        <CameraAlt sx={{ mr: 1 }} />
        Real-time Face Recognition
      </Typography>

      <Box sx={{ 
        width: '100%', 
        height: 300,
        border: '2px solid #1976d2',
        borderRadius: 2,
        overflow: 'hidden',
        mx: 'auto',
        my: 2,
        backgroundColor: '#000',
        position: 'relative'
      }}>
        {isScanning ? (
          <video
            ref={videoRef}
            style={{
              width: '100%',
              height: '100%',
              objectFit: 'cover'
            }}
            playsInline
            muted
          />
        ) : (
          <Box sx={{ 
            display: 'flex', 
            alignItems: 'center', 
            justifyContent: 'center', 
            height: '100%',
            color: 'white'
          }}>
            <Typography variant="body1" align="center">
              Camera feed will appear here
              <br />
              when recognition starts
            </Typography>
          </Box>
        )}

        {isScanning && (
          <Box sx={{
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            background: 'rgba(0,0,0,0.3)'
          }}>
            <Typography variant="h6" color="white">
              {isProcessing ? 'ðŸ”„ Processing...' : 'ðŸ” Scanning for faces...'}
            </Typography>
          </Box>
        )}
      </Box>

      {/* Hidden canvas for image capture */}
      <canvas ref={canvasRef} style={{ display: 'none' }} />

      <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center', mb: 3 }}>
        <Button
          variant="contained"
          startIcon={<PlayArrow />}
          onClick={startFaceRecognition}
          disabled={isScanning}
          color="primary"
        >
          Start Recognition
        </Button>
        <Button
          variant="outlined"
          startIcon={<Stop />}
          onClick={stopFaceRecognition}
          disabled={!isScanning}
          color="primary"
        >
          Stop Recognition
        </Button>
      </Box>

      <Alert severity="info" sx={{ mb: 2, bgcolor: '#e3f2fd' }}>
        Make sure students are facing the camera clearly. The system will automatically detect and mark attendance.
      </Alert>

      {detectedFaces.length > 0 && (
        <Box>
          <Typography variant="subtitle1" gutterBottom color="primary">
            âœ… Recognized Students ({detectedFaces.length})
          </Typography>
          <List dense>
            {detectedFaces.map((student, index) => (
              <ListItem key={`${student.student_id}-${index}`}>
                <ListItemIcon>
                  <Avatar sx={{ width: 32, height: 32, bgcolor: 'primary.main' }}>
                    <Person />
                  </Avatar>
                </ListItemIcon>
                <ListItemText 
                  primary={student.name}
                  secondary={`ID: ${student.student_id} - Marked Present`}
                />
                <CheckCircle color="primary" />
              </ListItem>
            ))}
          </List>
        </Box>
      )}
    </Paper>
  );
};

// Enhanced QR Attendance Component
const QRAttendanceComponent = ({ 
  qrCodeHash, 
  onRefresh, 
  onSimulateScan, 
  scannedCount, 
  isActive,
  course,
  sessionTimeLeft 
}) => {
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const qrValue = qrCodeHash ? JSON.stringify({
    type: 'attendance',
    courseCode: course?.course_code,
    courseName: course?.course_name,
    sessionId: qrCodeHash,
    timestamp: new Date().toISOString(),
    institution: 'Smart Classroom System'
  }) : '';

  return (
    <Paper sx={{ p: 3, textAlign: 'center', border: '1px solid #1976d2' }}>
      <Typography variant="h6" gutterBottom color="primary">
        <QrCode2 sx={{ mr: 1 }} />
        QR Code Attendance
      </Typography>

      <Box sx={{ 
        width: 256, 
        height: 256, 
        mx: 'auto', 
        my: 3,
        border: '2px solid #1976d2',
        borderRadius: 2,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#ffffff',
        overflow: 'hidden',
        p: 2
      }}>
        {qrCodeHash ? (
          <QRCodeSVG 
            value={qrValue}
            size={200}
            level="H"
            includeMargin={true}
            bgColor="#FFFFFF"
            fgColor="#1976d2"
          />
        ) : (
          <Typography variant="body2" color="textSecondary">
            Generate QR Code to start
          </Typography>
        )}
      </Box>

      <Box sx={{ mb: 3 }}>
        <Typography variant="body2" color="textSecondary" gutterBottom>
          Students should scan this QR code with their mobile devices
        </Typography>
        <Typography variant="caption" color="textSecondary">
          Session: {isActive ? 'Active' : 'Inactive'} â€¢ Scanned: {scannedCount}
        </Typography>
        {isActive && sessionTimeLeft && (
          <Typography variant="caption" color="primary" display="block" sx={{ mt: 1, fontWeight: 'bold' }}>
            â° Expires in: {formatTime(sessionTimeLeft)}
          </Typography>
        )}
        {qrCodeHash && (
          <Typography variant="caption" color="textSecondary" display="block" sx={{ mt: 1 }}>
            Course: {course?.course_code} â€¢ Session ID: {qrCodeHash.substring(0, 8)}...
          </Typography>
        )}
      </Box>

      <Box sx={{ display: 'flex', gap: 2, justifyContent: 'center', flexWrap: 'wrap' }}>
        <Button
          variant="contained"
          startIcon={<Refresh />}
          onClick={onRefresh}
          disabled={!isActive}
          color="primary"
        >
          Refresh QR
        </Button>
        <Button
          variant="outlined"
          onClick={onSimulateScan}
          disabled={!isActive}
          color="primary"
        >
          Simulate Student Scan
        </Button>
      </Box>

      <Alert severity="info" sx={{ mt: 2, bgcolor: '#e3f2fd' }}>
        QR Code will expire in 5 minutes. Students must scan during active session.
      </Alert>

      {isActive && (
        <Box sx={{ mt: 2, p: 2, backgroundColor: '#e3f2fd', borderRadius: 1, border: '1px solid #1976d2' }}>
          <Typography variant="body2" color="primary" sx={{ fontWeight: 'bold' }}>
            âœ… QR Session Active - Students can scan now
          </Typography>
        </Box>
      )}
    </Paper>
  );
};

// Faculty Profile Component
const FacultyProfile = ({ faculty, onUpdateProfile, setSnackbar }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editedProfile, setEditedProfile] = useState(faculty);
  const [loading, setLoading] = useState(false);

  const handleEdit = () => {
    setIsEditing(true);
    setEditedProfile({ ...faculty });
  };

  const handleCancel = () => {
    setIsEditing(false);
    setEditedProfile(faculty);
  };

  const handleSave = async () => {
    try {
      setLoading(true);
      await onUpdateProfile(editedProfile);
      setIsEditing(false);
      setSnackbar({
        open: true,
        message: 'Profile updated successfully!',
        severity: 'success'
      });
    } catch (error) {
      setSnackbar({
        open: true,
        message: 'Failed to update profile. Please try again.',
        severity: 'error'
      });
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (field, value) => {
    setEditedProfile(prev => ({
      ...prev,
      [field]: value
    }));
  };

  return (
    <Paper sx={{ p: 3, border: '1px solid #1976d2' }}>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h5" color="primary.main">
          Faculty Profile
        </Typography>
        {!isEditing ? (
          <Button
            startIcon={<Edit />}
            onClick={handleEdit}
            variant="outlined"
            color="primary"
          >
            Edit Profile
          </Button>
        ) : (
          <Box sx={{ display: 'flex', gap: 1 }}>
            <Button
              startIcon={<Cancel />}
              onClick={handleCancel}
              variant="outlined"
              color="secondary"
            >
              Cancel
            </Button>
            <Button
              startIcon={<Save />}
              onClick={handleSave}
              variant="contained"
              color="primary"
              disabled={loading}
            >
              {loading ? 'Saving...' : 'Save Changes'}
            </Button>
          </Box>
        )}
      </Box>

      <Grid container spacing={3}>
        <Grid item xs={12} md={4}>
          <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <Avatar
              sx={{ width: 120, height: 120, mb: 2, bgcolor: 'primary.main' }}
              src={faculty?.profileImage}
            >
              <Person sx={{ fontSize: 60 }} />
            </Avatar>
            <Typography variant="h6" gutterBottom>
              Dr. {faculty?.name}
            </Typography>
            <Typography variant="body2" color="textSecondary">
              {faculty?.department}
            </Typography>
            <Chip 
              label={faculty?.role || 'Faculty'} 
              color="primary" 
              variant="outlined"
              sx={{ mt: 1 }}
            />
          </Box>
        </Grid>

        <Grid item xs={12} md={8}>
          <Grid container spacing={2}>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Full Name"
                value={isEditing ? editedProfile.name : faculty?.name}
                onChange={(e) => handleChange('name', e.target.value)}
                disabled={!isEditing}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <Person />
                    </InputAdornment>
                  ),
                }}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Employee ID"
                value={faculty?.employeeId || 'N/A'}
                disabled
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Email"
                value={isEditing ? editedProfile.email : faculty?.email}
                onChange={(e) => handleChange('email', e.target.value)}
                disabled={!isEditing}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <Email />
                    </InputAdornment>
                  ),
                }}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                fullWidth
                label="Phone"
                value={isEditing ? editedProfile.phone : faculty?.phone}
                onChange={(e) => handleChange('phone', e.target.value)}
                disabled={!isEditing}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <Phone />
                    </InputAdornment>
                  ),
                }}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Department"
                value={faculty?.department || 'Computer Science'}
                disabled
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Office Address"
                value={isEditing ? editedProfile.officeAddress : faculty?.officeAddress}
                onChange={(e) => handleChange('officeAddress', e.target.value)}
                disabled={!isEditing}
                multiline
                rows={2}
                InputProps={{
                  startAdornment: (
                    <InputAdornment position="start">
                      <LocationOn />
                    </InputAdornment>
                  ),
                }}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Bio"
                value={isEditing ? editedProfile.bio : faculty?.bio}
                onChange={(e) => handleChange('bio', e.target.value)}
                disabled={!isEditing}
                multiline
                rows={3}
                placeholder="Tell us about yourself..."
              />
            </Grid>
          </Grid>
        </Grid>
      </Grid>

      {!isEditing && (
        <>
          <Divider sx={{ my: 3 }} />
          <Grid container spacing={2}>
            <Grid item xs={12} sm={6}>
              <Typography variant="body2" color="textSecondary">
                Joined Date
              </Typography>
              <Typography variant="body1">
                {faculty?.joinDate ? new Date(faculty.joinDate).toLocaleDateString() : 'N/A'}
              </Typography>
            </Grid>
            <Grid item xs={12} sm={6}>
              <Typography variant="body2" color="textSecondary">
                Last Login
              </Typography>
              <Typography variant="body1">
                {faculty?.lastLogin ? new Date(faculty.lastLogin).toLocaleString() : 'N/A'}
              </Typography>
            </Grid>
          </Grid>
        </>
      )}
    </Paper>
  );
};

const FacultyPortal = ({ user, setUser }) => {
  const [activeTab, setActiveTab] = useState(0);
  const [courses, setCourses] = useState([]);
  const [attendanceDialog, setAttendanceDialog] = useState(false);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [qrCodeHash, setQrCodeHash] = useState('');
  const [attendanceMethod, setAttendanceMethod] = useState('qr');
  const [attendanceRecords, setAttendanceRecords] = useState({});
  const [manualAttendance, setManualAttendance] = useState({});
  const [scheduleData, setScheduleData] = useState([]);
  const [reportsData, setReportsData] = useState({});
  const [snackbar, setSnackbar] = useState({ open: false, message: '', severity: 'success' });
  const [qrSessionActive, setQrSessionActive] = useState(false);
  const [scannedCount, setScannedCount] = useState(0);
  const [qrSessionTimer, setQrSessionTimer] = useState(300);
  const [facultyProfile, setFacultyProfile] = useState(null);
  const [loading, setLoading] = useState(true);

  // Logout function
  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    window.location.href = '/login';
  };

  // Fetch all faculty data
  const fetchFacultyData = async () => {
    try {
      setLoading(true);

      // Fetch profile
      const profileData = await apiService.getFacultyProfile();
      setFacultyProfile(profileData);

      // Fetch courses
      const coursesData = await apiService.getFacultyCourses();
      setCourses(coursesData);

      // Fetch schedule
      const scheduleData = await apiService.getAttendanceReports();
      setScheduleData(scheduleData.schedule || []);

      // Initialize attendance records for each course
      const initialRecords = {};
      for (const course of coursesData) {
        const students = await apiService.getCourseStudents(course.id);
        initialRecords[course.id] = students.map(student => ({
          id: student.student_id,
          student_name: student.name,
          student_id: student.student_id,
          status: 'Absent',
          time: '-',
          method: '-'
        }));
      }
      setAttendanceRecords(initialRecords);

      // Fetch reports
      const reports = await apiService.getAttendanceReports();
      setReportsData(reports);

    } catch (error) {
      console.error('Failed to fetch faculty data:', error);
      setSnackbar({
        open: true,
        message: 'Failed to load data. Please refresh the page.',
        severity: 'error'
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchFacultyData();
  }, []);

  useEffect(() => {
    let timer;
    if (qrSessionActive && qrSessionTimer > 0) {
      timer = setInterval(() => {
        setQrSessionTimer(prev => prev - 1);
      }, 1000);
    } else if (qrSessionTimer === 0 && qrSessionActive) {
      setQrSessionActive(false);
      setSnackbar({
        open: true,
        message: 'QR Code session expired. Please generate a new one.',
        severity: 'warning'
      });
    }
    return () => clearInterval(timer);
  }, [qrSessionActive, qrSessionTimer]);

  const generateQRCode = async (course) => {
    try {
      const sessionData = {
        courseId: course.id,
        courseCode: course.course_code,
        duration: 300, // 5 minutes
        method: 'qr'
      };

      const result = await apiService.startAttendanceSession(sessionData);
      const hash = result.sessionId;

      setQrCodeHash(hash);
      setQrSessionActive(true);
      setQrSessionTimer(300);
      setScannedCount(0);

      setSnackbar({
        open: true,
        message: 'QR Code generated successfully! Students can now scan it.',
        severity: 'success'
      });

      return hash;
    } catch (error) {
      setSnackbar({
        open: true,
        message: 'Failed to generate QR code. Please try again.',
        severity: 'error'
      });
    }
  };

  const refreshQRCode = () => {
    if (selectedCourse) {
      generateQRCode(selectedCourse);
      setSnackbar({
        open: true,
        message: 'New QR Code generated!',
        severity: 'info'
      });
    }
  };

  const startAttendanceSession = async (course, method = 'qr') => {
    setSelectedCourse(course);
    setAttendanceMethod(method);

    if (method === 'qr') {
      await generateQRCode(course);
    }

    setAttendanceDialog(true);
  };

  const markAttendance = async (studentId, status = 'Present', method = 'Manual', studentName = '') => {
    if (!selectedCourse) return;

    try {
      const attendanceData = {
        studentId,
        courseId: selectedCourse.id,
        status,
        method,
        timestamp: new Date().toISOString()
      };

      await apiService.markAttendance(attendanceData);

      const time = new Date().toLocaleTimeString('en-US', { 
        hour12: true, 
        hour: 'numeric', 
        minute: '2-digit' 
      });

      setAttendanceRecords(prev => ({
        ...prev,
        [selectedCourse.id]: prev[selectedCourse.id].map(record =>
          record.student_id === studentId
            ? { ...record, status, time, method }
            : record
        )
      }));

      if (method === 'QR Code') {
        setScannedCount(prev => prev + 1);
      }

      setSnackbar({
        open: true,
        message: `Attendance marked for ${studentName || studentId}`,
        severity: 'success'
      });
    } catch (error) {
      setSnackbar({
        open: true,
        message: 'Failed to mark attendance. Please try again.',
        severity: 'error'
      });
    }
  };

  const markManualAttendance = (studentId, status) => {
    const student = attendanceRecords[selectedCourse.id]?.find(s => s.student_id === studentId);
    setManualAttendance(prev => ({
      ...prev,
      [studentId]: status
    }));
    markAttendance(studentId, status, 'Manual', student?.student_name);
  };

  const submitManualAttendance = async () => {
    try {
      // Submit all manual attendance records
      for (const [studentId, status] of Object.entries(manualAttendance)) {
        if (status !== 'Absent') {
          const student = attendanceRecords[selectedCourse.id]?.find(s => s.student_id === studentId);
          await markAttendance(studentId, status, 'Manual', student?.student_name);
        }
      }

      setSnackbar({
        open: true,
        message: 'Manual attendance submitted successfully!',
        severity: 'success'
      });
      setAttendanceDialog(false);
      setManualAttendance({});
    } catch (error) {
      setSnackbar({
        open: true,
        message: 'Failed to submit attendance. Please try again.',
        severity: 'error'
      });
    }
  };

  const simulateQRScan = () => {
    if (!selectedCourse || !qrSessionActive) return;

    const students = attendanceRecords[selectedCourse.id] || [];
    const absentStudents = students.filter(s => s.status === 'Absent');

    if (absentStudents.length > 0) {
      const randomStudent = absentStudents[Math.floor(Math.random() * absentStudents.length)];
      markAttendance(randomStudent.student_id, 'Present', 'QR Code', randomStudent.student_name);
    } else {
      setSnackbar({
        open: true,
        message: 'All students have already been marked present!',
        severity: 'info'
      });
    }
  };

  const closeAttendanceSession = () => {
    setAttendanceDialog(false);
    setQrSessionActive(false);
    setSelectedCourse(null);
    setScannedCount(0);
    setQrSessionTimer(300);
    setManualAttendance({});
    setSnackbar({
      open: true,
      message: 'Attendance session closed',
      severity: 'info'
    });
  };

  const downloadAttendanceReport = async (courseId) => {
    try {
      const course = courses.find(c => c.id === courseId);
      const reports = await apiService.getAttendanceReports(courseId);

      const csvContent = [
        ['Student ID', 'Student Name', 'Status', 'Time', 'Method', 'Date'],
        ...reports.records.map(record => [
          record.student_id,
          record.student_name,
          record.status,
          record.time,
          record.method,
          new Date(record.timestamp).toLocaleDateString()
        ])
      ].map(row => row.join(',')).join('\n');

      const blob = new Blob([csvContent], { type: 'text/csv' });
      const url = URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `${course.course_code}_attendance_${new Date().toISOString().split('T')[0]}.csv`;
      a.click();
      URL.revokeObjectURL(url);

      setSnackbar({
        open: true,
        message: 'Attendance report downloaded successfully!',
        severity: 'success'
      });
    } catch (error) {
      setSnackbar({
        open: true,
        message: 'Failed to download report. Please try again.',
        severity: 'error'
      });
    }
  };

  const updateFacultyProfile = async (profileData) => {
    const updatedProfile = await apiService.updateFacultyProfile(profileData);
    setFacultyProfile(updatedProfile);
    return updatedProfile;
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <Typography variant="h6">Loading Faculty Portal...</Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ flexGrow: 1 }}>
      {/* Navigation Bar with Logout */}
      <AppBar position="static" sx={{ bgcolor: '#1976d2', mb: 3 }}>
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            ðŸ‘¨â€ðŸ« Faculty Portal - Smart Classroom
          </Typography>
          <Typography variant="body2" sx={{ mr: 2 }}>
            Welcome, Dr. {facultyProfile?.name}
          </Typography>
          <IconButton color="inherit">
            <AccountCircle />
          </IconButton>
          <Button 
            color="inherit" 
            startIcon={<Logout />}
            onClick={handleLogout}
            sx={{ ml: 2 }}
          >
            Logout
          </Button>
        </Toolbar>
      </AppBar>

      <Container maxWidth="lg" sx={{ mb: 4 }}>
        <Paper sx={{ width: '100%', mb: 3, border: '1px solid #1976d2' }}>
          <Tabs 
            value={activeTab} 
            onChange={(e, newValue) => setActiveTab(newValue)} 
            variant="scrollable"
            textColor="primary"
            indicatorColor="primary"
          >
            <Tab icon={<Schedule />} label="My Schedule" />
            <Tab icon={<Groups />} label="My Courses" />
            <Tab icon={<QrCode2 />} label="Take Attendance" />
            <Tab icon={<Analytics />} label="Attendance Reports" />
            <Tab icon={<Face />} label="Manual Attendance" />
            <Tab icon={<AccountCircle />} label="My Profile" />
          </Tabs>
        </Paper>

        {/* My Schedule Tab */}
        {activeTab === 0 && (
          <Box>
            <Typography variant="h5" gutterBottom sx={{ mb: 3, color: 'primary.main' }}>
              My Weekly Schedule
            </Typography>
            <Paper sx={{ p: 3, border: '1px solid #1976d2' }}>
              <TableContainer>
                <Table>
                  <TableHead sx={{ bgcolor: '#1976d2' }}>
                    <TableRow>
                      <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>Day</TableCell>
                      <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>Course Code</TableCell>
                      <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>Time</TableCell>
                      <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>Room</TableCell>
                      <TableCell sx={{ color: 'white', fontWeight: 'bold' }}>Type</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {scheduleData.map((schedule) => (
                      <TableRow key={schedule.id} hover>
                        <TableCell>
                          <Typography variant="body1" fontWeight="bold">
                            {schedule.day}
                          </Typography>
